INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (100, NULL, NULL, N'what is NetworkMiner', N'what is networkminer', N'NetworkMiner is a Network Forensic Analysis Tool that can parse PCAP files for off-line analysis and to regenerate/reassemble transmitted files and certificates from PCAP files. NetworkMiner can be used as a passive network sniffer/packet capturing tool to detect operating systems, sessions, hostnames, open ports etc. without putting any traffic on the network.', NULL, N'9a6650b452174e20a8ef2afdf07efb8b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (101, NULL, NULL, N'what is elsa?', N'what is elsa', N'Enterprise Log Search and Archive (ELSA) is a centralized syslog framework built on Syslog-NG, MySQL, and Sphinx full-text search. It provides a fully asynchronous web-based query interface that normalizes logs and makes searching billions of them for arbitrary strings as easy as searching the web. ELSA provides a SIEM alternative out of the box.', NULL, N'fe08a6ddb32a4d8da532c3485298a771')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (102, NULL, NULL, N'what is a plc?', N'what is a plc', N'A PLC, or programmable logic controller, is a computer that has been adapted for industrial purposes like controlling an assembly line or other manufacturing process.', NULL, N'a29fbe23578c44f5a9fed0f8741a68d7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (103, NULL, NULL, N'what are snort actions?', N'what are snort actions', N'There are 5 available default actions in Snort: alert, log, pass, activate, and dynamic. In addition, if you are running Snort in inline mode (i.e. as a NIPS), you have additional options which include drop, reject, and sdrop.', NULL, N'15ee67dd2bfb4d16a7ad84f9f0e02dae')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (104, NULL, NULL, N'what next?', N'what next', N'Take a look at your objectives to determine what you need to do next. You can ask me specific questions about any objective in the form of ''how do I complete objective 3?''. If I outline steps for an objective, you can ask me questions of the form, ''how do I do step 1?''. I will assume you are referring to the steps for the objective you most recently asked about.', NULL, N'c1cfad535dae417a9818694cc88cec0e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (105, NULL, NULL, N'how do I get a hint?', N'how do i get a hint', N'If the mission administrator has activated hints for this mission, you can click the question mark next to each objective to access hints pertaining to that objective. Each hint may be broken up into multiple slides, and each slide you view sacrifices bonus points from your overall mission score.', NULL, N'fe1b68de4e6246bdb48ff5da7cb964f2')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (106, NULL, NULL, N'how do I use traceroute?', N'how do i use traceroute', N'Here is an example of using traceroute with the options to Wait 3 seconds (defaults to 5 without -w flag), send only 1 Query (defaults to 3 without -q flag), limit the maximum number of hops to 16 before giving up (defaults to 30 without the -m flag), and set example.com as the final host: traceroute -w 3 -q 1 -m 16 example.com. View the man page for more details.', NULL, N'b382e8b8795d4e55aa2c8f6b2a5a1875')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (107, NULL, NULL, N'how do I use nslookup?', N'how do i use nslookup', N'You can use nslookup to find your DNS server with the following command: nslookup <your IP address>. You can view the man page for more information.', NULL, N'4fd5bf80ece644e199e4b15e3ec88ca4')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (108, NULL, NULL, N'how do I use strings?', N'how do i use strings', N'The Unix command line tool ''strings'' finds and prints text strings embedded in binary files such as executables. For example, you can find and print any strings in the binary example.exe with the following command: strings example.exe', NULL, N'ce75576f8ab84157ab0eb67762d911f1')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (109, NULL, NULL, N'how do I use autoruns?', N'how do i use autoruns', N'Autoruns shows you the currently configured auto-start applications as well as the full list of Registry and file system locations available for auto-start configuration. You can use autoruns to schedule your system to do things, but malware can do the same thing. Autoruns entries may be a good place to look for persistence mechanisms on a Windows machine.', NULL, N'd4815e1588fe421ab55759ff657b237f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (110, NULL, NULL, N'how do I use gdb?', N'how do i use gdb', N'The GNU debugger can be used to inspect and modify values and breakpoints in a compiled program written in a language such as c or c++.', NULL, N'28a921585ad54b009974aea2b8459ec6')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (111, NULL, NULL, N'how do I use IDA?', N'how do i use ida', N'The IDA Disassembler and Debugger is an interactive, programmable, extendible, multi-processor disassembler hosted on Windows, Linux, or Mac OS X. IDA has become the de-facto standard for the analysis of hostile code, vulnerability research and COTS validation.', NULL, N'31f820cd4dc5445baad2332271c74e1e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (112, NULL, NULL, N'how do I use objdump?', N'how do i use objdump', N'objdump is a program for displaying various information about object files. For instance, it can be used as a disassembler to view an executable in assembly form. The basic syntax of objdump is:  objdump [options] objfile...', NULL, N'c616fad637b643ab95abd9a2ea48b4d1')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (113, NULL, NULL, N'how do I use radare2?', N'how do i use radare2', N'Radare2 is used to analyze binary files and devise strategies to perform buffer overflow attacks. To start radare2, use the command r2 <binary_filename> .', NULL, N'33c1914ef49b4f40885038ce5d7e6ab3')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (114, NULL, NULL, N'how do I use cd?', N'how do i use cd', N'You can change your current working directory in a terminal using cd. For example, if you are on Linux in the directory /home/user1/Desktop and you want to cd to the directory /home/user1/Downloads/example/ you could do so with the following command using a relative path: cd ../Downloads/example/. You could of course do the same thing using the absolute path: cd /home/user1/Downloads/example/', NULL, N'6b8b8ef8c31c4ccf914f28082081a647')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (115, NULL, NULL, N'how do I use vnc?', N'how do i use vnc', N'To use VNC, click on the ''VNC Access'' button in the gameplay UI within the mission objectives and mission recon screens.', NULL, N'303a8f28f7d1485e912ce278acc65b6d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (116, NULL, NULL, N'how do I use elsa?', N'how do i use elsa', N'To use ELSA, you can enter https://172.16.30.11/elsa in the Chromium browser and login with your credentials. There is a query box at the top of the ELSA interface where you can query the Bro logs.', NULL, N'0e5b39bc831c4073a72975709b5567eb')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (117, NULL, NULL, N'how do I redirect output to a file?', N'how do i redirect output to a file', N'The most common syntax for redirecting standard output uses the ''>'' operator. A single ''>'' operator will create a new file to store the output while obliterating any existing file with the same filename. A double ''>>'' operator will append the redirected standard output to the end of an existing file or create a new file if one does not already exist. For example, the following command redirects the output of the strings command into a new file called new.txt: strings example.exe > new.txt', NULL, N'15e71913df5646fd895f474ea26837ec')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (118, NULL, NULL, N'how do I redirect output to a command?', N'how do i redirect output to a command', N'The operator to pipe output from one command to another is the vertical bar ''|'' character. For example, to pipe the output of ls into the command wc, the command would be: ls | wc', NULL, N'3e98df74dee34d22a03b2b7284ce907f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (119, NULL, NULL, N'how do I redirect output to strings?', N'how do i redirect output to strings', N'The operator to pipe output from one command to another is the vertical bar ''|'' character. For example, to pipe the output of ls into the command wc, the command would be: ls | wc', NULL, N'2e8999199c1a4ad8a4ef93d016c5a61f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (120, NULL, NULL, N'how do I redirect output?', N'how do i redirect output', N'On the linux command line, many common utility programs are designed to allow input from other commands, and likewise, have their output consumed by other command line utilities. The operator to pipe output from one command to another is the vertical bar ''|'' character. For example, to pipe the output of ls into the command wc, the command would be ''ls | wc''. Additionally, standard output can be directed to a file to be saved on disk. The most common syntax for redirecting standard output uses the ''>'' operator. A single ''>'' operator will create a new file to store the output while obliterating any existing file with the same filename. A double ''>>'' operator will append the redirected standard output to the end of an existing file or create a new file if one does not already exist. For example, the following command redirects the output of the strings command into a new file called new.txt: strings example.exe > new.txt', NULL, N'f6c07e3f25ac4ee9927b359accf74699')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (121, NULL, NULL, N'how do I background a meterpreter session?', N'how do i background a meterpreter session', N'To ''background'' a meterpreter session in metasploit, simply type ''background'' in the metasploit terminal while you are in a meterpreter session. To resume a backgrounded meterpreter session, the command is: sessions -i <session number>.', NULL, N'fe3cc963776a4ce4bd6cc23cc004574a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (122, NULL, NULL, N'how do I use meterpreter?', N'how do i use meterpreter', N'Meterpreter is a shell component of metasploit that can be used to listen for incoming reverse shell connections initiated by the payload delivery. The payload used will depend on the vulnerability being exploited. Meterpreter is also much more than just a type of shell. Really Meterpreter is a dynamically extensible payload that is extended over the network at runtime and utilizes DLL injection stagers. Meterpreter communicates over the stager socket and provides a client side Ruby API.', NULL, N'33f75d6c64734e72bad669f5ff4aac9d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (123, NULL, NULL, N'what is privilege?', N'what is privilege', N'Privilege can be defined in computing as the delegation of authority over a computer system. Users require the necessary privileges to perform an action, e.g. create a file in a certain directory, delete or read a particular file, access a device, or read or write to a socket for communicating over the internet. The exact details of how privileges work vary between different operating systems.', NULL, N'81541f594895408fbf39bcc57d02e21f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (124, NULL, NULL, N'what is distccd?', N'what is distccd', N'In software development, distcc is a tool for speeding up compilation of source code by using distributed computing over a computer network. With the right configuration, distcc can dramatically reduce a project''s compilation time. Distccd is the daemon running on a distcc server, used to service requests from clients on the network.', NULL, N'2e57bc971a274c9397530861caaf84eb')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (125, NULL, NULL, N'what is a password hash?', N'what is a password hash', N'Storing all user passwords as cleartext can result in a massive security breach if the password file is compromised. One way to reduce this danger is to only store the hash digest of each password. To authenticate a user, the password presented by the user is hashed and compared with the stored hash. (Note that this approach prevents the original passwords from being retrieved if forgotten or lost, and they have to be replaced with new ones.) The password is often concatenated with a random, non-secret salt value before the hash function is applied. The salt is stored with the password hash. Because users have different salts, it is not feasible to store tables of precomputed hash values for common passwords.', NULL, N'f9cc366f10c14cd99af245554c2e3df0')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (126, NULL, NULL, N'what is sql?', N'what is sql', N'SQL stands for structured query language. There are multiple variations on SQL, such as MySQL, or the turing complete T-SQL. All SQL languages are used to communicate with and/or manipulate databases, and as such any interface that allows for interaction with an applications underlying SQL is an attractive target for attackers.', NULL, N'0afa251aec124a009ff53e84a2461ce2')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (127, NULL, NULL, N'what is sql injection?', N'what is sql injection', N'A user supplied input is used to construct a SQL request to retrieve information from a database. SQL Injection Attack is a code injection technique, used to attack data-driven applications, in which nefarious SQL statements are inserted into an entry field for execution (e.g. to dump the database contents to the attacker). SQL injection must exploit a security vulnerability in an application''s software, for example, when user input is either incorrectly filtered for string literal escape characters embedded in SQL statements or user input is not strongly typed and unexpectedly executed. SQL injection is mostly known as an attack vector for websites but can be used to attack any type of SQL database.', NULL, N'b51b7b3a27444a2f9b678be8e2a25e6e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (128, NULL, NULL, N'what is sh-4.2$?', N'what is sh-4.2$', N'A command-line prompt is what a shell will display to allow you to enter a new command, and the way the prompt looks will vary from one shell to another. The prompt sh-4.2$ for instance is one you may see playing certain Ares missions, and indicates you are using the Bourne Shell logged in as a normal user, as opposed to root.', NULL, N'fec08f2f20b34cbbaa91cb6f3e2496fe')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (129, NULL, NULL, N'what is client1?', N'what is client1', N'The client1 machine is the Ubuntu box you are first given access to in the cyber range when you play defensive missions in Project Ares.', NULL, N'12f2dab6bbc7455b8113740ebe7a9775')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (130, NULL, NULL, N'what is a server?', N'what is a server', N'A client is a piece of computer hardware or software that accesses a service made available by a server. The server is often (but not always) on another computer system, in which case the client accesses the service by way of a network.', NULL, N'ec096df384144b48b1eeb55edf562127')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (131, NULL, NULL, N'what is a client?', N'what is a client', N'A client is a piece of computer hardware or software that accesses a service made available by a server. The server is often (but not always) on another computer system, in which case the client accesses the service by way of a network.', NULL, N'e72770db61e44b9daa63d71971f96dc9')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (132, NULL, NULL, N'what is lhost?', N'what is lhost', N'Remote Host, or RHost, is the parameter that defines the remote address, a.k.a the target. Local Host, or LHost, is the parameter that defines the connect-back address in reverse payloads, a.k.a your IP address.', NULL, N'1d15a159862f4417833647f782e96db6')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (133, NULL, NULL, N'what is rhost?', N'what is rhost', N'Remote Host, or RHost, is the parameter that defines the remote address, a.k.a the target. Local Host, or LHost, is the parameter that defines the connect-back address in reverse payloads, a.k.a your IP address.', NULL, N'98911748a0c94c06b3c68a363f42a966')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (134, NULL, NULL, N'what is https?', N'what is https', N'Hypertext Transfer Protocol Secure (HTTPS) is a variant of the standard web transfer protocol (HTTP) that adds a layer of security on the data in transit through a secure socket layer (SSL) or transport layer security (TLS) protocol connection. HTTPS enables encrypted communication and secure connection between a remote user and the primary web server. HTTPS is primarily designed to provide enhanced security layer over the unsecured HTTP protocol for sensitive data and transactions such as billing details, credit card transactions and user login etc. HTTPS encrypts every data packet in transition using SSL or TLS encryption technique to avoid intermediary hackers and attackers to extract the content of the data; even if the connection is compromised. HTTPS is configured and supported by default in most web browsers and initiates a secure connection automatically if the accessed web servers requests secure connection. HTTPS works in collaboration with certificate authorities that evaluates the security certificate of the accessed website.', NULL, N'a47a562bacd049f6bfb986936cfb5ff4')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (135, NULL, NULL, N'what is dirbuster?', N'what is dirbuster', N'Dirbuster is a tool that can quickly make many HTTP GET requests to a server and keep track of which requests return valid data, effectively brute forcing the process of finding any files left available to the public.', NULL, N'afd84d03647e4bb2bf73d44d67d73811')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (136, NULL, NULL, N'what is a dropper?', N'what is a dropper', N'A dropper is a type of malware developed to launch viruses by "dropping" (installing) them. Dropper viruses may go undetected because they are hidden, difficult to pinpoint and relatively uncommon. Droppers are also a relatively new type of virus that many anti-virus programs are not equipped to detect.Droppers are programs that contain viruses that impede the functioning of targeted computers. They can install themselves onto a disk or a hard drive. They typically do not duplicate themselves as worms do. Instead, droppers launch their payloads while disguising themselves within computer systems and directories. The code of the virus is contained within the dropper. Usually, dropper viruses are Trojans, and the virus installation takes place in the form of a payload, which is the malicious activity of the virus.This term is also known as a dropper program or a virus dropper. Droppers are not associated with any computer files, making it difficult for anti-virus scanners to capture or detect them. Droppers infest themselves within downloads and suspicious email attachments (common when the email recipient does not recognize the email sender) or are bundled in some other clandestine manner. As such, anti-spyware software is considered to be the most effective tool for dropper detection and deletion. Droppers may not always be evident right away, but they may cause a PC to slow down, or submit a trigger to steal data once it is gathered. Droppers are not common, but as with most computer viruses, they are becoming more sophisticated and harder for anti-malware programs to detect.', NULL, N'26840218d02a4dcf868d456277790711')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (137, NULL, NULL, N'what is tcpview?', N'what is tcpview', N'Tcpview is a tool to that shows detailed listings of all TCP and UDP endpoints connected to your system, including the local and remote addresses and state of TCP connections. You may be able to use this to find malicious processes connecting to IP addresses identified as suspicious in previous objectives.', NULL, N'fa05c3ec55bf4243a5072ab82ba8e6e8')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (138, NULL, NULL, N'what is SGUIL?', N'what is sguil', N'Sguil (pronounced sgweel) is built for network security analysis. Sguil''s main component is a GUI that provides access to realtime events, session data, and raw packet captures. Sguil facilitates the practice of Network Security Monitoring and event driven analysis. The Sguil client is written in tcl/tk and can be run on any operating system that supports tcl/tk (including Linux, *BSD, Solaris, MacOS, and Win32).', NULL, N'72bb1fe21cef4c0996960de703e4b150')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (139, NULL, NULL, N'what is vi?', N'what is vi', N'Vi, available on Linux and other Unix-like systems, is a powerful text editor for the command line. The most important vi commands are - open file called example.py in vi: vi example.py. Enter insert mode by pressing i. Exit insert mode with the esc key. Save and quit a file with :wq.', NULL, N'ad4e60039d7448029bb0bd5aa10136f0')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (140, NULL, NULL, N'what is a request?', N'what is a request', N'HTTP is a request/response protocol. That means a computer sends a request for some file (e.g. a GET request for the file example.html), and the web server sends back a response ("Here''s the file", followed by the file itself). Two of the most common types of HTTP requests are GET, which requests data from a specified resource, and POST, which submits data to be processed by a specified resource. Other types of requests include but are not limited to: HEAD, DELETE, and OPTIONS.', NULL, N'c36bd62c762144c2a55cca52d17d5c30')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (141, NULL, NULL, N'what is autoruns?', N'what is autoruns', N'This command line or GUI utility allows an analyst collect information about auto-starting locations for binaries in the system. The binaries can fall under different categories and can be started by the system while booting or by the user environment at different times. Excellent tool to find persistence by a piece of malware not using a rootkit.', NULL, N'84a888f6fe124398afd0f1821b2569cf')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (142, NULL, NULL, N'what is chmod?', N'what is chmod', N'In Unix-like operating systems chmod is the command used to change the access permissions to file system objects.', NULL, N'a77dca7a8427412a8ffcb6103ead7db5')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (143, NULL, NULL, N'what is an exploit handler?', N'what is an exploit handler', N'An exploit handler is a stub that provides all of the features of the Metasploit payload system to exploits that have been launched outside of the framework.', NULL, N'b24de33bcd6f49aaa8010f65d6871a10')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (144, NULL, NULL, N'what is bro?', N'what is bro', N'Bro is an open source Unix based network monitoring framework. Bro is often compared to a Network Intrusion Detection System but can be used as much more. Bro has been compared to tcpdump, Snort, netflow, and Perl (or any other scripting language) all in one.', NULL, N'69e8bcad4c464cc6b9560f6c3b57a940')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (145, NULL, NULL, N'what is a snort rule header?', N'what is a snort rule header', N'The header contains the rule''s action, protocol, source and destination IP addresses and netmasks, and the source and destination ports information.', NULL, N'ea607ad571cc4b31ad3e8d91ea369c51')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (146, NULL, NULL, N'what is a netmask?', N'what is a netmask', N'A netmask is a bitmask for IP addresses. For example, 255.255.255.0 (11111111.11111111.11111111.00000000 in binary) is the network mask for the 192.168.1.0/24 routing prefix. The /24 refers to the number of bits ''masked'' by 1s. Traffic is exchanged (routed) between subnetworks with special gateways (routers) when the routing prefixes of the source address and the destination address differ.', NULL, N'6ae56f3e065541b48a2c407953b7ca9a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (147, NULL, NULL, N'what is a bitmask?', N'what is a bitmask', N'In computer science, a mask is data that is used for bitwise operations, particularly in a bit field. For example, 255.255.255.0 (11111111.11111111.11111111.00000000 in binary) is the network mask for the 192.168.1.0/24 routing prefix. The /24 refers to the number of bits ''masked'' by 1s.', NULL, N'd20e64b4b66a4e6999e6b4ef2d66ef94')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (148, NULL, NULL, N'what are snort rule actions?', N'what are snort rule actions', N'There are 5 available default actions in Snort: alert, log, pass, activate, and dynamic. In addition, if you are running Snort in inline mode (i.e. as a NIPS), you have additional options which include drop, reject, and sdrop.', NULL, N'faac4cb9933248c89c45f6219c68974e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (149, NULL, NULL, N'what are snort rule protocols?', N'what are snort rule protocols', N'There are four protocols that Snort can currently analyze for suspicious behavior - TCP, UDP, ICMP, and IP.', NULL, N'a23dc3beaf0744ae87e2aff0ed45374c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (150, NULL, NULL, N'what is the routing prefix?', N'what is the routing prefix', N'Computers that belong to a subnet are addressed with a common, identical, most-significant bit-group in their IP address. This results in the logical division of an IP address into two fields, a network or routing prefix and the ''rest'' field or host identifier.', NULL, N'b33c6e85a06945e8b02f1e0e62aaea3a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (151, NULL, NULL, N'what are snort rule options?', N'what are snort rule options', N'The rule option section contains alert messages and information on which parts of the packet should be inspected to determine if the rule action should be taken.', NULL, N'2cf192f2db6e478b9c5730dd35c78b12')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (152, NULL, NULL, N'what is a NIPS?', N'what is a nips', N'A Network Intrusion Prevention System is a system that examines network traffic flows to detect and prevent vulnerability exploits.', NULL, N'27a7639187e14fcc8580cfb7a113ddc8')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (153, NULL, NULL, N'what is a NIDS?', N'what is a nids', N'A Network Intrusion Detection System is a system that examines network traffic flows to detect possible attempts at exploiting vulnerabilities in the network.', NULL, N'0cf0aa78b40f43d0b4aaa32c2d57f430')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (154, NULL, NULL, N'what is a bitwise operation?', N'what is a bitwise operation', N'A bitwise operation operates on one or more bit patterns or binary numerals at the level of their individual bits.', NULL, N'8143253588564cf79e946c66a21fa0e6')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (155, NULL, NULL, N'what is snortd?', N'what is snortd', N'The snort daemon is called snortd.', NULL, N'8e6c1ce8bc3f4470a8cdef4629eb5f5a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (156, NULL, NULL, N'what is netflow?', N'what is netflow', N'NetFlow was introduced on Cisco routers to provide the ability to collect IP network traffic as it enters or exits an interface. The data provided by NetFlow can be used to determine things such as the source and destination of traffic, class of service, and the causes of congestion.', NULL, N'5f42e124b98b4c4295c22490cb170f6f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (157, NULL, NULL, N'what is perl?', N'what is perl', N'Perl is a family of dynamic programming languages. Perl provides powerful text processing facilities.', NULL, N'17cab1467c8b494e96fa521caa59e7db')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (158, NULL, NULL, N'what is a subnet?', N'what is a subnet', N'A subnet (short for subnetwork) is a logical subdivision of an IP network. Subnets can be represented numerically by attaching a network mask, i.e. subnet mask, to the IP address.', NULL, N'03eadfc549224799b910e3fa5d89727e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (159, NULL, NULL, N'what is an IP network?', N'what is an ip network', N'An IP network is a communication network that uses Internet Protocol (IP) to send and receive messages between one or more computers.', NULL, N'401b4cf52f874f5db096fa3c187b3037')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (160, NULL, NULL, N'what is the internet protocol?', N'what is the internet protocol', N'The Internet Protocol (IP) is a protocol used for relaying datagrams across network boundaries. Its routing function enables internetworking, and essentially establishes the Internet.', NULL, N'0aa6b363673d49afa5f879e7e872bdba')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (161, NULL, NULL, N'what is an octet?', N'what is an octet', N'The octet is a unit of digital information in computing and telecommunications that consists of eight bits. One of the most common uses of octets is in IP addresses.', NULL, N'605aba300ef844d09b46800a3cf637f5')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (162, NULL, NULL, N'what is a bit?', N'what is a bit', N'The bit (a portmanteau of binary digit) is a basic unit of information used in computing and digital communications. A bit has only two possible values, 0 or 1.', NULL, N'c4c5184f017344ccb2927209c2b82aff')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (163, NULL, NULL, N'what is a byte?', N'what is a byte', N'The byte is a unit of digital information that usually consists of eight bits. Historically, the byte was the number of bits used to encode a single character of text in a computer which differentiates it from an octet. Computer storage is typically measured in units that are multiples of 8 bit bytes, e.g. a gigabyte is billion bytes.', NULL, N'96573dc623fb4521b0dc7b89b5ca0cc7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (164, NULL, NULL, N'what is a user-agent string?', N'what is a user-agent string', N'A user agent is software (a software agent) that is acting on behalf of a user. When a software agent operates in a network protocol, it often identifies itself by submitting a characteristic identification string to its operating peer. This string might include the agent''s application type, operating system, software vendor, or software revision.', NULL, N'2e204f4d1eb9488b8ef96c4c0f803bd8')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (165, NULL, NULL, N'what is http?', N'what is http', N'HTTP, the Hypertext Transfer Protocol, is an application protocol for distributed, collaborative, and hypermedia information systems; most notably, this protocol serves as the foundation of data communication for the World Wide Web.', NULL, N'c418ee7c577f4c0c85f9eb4f2d991d97')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (166, NULL, NULL, N'what is a subdirectory?', N'what is a subdirectory', N'In a hierarchical filesystem (one in which files and directories are organized in a manner resembling a tree, commonly used in Operating Systems), a directory contained inside another directory is called a subdirectory.', NULL, N'6c09836306c542d2b163ab1e6442eb8c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (167, NULL, NULL, N'what is a directory?', N'what is a directory', N'In the context of computer file systems, a directory is a catalog structure containing references to files or other directories. On many operating systems, directories are commonly referred to as folders. On Windows, Mac, and Linux, basically every folder you''ve ever seen holding pictures, documents, or other files is just a graphical representation of a directory in the file system.', NULL, N'cc977e6c46bb4969b521a45bf0226cae')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (168, NULL, NULL, N'what are the source and destination?', N'what are the source and destination', N'In the context of network connections, the source and destination often refer to the machine where the connection originated and the machine where the connection is being received, respectively. These terms are relative, so care must be taken to ensure you interpret them correctly.', NULL, N'09bff89166cc46e1b48ec25b63a45361')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (169, NULL, NULL, N'what is a string?', N'what is a string', N'The idea of a text string has multiple similar meanings in computer science. For instance in some programming languages a string is a primitive data type, often enclosed in single or double quotation marks, e.g. "this is a string enclosed in double quotes." In other contexts, a string may just refer to a collection of one or more textual characters, like letters, words, or other symbols.', NULL, N'7102879700fa41a2ba031dfa38471a33')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (170, NULL, NULL, N'what is scp?', N'what is scp', N'Scp stands for secure copy, and it can be used on the Linux command line (and other Unix-like command lines) to copy files from one machine to another machine on the same network.', NULL, N'c082f27988834a0c9a0bd38ebec57831')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (171, NULL, NULL, N'what is crontab?', N'what is crontab', N'Short for cron table, on Linux and other Unix-like OSs ''crontab'' can mean to two things. The crontab is a special file containing a list of commands that you want to run on a regular schedule, and crontab is the name of the command used to manage that list. View the man page for more info.', NULL, N'c9a708f5e8f34b3cb2b6e965fb80c519')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (172, NULL, NULL, N'what is tcpdump?', N'what is tcpdump', N'This command line tool collects information about the traffic flowing through a network.', NULL, N'6b7f8d9c9ac948c0aca9e1d8b1b74122')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (173, NULL, NULL, N'what is the registry?', N'what is the registry', N'In Windows Operating Systems, the Registry is a hierarchical database that stores low-level settings for the operating system and for applications that opt to use the Registry. As such, mucking around in the registries can cause big problems if you''re not careful. Naturally, hackers like to hide things here sometimes.', NULL, N'65a37ab283914d94a970ba4067b32a4e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (174, NULL, NULL, N'what is the task scheduler?', N'what is the task scheduler', N'In Windows Operating Systems, the task scheduler provides the ability to schedule the launch of programs or scripts at pre-defined times or after specified time intervals.', NULL, N'7b1e3cb885354f129a3fa22f7f1c4bb1')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (175, NULL, NULL, N'what is a response?', N'what is a response', N'HTTP is a request/response protocol. That means a computer sends a request for some file (e.g. a GET request for the file example.html), and the web server sends back a response ("Here''s the file", followed by the file itself). One of the most important parts of a response is the Status-Line.', NULL, N'6cfc8493ddad450397e06b0daf6562b3')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (176, NULL, NULL, N'what is a status-line?', N'what is a status-line', N'The Status-Line in an HTTP response consists of the protocol version followed by a numeric status code and its associated textual phrase. The status code is for use by applications and other programs, and the textual phrase is included for human users. The Status-Line you are most familiar with probably includes the code and phrase: "404: Not Found"', NULL, N'2edba40ec2554772a2f1b728e849a225')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (177, NULL, NULL, N'what is a XMAS scan?', N'what is a xmas scan', N'In Xmas scan, all flags are set. All the available flags in the TCP header are set (ACK, FIN, RST, SYN, URG, PSH) to give the scan an ornamental look. This scan will work on UNIX and related systems and cause the kernel to drop the packet if the receiving port is open.', NULL, N'de85f4cabfaf4ca29f3d5aeca2368af4')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (178, NULL, NULL, N'what is nslookup?', N'what is nslookup', N'nslookup is command-line tool used for network administration, and is available for many computer operating systems. This tool can query the Domain Name System (DNS) to obtain domain name or IP address mapping or any other specific DNS record.', NULL, N'f48a1db08f6241dca7ada46eed9d90b7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (179, NULL, NULL, N'what is traceroute?', N'what is traceroute', N'In computing, traceroute is a computer network diagnostic tool for displaying the route (path) and measuring transit delays of packets across an Internet Protocol (IP) network.', NULL, N'aff74c7d457045388942ef18c59d0be2')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (180, NULL, NULL, N'what is a stealth scan?', N'what is a stealth scan', N'Look at all nmap commands by typing ''man nmap'' to choose the appropriate one. Initially half open scans were considered stealthy, however as intrusion detection software evolved, these scan were easily logged. Now, a stealthy scan refers to the type of scan where packets are flagged with a particular set of flags other than SYN, or a combination of flags, no flag set, with all flag set, appearing as normal traffic, using fragmented packet or avoiding filtering devices by any other means. All these techniques resort to inverse mapping to determine open ports.', NULL, N'866239dc41824ac69f01bb8679e00cf8')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (181, NULL, NULL, N'what is smb?', N'what is smb', N'Server Message Block (SMB) is a very standard protocol found in almost all Windows environments and is used to allow machines to communicate with each other. SMB is often repurposed by attackers to move laterally because it is trusted, and it''s present.', NULL, N'1de8f948c56945dfbffa8234e00f5553')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (182, NULL, NULL, N'what is alphanumeric?', N'what is alphanumeric', N'The term alphanumeric refers to strings of text containing letters and/or numbers.', NULL, N'73dc12e3a1074be3b538098b4662e2a5')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (183, NULL, NULL, N'what is a path?', N'what is a path', N'A path is the general form of the name of a file or directory in an operating system. A path specifies a unique location in a file system. Generally, there are two types of paths, an absolute path and a relative path.', NULL, N'aa66adfb27ba4667b4d1450c35df428e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (184, NULL, NULL, N'what is authentication?', N'what is authentication', N'Authentication is the process of verification that an individual or an entity is who it claims to be. Authentication is commonly performed by submitting a user name or ID and one or more items of private information that only a given user should know.', NULL, N'004bfaea93174c248d0307118a8d3eaf')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (185, NULL, NULL, N'what is ssl authentication?', N'what is ssl authentication', N'SSL Client Authentication, also known as two-way SSL authentication, consists of both, browser and server, sending their respective SSL certificates during the TLS handshake process. Just as you can validate the authenticity of a server by using the certificate and asking a well-known Certificate Authority (CA) if the certificate is valid, the server can authenticate the user by receiving a certificate from the client and validating against a third party CA or its own CA. To do this, the server must provide the user with a certificate generated specifically for him, assigning values to the subject so that these can be used to determine what user the certificate should validate.', NULL, N'e05d78d2086b424b8346c855ce6af737')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (186, NULL, NULL, N'what is multi-factor authentication?', N'what is multi-factor authentication', N'Multi-factor authentication (MFA) is using more than one authentication factor to logon or process a transaction: Something you know (account details or passwords), Something you have (tokens or mobile phones), Something you are (biometrics). Authentication schemes such as One Time Passwords (OTP) implemented using a hardware token can also be key in fighting attacks such as CSRF and client-side malware. A number of hardware tokens suitable for MFA are available in the market that allow good integration with web applications.', NULL, N'dc3f46c93af743ee8f98dcefcc8d5350')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (187, NULL, NULL, N'what is csrf?', N'what is csrf', N'Cross-Site Request Forgery (CSRF) is a type of attack that occurs when a malicious Web site, email, blog, instant message, or program causes a user''s Web browser to perform an unwanted action on a trusted site for which the user is currently authenticated. The impact of a successful cross-site request forgery attack is limited to the capabilities exposed by the vulnerable application. For example, this attack could result in a transfer of funds, changing a password, or purchasing an item in the user''s context. In effect, CSRF attacks are used by an attacker to make a target system perform a function (funds Transfer, form submission etc.) via the target''s browser without knowledge of the target user, at least until the unauthorized function has been committed. Impacts of successful CSRF exploits vary greatly based on the role of the victim. When targeting a normal user, a successful CSRF attack can compromise end-user data and their associated functions. If the targeted end user is an administrator account, a CSRF attack can compromise the entire Web application. The sites that are more likely to be attacked are community Websites (social networking, email) or sites that have high dollar value accounts associated with them (banks, stock brokerages, bill pay services). This attack can happen even if the user is logged into a Web site using strong encryption (HTTPS). Utilizing social engineering, an attacker will embed malicious HTML or JavaScript code into an email or Website to request a specific ''task url''. The task then executes with or without the user''s knowledge, either directly or by utilizing a Cross-site Scripting flaw (ex: Samy MySpace Worm).', NULL, N'e8d851ce37394b8996ddd6b44fea506f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (188, NULL, NULL, N'what is a session?', N'what is a session', N'Sessions are the state of an entity interacting with another. This is required for a server to remember how to react to subsequent requests throughout a transaction. Sessions are maintained on the server by a session identifier which can be passed back and forward between the client and server when transmitting and receiving requests. Sessions should be unique per user and computationally very difficult to predict.', NULL, N'16a8ea6fe332497bbdf51d6dcef027b7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (189, NULL, NULL, N'what is gadget chaining?', N'what is gadget chaining', N'It is indirect jumps executed by the program. Gadgets begin with useful instructions and end with an indirect jump. The attacker chains together gadgets by controlling the target of a gadget''s indirect jump to point to the beginning of the next gadget in the sequence. In a classic ROP attack, gadgets end with the ret instruction and the attacker chains gadgets by writing appropriate values over the stack.', NULL, N'a8f6a8c6475a4b42b60c3e974903e496')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (190, NULL, NULL, N'what is rop?', N'what is rop', N'Return-oriented programming (ROP) is a computer security exploit technique that allows an attacker to execute code in the presence of security defenses such as non-executable memory and code signing.', NULL, N'6bd640eba381435ebfc940100cf1fec6')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (191, NULL, NULL, N'what is phishing?', N'what is phishing', N'Phishing is the attempt to acquire sensitive information such as usernames, passwords, and credit card details (and sometimes, indirectly, money), often for malicious reasons, by masquerading as a trustworthy entity in an electronic communication.', NULL, N'936c3d52377b45a28aebb4845001aa07')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (192, NULL, NULL, N'what is an IPS?', N'what is an ips', N'An intrusion Prevention System (IPS) can be used to respond to an alert. This is different from an Intrusion Detection System (IDS) that just alerts you. An IPS alerts and responds based on the rules you set.', NULL, N'af6f1e309a4b4c9cbf059c362d1c3409')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (193, NULL, NULL, N'what is session management?', N'what is session management', N'Session Management is a process by which a server maintains the state of an entity interacting with it. This is required for a server to remember how to react to subsequent requests throughout a transaction. Sessions are maintained on the server by a session identifier which can be passed back and forward between the client and server when transmitting and receiving requests. Sessions should be unique per user and computationally very difficult to predict.', NULL, N'a24a63edaf08484f9c46f3043f865442')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (194, NULL, NULL, N'what is an edge exchange server?', N'what is an edge exchange server', N'Edge Transport servers handle all inbound and outbound Internet mail flow by providing mail relay and smart host services for your Exchange organization. Agents running on the Edge Transport server provide additional layers of message protection and security.', NULL, N'32d1992564354f6f804e3ffc388fb25a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (195, NULL, NULL, N'what is VirusIOCchecker?', N'what is virusiocchecker', N'This is a tool provided for the mission which acts like the real-world ''Virus Total'' site. When an initial infection vector has been found, submit the URL to virusiocchecker.com. Do not rely on this service and note that results may not be immediately available.', NULL, N'690eb06b0e904f3a92adafc76623f28f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (196, NULL, NULL, N'what is an event?', N'what is an event', N'An event is something that happened. All incidents are composed of a series of events but not all events are considered incidents.', NULL, N'db85486a1191495baa61f70b3a3f6f3c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (197, NULL, NULL, N'what is a relative path?', N'what is a relative path', N'A relative path starts in the current working directory so absolute paths are not always needed. For example, if you are on a Linux terminal in the folder /home/user1/Desktop/ and there is a subdirectory called example containing a file called file.txt, you could cat the file with the command: cat example/file.txt. On Windows and Linux, the current directory can be referred to using a . and the parent directory can be referred to using .. - e.g. in our same example situation, say you want to cat the file /home/user1/Documents/letter.txt, you could do so by passing ../Documents/letter.txt as the file argument to cat since you are sitting in the Desktop directory, a ''sibling'' of the Documents directory.', NULL, N'e1c327b3e63443bdabe8f395f34e245c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (198, NULL, NULL, N'what is an absolute path?', N'what is an absolute path', N'An absolute path or full path points to the same place in the file system regardless of the current working directory by including every part of the path beginning at the root of the file system.', NULL, N'aa1b2f6d4684401db98dc4f5a9df37de')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (199, NULL, NULL, N'what is an incident?', N'what is an incident', N'An incident refers to harm or significant threat of harm. Events are classified as incidents when it is determined that an adverse event in an information systems and/or network has occurred.', NULL, N'e5c67e62e2ac430d8cbd52c408a4ac51')
GO
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (200, NULL, NULL, N'what is a daemon?', N'what is a daemon', N'In Unix-like operating systems a daemon is a program that runs unobtrusively in the background, as opposed to being directly controlled by a user. Daemons wait to be activated by the occurrence of a specific event or condition. Many Linux tools have daemons associated with them.', NULL, N'88d27f3c7b9f4b29ae7da4bf0125cb10')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (201, NULL, NULL, N'what is unix?', N'what is unix', N'Unix is a family of multitasking, multiuser computer operating systems all derived from the original Unix developed by AT&T in the 70s. Operating Systems based on Unix, i.e. Unix-like systems (sometimes referred to as UN*X or *nix) include but are not limited to Mac OSX and the various distributions of Linux.', NULL, N'3466672826e74727b5ca00412eed8546')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (202, NULL, NULL, N'what is cd?', N'what is cd', N'The cd command line tool (short for change directory) can be used to change your current working directory.', NULL, N'f0fa7efc227e4abb853c475ef609887d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (203, NULL, NULL, N'what is a path separator?', N'what is a path separator', N'The path separator for an Operating System is the symbol used to separate the parts of a file path. For example, in Windows the separator is a  but in Linux it is a /.', NULL, N'a8a7eb649ace452b84cbdc9e8cbfbf1a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (204, NULL, NULL, N'what is a flag?', N'what is a flag', N'A command-line option or simply option (also known as a flag or switch) modifies the operation of a command.', NULL, N'7cd024c4e964463f8c5deb52907bdfe7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (205, NULL, NULL, N'what is ping?', N'what is ping', N'Ping is a network utility that can be used to verify connectivity to hosts on a network.', NULL, N'e24a49d33ca846079366c1a4551adfbc')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (206, NULL, NULL, N'what is a datagram?', N'what is a datagram', N'A datagram is a basic unit of transfer on a packet-switched network. Datagrams are typically structured with a header section and a payload section. Datagrams provide a connectionless communication service across a packet-switched network. The delivery, arrival time, and order of arrival of datagrams need not be guaranteed by the network.', NULL, N'8272bae7c08a4874a4caf7c6dcb5b63e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (207, NULL, NULL, N'what is a router?', N'what is a router', N'In computer networking, traffic is exchanged (routed) between subnetworks with special gateways (routers) when the routing prefixes of the source address and the destination address differ.', NULL, N'5f6e654e434d4eb69238d35eb8b8fd1b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (208, NULL, NULL, N'what is powershell?', N'what is powershell', N'PowerShell is a framework from Microsoft for task automation and configuration management. PowerShell consists of a command-line shell and an associated scripting language.', NULL, N'1b26096ecaab4dbaa4092f87e1b0ebfe')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (209, NULL, NULL, N'what is smtp?', N'what is smtp', N'The Simple Mail Transfer Protocol is an internet standard for email. While proprietary systems like Microsoft Exchange and webmail systems like Gmail use their own nonstandard protocols to access mail box accounts on their own mail servers, all use SMTP when sending or receiving email from outside their own systems. This protocol was last updated in 2008 to extended SMTP, which is in widespread use today.', NULL, N'3ee4695860a648a88d1b1a93fa6534c0')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (210, NULL, NULL, N'what is office parser?', N'what is office parser', N'Officeparser.py is a Python script that can be used to extract macros or files embedded in Microsoft Office files. The file can be found online at: https://raw.githubusercontent.com/unixfreak0037/officeparser/master/officeparser.py', NULL, N'8cedefdf318945d097c8549535f0df00')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (211, NULL, NULL, N'what is ftp?', N'what is ftp', N'The File Transfer Protocol (FTP) is a standard network protocol for transferring files between a client and server on a computer network.', NULL, N'ed4409b32bce4680bbad65e863a8fe30')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (212, NULL, NULL, N'what is vsftpd?', N'what is vsftpd', N'The Very Secure File Transfer Protocol Daemon (vsftpd) is an FTP server for Unix-like systems, including Linux.', NULL, N'aed8f3002c4345398b63354de1454692')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (213, NULL, NULL, N'what is udp?', N'what is udp', N'The User Datagram Protocol (UDP) is one of the main protocols of the Internet Protocol suite. UDP is suitable for purposes where error checking and correction are either not necessary or are performed in the application; UDP avoids the overhead of such processing at the level of the network interface. Time-sensitive applications often use UDP because dropping packets is preferable to waiting for delayed packets, which may not be an option in a real-time system. If reliability and error checking at the network interface level are important for your application, it is better to use TCP.', NULL, N'b3419643260a47d88475562b7e5264a0')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (214, NULL, NULL, N'what is a firewall?', N'what is a firewall', N'There are several types of firewalls, but in general a firewall is a type of network security system that monitors incoming and outgoing traffic based on a predetermined set of rules. Typically a firewall is used as a barrier between a secure internal network and an outside network that is assumed to be insecure, like the internet.', NULL, N'2c9a5995f3ef4ea0b5b0b3ef4e889a50')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (215, NULL, NULL, N'what is DNS?', N'what is dns', N'The Domain Name System (DNS) is a decentralized naming system for computers, services, or other resources connected to a computer network. This abbreviation is also sometimes used to mean Domain Name Server, which is a server designated by the Domain Name System to be an authoritative name server for a domain. This means that server answers questions about the names in its zone. The Domain Name System and its associated servers are often called the backbone of the internet, and are likewise often compared to a phone book for computers to communicate with each other.', NULL, N'ff640953a7e5486eb7959f8d6868e887')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (216, NULL, NULL, N'what is a shell?', N'what is a shell', N'A shell is a user interface used to access an operating system''s services. Most shells use CLI (Command-line Interface) as opposed to a GUI (Graphical User Interface). Often shells have nice features such as tab-completion and maintaining a command history.', NULL, N'6bd0aa1417bf4c22adf850255b447b23')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (217, NULL, NULL, N'what is bash?', N'what is bash', N'Bash, short for the Bourne Again Shell, was developed as a replacement for the Bourne Shell (sh). Bash incorporates useful features of the Korn Shell (ksh) and the C Shell (csh), for instance an unlimited size for the command history and the ability to edit files from the command line.', NULL, N'57536e7920dd4300a7ee82e156d66c2e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (218, NULL, NULL, N'what is vnc?', N'what is vnc', N'VNC, which stands for Virtual Network Computing, is a system used to send the display of one computer to a second computer, which in turn can send mouse and keyboard input to the first computer. The computer sending the display must be running a VNC server, and the computer sending input must be running a corresponding VNC client. There are multiple implementations of VNC. The Remote Desktop Protocol for Windows is a similar system that works based on different rules.', NULL, N'86ebf3e80262447b9459b96906ea4b35')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (219, NULL, NULL, N'what is connectionless communication?', N'what is connectionless communication', N'Connectionless communication, often referred to as CL-mode communication, is a form of data transmission used in packet switching networks. CL-mode communication packets contain all the information needed to deliver them so they can be delivered independently of each other in any order. This is in contrast to connection oriented communication. One example of CL-mode communication is UDP.', NULL, N'b773bfe4f09747d9949f125350a60bf3')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (220, NULL, NULL, N'what is connection oriented communication?', N'what is connection oriented communication', N'Connection oriented communication is a method of data transmission used in computer networking and other forms of telecommunications. The main characteristic of this method is that a semi-permanent connection must be established before any useful data can be transmitted. An example of connection oriented communication in computer networking is TCP.', NULL, N'ea833aee80374fe9aeb100dd4f366511')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (221, NULL, NULL, N'what is a packet?', N'what is a packet', N'A packet is a unit of data that is routed from a source to a destination on the internet, or any other packet-switched network. IP packets are composed of a header and a payload, but the exact details differ between IPv4 and IPv6 packets.', NULL, N'17af70c4c6704cd2b3e5f635f1c8944f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (222, NULL, NULL, N'what is wireshark?', N'what is wireshark', N'Wireshark is a network protocol analyzer. With Wireshark you can examine what is happening on your network at the packet level, which is very useful when trying to track down bad guys.', NULL, N'2332b5e9f95848048bfb1245b6344135')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (223, NULL, NULL, N'what is meterpreter?', N'what is meterpreter', N'Meterpreter is a shell component of metasploit that can be used to listen for incoming reverse shell connections initiated by the payload delivery. The payload used will depend on the vulnerability being exploited. Meterpreter is also much more than just a type of shell. Really Meterpreter is a dynamically extensible payload that is extended over the network at runtime and utilizes DLL injection stagers. Meterpreter communicates over the stager socket and provides a client side Ruby API.', NULL, N'7d738d1662594d93b675ab0e925ab83c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (224, NULL, NULL, N'what is i.e.?', N'what is i.e', N'The abbreviation i.e. stands for the Latin phrase ''id est'' which means ''that is''. This abbreviation is used when introducing a more full or complete explanation of something previously mentioned, e.g. ''I am Athena, i.e. a member of the Aerospace Team Help for Engineering Network Advice.''', NULL, N'1c199d90ffee4d14ac30795452b51211')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (225, NULL, NULL, N'what is binary notation?', N'what is binary notation', N'Binary notation is a way of representing numbers that only uses two digits, 0 and 1. This is called a base 2 number system because there are two symbols. We use binary notation in computer science because of the way we store data. Computer hard drives consist of billions and billions of microscopic components called transistors that can take one of two states, ''on'' or ''off'', and we can use electricity to toggle the states. We encode things like letters and pictures as binary numbers to store them on hard drives.', NULL, N'749fe68a8e6a4f6898db3cf67de4dcf8')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (226, NULL, NULL, N'what is debian?', N'what is debian', N'Debian is a Unix-like operating system. This OS is composed entirely of free software, a lot of which is under the GNU General Public License.', NULL, N'bbc62b7b4cd14483a8ce766c14fd28d4')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (227, NULL, NULL, N'what is ubuntu?', N'what is ubuntu', N'Ubuntu is an open source Linux distribution derived from Debian architecture.', NULL, N'beb273f2e2894ac29c5d0c2adb6c83c2')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (228, NULL, NULL, N'what is Kali?', N'what is kali', N'Kali is a Linux distribution that was derived from Debian and designed for penetration testing and digital forensics. When you play an offensive mission in Project Ares, we typically give you access to a Kali Linux box to start from.', NULL, N'65e30c66d6274b86b7e6cfc000853fa0')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (229, NULL, NULL, N'what is hexadecimal?', N'what is hexadecimal', N'Hexadecimal is a base 16 number system. That means it uses sixteen distinct symbols to represent numbers, most often the symbols 0 through 9 to represent values zero to nine, and A, B, C, D, E, F (or alternatively a, b, c, d, e, f) to represent values ten to fifteen. We frequently use hexadecimal in computer science because it has a very nice property. Hexadecimal is more compact than binary notation, but is very easy to convert to binary. Every digit in a hexadecimal number can be converted directly into four binary digits. For example, say we want to convert the hexadecimal number 2F7 to binary. We convert 7 to 0111, then we convert F (15 in decimal notation) to 1111, then we convert 2 to 0010. So the hexadecimal number 2F7 in binary notation is 1011110111. Notice we can omit leading 0''s on the most significant digit, in this case the 2. Going from hex to binary, the order of conversion is not very important, but it is good practice to start with the right most digit and work your way left, because that is what you must do when converting in the other direction, binary to hex.', NULL, N'8e9915a6585d4361b6c72f82590419d6')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (230, NULL, NULL, N'what is decimal notation?', N'what is decimal notation', N'Decimal notation is the standard way we represent numbers in society, using the digits 0 through 9. This is a base 10 number system because there are ten symbols. In computer science we often use binary notation instead because of the way we store data.', NULL, N'0e442ce9b79f4fc4a63551551b4f2fc1')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (231, NULL, NULL, N'what is e.g.?', N'what is e.g', N'The abbreviation e.g. means ''for example''. This abbreviation comes from the Latin phrase ''exempli gratia'', which of course means ''for example''.', NULL, N'0eda01247b9d4b6f862fc7307717f14d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (232, NULL, NULL, N'what is tab completion?', N'what is tab completion', N'Tab-completion refers to the ability of a Command-line Interface to ''complete'' what you are currently typing based on contextual information such as the working directory. For example, if you are in the /home/testuser/Downloads/ directory, and that directory contains only the files example1.txt and example2.txt, then you could type ''cat e'' then press tab to complete up to ''cat example''. At that point, if you press tab twice your options for tab-completion will be displayed (example1.txt and example2.txt). If you then type a 1, since there is no more ambiguity in your command pressing tab again will type the remainder of the command for you, so you would have ''cat example1.txt''. In this example tab completion doesn''t seem too useful, but take my word for it, this is a very helpful feature when you''re poking around deep in an unfamiliar file system.', NULL, N'0316292cbf7c46739668c9e02386dd96')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (233, NULL, NULL, N'what is /etc/shadow?', N'what is /etc/shadow', N'In many modern Unix-like systems, the /etc/shadow file is used to store sensitive account information apart from the less secure /etc/passwd file. The shadow file contains a line of colon (:) separated values for each user, with the most important data in the first two sections, the username and encrypted password. The encrypted password will typically follow this format where id is the hashing algorithm, salt is the salt used, and hashed is the hashed password: $id$salt$hashed', NULL, N'19ab241e2e1c435dae11a5ac54da8cf4')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (234, NULL, NULL, N'what is /etc/passwd?', N'what is /etc/passwd', N'In many Unix-like systems, the /etc/passwd file contains information about user accounts on the system. Each line in this file corresponds to a particular user and historically contained that user''s account name, encrypted password,  User ID (UID), User''s group ID (GID), Full name of the user (GECOS), user''s home directory, and Login shell. Typically in modern systems the password is replaced with a placeholder to indicate the encrypted password is stored in the /etc/shadow file.', NULL, N'41871c2277e64b9d8c330d0902177430')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (235, NULL, NULL, N'what is salt?', N'what is salt', N'In computer science, salt can refer to at least two different things. Typically salt refers to the randomly generated string used in the ''salting'' of a password - salting is when an Operating System or an application adds such a string to your password prior to encrypting, then only stores the encrypted result. This is much more secure than storing a plain text password, and more secure than storing an unsalted encrypted password. When you log in, the system appends the salt to the password you enter, hashes the result, then compares the hash to the one stored on the system - one major advantage of that approach is that two users will never have the same password, because if they do happen to use the same password the salt will still be different, thus the encrypted results stored on the system will be different. Secondly, Salt can refer to a software package, sometimes referred to as the SaltStack Platform, which is a configuration management and remote execution tool based in Python.', NULL, N'639980615d174989bdb72e13b9e50453')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (236, NULL, NULL, N'what is a gadget?', N'what is a gadget', N'Gadgets perform small computation, such as loading a value from memory into a register or adding two registers. In a ROP attack, the attacker finds gadgets within the original program text and causes them to be executed in sequence to perform a task other than what was intended.', NULL, N'd8be911f4a1040d589ed322507a990b1')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (237, NULL, NULL, N'what is an IDS?', N'what is an ids', N'An intrusion detection system (IDS) is a software application that monitors a network or systems for malicious activity or policy violations. Any detected activity or violation is typically reported either to an administrator or collected centrally using a security information and event management (SIEM) system.', NULL, N'f775b591a612409db92ae282a67b1c81')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (238, NULL, NULL, N'what is cat?', N'what is cat', N'The cat command can be used on Linux and other Unix-like operating systems to display the contents of a file. View the manpage for more information.', NULL, N'c8b38f6701604494b27bac2e2d8b9e6c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (239, NULL, NULL, N'what is the manpage?', N'what is the manpage', N'A man page, or manual page, is a reference supplied for most commands on a unix system.', NULL, N'6983cce639244ab8b0d070ce2bb9b74c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (240, NULL, NULL, N'what is a hash?', N'what is a hash', N'A hash value (or simply hash), also called a message digest, is a number generated from a string of text. The hash is substantially smaller than the text itself, and is generated by a formula in such a way that it is extremely unlikely that some other text will produce the same hash value.', NULL, N'19576352653e4550bc29d16dadbcb30a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (241, NULL, NULL, N'what is a GPO?', N'what is a gpo', N'A Group Policy Object (GPO) is a collection of settings that apply to a group of users or computers on a network managed by a member of the Microsoft Windows NT family of operating systems. You can manage all aspects of Group Policy by using the Group Policy Management Console (GPMC). You start the GPMC from the Start menu: Click Start, All Programs, Administrative Tools, Group Policy Management. You can also click Start, type Group Policy Management, and then click Group Policy Management in the Programs section of the Start menu.', NULL, N'f7f86e6304d24e438d3b93a5ad483651')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (242, NULL, NULL, N'what is a domain group policy?', N'what is a domain group policy', N'Group Policy is a feature of the Microsoft Windows NT family of operating systems that controls the working environment of user accounts and computer accounts. Group Policy provides the centralized management and configuration of operating systems, applications, and users'' settings in an Active Directory environment. Think of Group Policy as ''touch once, configure many.'' The requirements for using Group Policy and following the instructions that this white paper provides are straightforward.', NULL, N'88c6761e410c42689a4dbbe1d6705e33')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (243, NULL, NULL, N'what is Windows NT?', N'what is windows nt', N'Microsoft Windows NT is a family of operating systems designed with software and hardware portability in mind. The letters NT historically stood for ''New Technology'' but have been phased out of the official OS names and now only appear in the product version string. An example of a modern Windows NT system is Windows Server 2016, along with the other Windows Server OSs.', NULL, N'c8053f49159f4b6fbd92add85913c39c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (244, NULL, NULL, N'what is an indicator of compromise?', N'what is an indicator of compromise', N'Indicator of compromise (IOC) is an artifact observed on a network or in an operating system that with high confidence indicates a computer intrusion. Typical IOCs are virus signatures and IP addresses, MD5 hashes of malware files or URLs or domain names of botnet command and control servers.', NULL, N'6b163957a3174aaaaa5b35d47165df87')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (245, NULL, NULL, N'what are examples of snort rules?', N'what are examples of snort rules', N'You can look at the snort manual (http://manual.snort.org/node27.html). Additionally, the community ruleset can be used as an example located at /etc/snort/rules/community.rules on the IPS. Here is an example of a snort rule that will drop (block and log) outgoing tcp connections from any IP address/port combination on your network to the specified IP/port combination on the right of the direction operator (->). This could be useful in identifying machines infected with malware, which sometimes communicate with external IP addresses: drop tcp $HOME_NET any -> 115.69.21.190 80 (sid:10004; msg:''Malware connection attempt'')', NULL, N'a780309920b540ef8a046f488793bb36')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (246, NULL, NULL, N'how do I use patator?', N'how do i use patator', N'Patator does not have a manual entry and can have complex syntax. If you use the patator command in your terminal, assuming patator is installed, it will print a list of available modules. If you use the command followed by a module name (e.g. patator ssh_login) patator will display that module''s options and an example of using the module. Like hydra, you should provide patator with a password dictionary.', NULL, N'448c6809483443c2940198678d9f8e03')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (247, NULL, NULL, N'how do I open sguil?', N'how do i open sguil', N'You can search for sguil on your client1 box assuming the tool is installed. For the mission network the sguild host should be changed from localhost to 172.16.30.11. Use your credentials to login. At the next dialog box click select all, then click start sguil.', NULL, N'e964e086ed6b4776a5dd8fa11e62cfc0')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (248, NULL, NULL, N'how do I change file permissions?', N'how do i change file permissions', N'File permissions on linux can be changed with the command ''chmod''. In windows, the ''security'' section in a file''s ''Properties'' dialog can be used to change the file''s permissions.', NULL, N'b49f4808ead14aef97dbb851e0a4a468')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (249, NULL, NULL, N'how do I open python?', N'how do i open python', N'To use python, assuming python is installed, simply type ''python'' on the command line to enter an interactive python shell. To execute a python script,  execute ''python script_name.py'' on the command line. You can also execute python code directly with the -c flag, for example this command imports the pty module (used to handle pseudo-terminals) and starts an instance of the Bourne shell: python -c "import pty; pty.spawn(''/bin/sh'')"', NULL, N'd308c655014d48929e71de8a045fc7dd')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (250, NULL, NULL, N'how do I open metasploit?', N'how do i open metasploit', N'To launch the metasploit console, type ''msfconsole'' in the command line.', NULL, N'9544ebee331b44979f1065227ad668a1')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (251, NULL, NULL, N'how do I kill a service?', N'how do i kill a service', N'In general you can run the following command to stop a Linux service, although sudo is not always needed: sudo service <service name> stop. Alternatively you could use ps to determine which processes are running, then use the kill command to kill the appropriate processes.', NULL, N'23a69a45902b47d58da2577f314c1fbe')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (252, NULL, NULL, N'how do I open a terminal?', N'how do i open a terminal', N'Bash is used by default as the command-line interpreter for any new terminals opened in Kali and Ubuntu. You can open a new terminal on both systems by searching for terminal through the Operating System''s search functionality, available via a button in the top left corner of your desktop.', NULL, N'43866292850c42b5a097ec9f7b5c3922')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (253, NULL, NULL, N'how do I use snort?', N'how do i use snort', N'Snort is a powerful system for network intrusion detection and/or prevention. You can develop custom Snort rules to block or alert on malicious traffic as you find it on your network. You can find references to assist you writing snort rules in the references section of your mission orders and/or in Battle School. You can look at the snort manual (http://manual.snort.org/node27.html). Additionally, the community ruleset can be used as an example located at /etc/snort/rules/community.rules on most IPSs.', NULL, N'2af51bfb76234d13979b474883364497')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (254, NULL, NULL, N'how do I save the mission?', N'how do i save the mission', N'From the mission control screen, select the button labeled ''give up''. Within the popup dialog choose whether to quit the mission or save the mission so you can resume play later. I will miss you.', NULL, N'95c0b8b231cf4551a3bd1e7d7accb27b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (255, NULL, NULL, N'how do I open elsa?', N'how do i open elsa', N'To use ELSA, you can enter https://172.16.30.11/elsa in the Chromium browser and login with your credentials. There is a query box at the top of the ELSA interface where you can query the Bro logs.', NULL, N'dcd94387673841a9af551773c7c8ff3d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (256, NULL, NULL, N'how do I open netcat?', N'how do i open netcat', N'Assuming netcat is installed, you can use netcat to connect to a service running on example.com on port 1000 with this command: nc example.com 1000. You can also use netcat on your machine to listen for connections on port 1000 with the following command: nc -lvp 1000', NULL, N'b6afa7442c764856a1bb9b53b37455f2')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (257, NULL, NULL, N'how do I open radare2?', N'how do i open radare2', N'Assuming you have the radare2 binary on your system, execute the radare2 binary and pass in the name of a binary as an argument. For example to start radare2 and analyze the roc2 binary file, use this command: r2 roc2', NULL, N'1828d09bbfec4907ba065ac50704ffbb')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (258, NULL, NULL, N'how do I upload tools?', N'how do i upload tools', N'You can upload tools to your cyber range by opening the SSH terminal from the mission control screen in Ares and dragging files into the terminal.', NULL, N'bf297e40299047c883bbaabf450e1231')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (259, NULL, NULL, N'how do I edit a file from the command line?', N'how do i edit a file from the command line', N'Standard unix command line text editors include vi and nano. The tool names are the commands to launch the text editors. You can learn more about these by reading the man pages on either utility.', NULL, N'055ce7cd2afc4bd8839d89bf43cd5075')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (260, NULL, NULL, N'how do I use ping?', N'how do i use ping', N'To ping example.com and verify the domain is reachable from your network, you would use the command: ping example.com', NULL, N'30255377df3249469fe28022b08e7d40')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (261, NULL, NULL, N'how do I use scp?', N'how do i use scp', N'The general syntax of the scp command is as follows -  scp file_source file_destination. To copy the file "foo.txt" from a remote host to the local host - scp username@remote_name_or_IP:foo.txt /some/local/directory/. To copy the file "foo.txt" from the local host to a remote host, assuming the file is in your working directory - scp foo.txt username@remote_name_or_IP:/directory/. SCP will use the same authentication methods as ssh on any remote machines referenced in the command.', NULL, N'5321804950e5439492bbb1ca1b1a9893')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (262, NULL, NULL, N'what is an off-by-one error?', N'what is an off-by-one error', N'An off-by-one error (OBOE), sometimes called an OB1 error, OBOB (off-by-one bug), or "that extra inch you didn''t really want", is a type of logic error involving a boundary condition. OBOEs often occur in computer programming when an iterative loop iterates one time too few or too many.', NULL, N'f418e3f760eb49028947dafe5fb42ae2')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (263, NULL, NULL, N'how do I scan a network?', N'how do i scan a network', N'Nmap is a useful command line tool for exploring a computer network. Other tools that may prove useful for reconnaissance include but are not limited to: dig, traceroute, nslookup, netcat (nc), and of course, ping.', NULL, N'ef281b0f35a4409aa2366b9fe61f7a35')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (264, NULL, NULL, N'how do I remove malware?', N'how do i remove malware', N'The general strategy when removing malware is to first identify the offending process and use it to track down other artifacts. Next, remove the executable file from the physical disk, and kill the process. Finally, after applying preventative measures and eliminating all persistence mechanisms, ensure the offending process has not reappeared.', NULL, N'cfc2599fc036478992429a0455dea548')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (265, NULL, NULL, N'what is tls?', N'what is tls', N'Secure Sockets Layer (SSL) and its successor, Transport Layer Security (TLS), both frequently referred to as "SSL", are cryptographic protocols providing communications security over a network. These protocols utilize symmetric cryptography to encrypt the data transmitted.', NULL, N'26a5674e89934d09b6667ef738c5e8d5')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (266, NULL, NULL, N'what is ssl?', N'what is ssl', N'Secure Sockets Layer (SSL) and its successor, Transport Layer Security (TLS), both frequently referred to as "SSL", are cryptographic protocols providing communications security over a network. These protocols utilize symmetric cryptography to encrypt the data transmitted. Several versions of the protocols are widely used in applications such as email, web browsing, Internet faxing, instant messaging, and voice-over-IP (VoIP). Websites often use TLS to secure all communication between their servers and a client web browser.', NULL, N'1c10876207d74549bb0d627b8b4e3af7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (267, NULL, NULL, N'what is a port?', N'what is a port', N'In the internet protocol suite, a port is an endpoint of communication in an operating system. A port can be either ''open'' or ''closed'' meaning it is either allowing or blocking connections, respectively. A port is always associated with an IP address of a host and the protocol type of the communication, and thus completes the destination or origination network address of a communication session. A port is identified for each address and protocol by a 16-bit number, commonly known as the port number. For example, an address may be "protocol: TCP, IP address: 1.2.3.4, port number: 80", which may be written 1.2.3.4:80 when the protocol is known from context. Specific port numbers are often used to identify specific services.', NULL, N'9f56874a48f647aebfe50cb1bc310954')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (268, NULL, NULL, N'what is a vpn?', N'what is a vpn', N'A virtual private network (VPN) extends a private network across a public network, and enables users to send and receive data across shared or public networks as if their devices were directly connected to the private network. In the simplest terms, it creates a secure, encrypted connection, which can be thought of as a tunnel, between your computer and a server operated by the VPN service.', NULL, N'dfa2b43eb2524f389ceb0d6408f578e1')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (269, NULL, NULL, N'what is vulnerable software?', N'what is vulnerable software', N'Vulnerable software is software that is vulnerable to some form of exploit. An example is a webserver than can be exploited by sending malicious input that will overflow a memory buffer on the server, allowing the user to take control of the server.', NULL, N'3a98fe10e5a94574b0b9b0ec25d83bc6')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (270, NULL, NULL, N'how do I search for files on windows?', N'how do i search for files on windows', N'In windows, the search bar in File Explorer can be used to search for files.', NULL, N'63d57ef7586f42249d06953effe4a26c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (271, NULL, NULL, N'how do I search for files on linux?', N'how do i search for files on linux', N'Linux offers the command-line utilities ''find'', ''search'' and ''locate'' to allow a user to search for files. Please see the manual pages for ''find'', ''search'', or ''locate'' for more information about each utility.', NULL, N'e110739d256840e8afcf364cfd38c09e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (272, NULL, NULL, N'how do I search for files?', N'how do i search for files', N'In windows, the search bar in File Explorer can be used to search for files. Linux offers the command-line utilities ''find'', ''search'' and ''locate'' to allow a user to search for files. Please see the manual pages for ''find'', ''search'', or ''locate'' for more information about each utility.', NULL, N'04bdd49c0ee444ee833c38e13fe9fe52')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (273, NULL, NULL, N'how do I search through files quickly?', N'how do i search through files quickly', N'grep is a popular tool used to search one or multiple files for a specified input string. To search an entire directory (recursively), use the ''-r'' flag. For example, to search the directory ''/example/directory'' for files that contain the string ''foo'', you would use the command: grep -r "foo" /example/directory. To search a specific file (file.txt) for the pattern ''foo'', the command would be: grep -f file.txt "foo"', NULL, N'909f8ea9e7784687bf6ed254435c5854')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (274, NULL, NULL, N'what is a thread?', N'what is a thread', N'A thread of execution is the smallest sequence of programmed instructions that can be managed independently by a scheduler, which is typically a part of the operating system. The implementation of threads and processes differs between operating systems, but in most cases a thread is a component of a process. Multiple threads can exist within one process, executing concurrently and sharing resources such as memory, while different processes do not share these resources. In particular, the threads of a process share its executable code and the values of its variables at any given time.', NULL, N'3d099031f3d54905b616f03cbaaa82d9')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (275, NULL, NULL, N'what is openvas?', N'what is openvas', N'OpenVAS is an open-source framework of several services and tools offering a comprehensive and powerful vulnerability management/scanning solution.', NULL, N'0dc4303001074e689653f89616b1eea2')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (276, NULL, NULL, N'what are IDS rules?', N'what are ids rules', N'Many IDSs, such as Snort, come with a set of default rules that dictate the way the IDS reacts to certain traffic. Many of those systems also allow you to create custom rules that detect and react to particular patterns of network traffic.', NULL, N'6684768b24a44837bca8c3f90e6c77ec')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (277, NULL, NULL, N'what is fuzzing?', N'what is fuzzing', N'Fuzzing is the usually automated process of finding hackable software bugs by randomly feeding different permutations of data into a target program until one of those permutations reveals a vulnerability.', NULL, N'48cd5e6146f744868a5d6e4711d784fb')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (278, NULL, NULL, N'how do I redirect the output to a file?', N'how do i redirect the output to a file', N'The most common syntax for redirecting standard output uses the ''>'' operator. A single ''>'' operator will create a new file to store the output while obliterating any existing file with the same filename. A double ''>>'' operator will append the redirected standard output to the end of an existing file or create a new file if one does not already exist. For example, the following command redirects the output of the strings command into a new file called new.txt: strings example.exe > new.txt', NULL, N'8f291871f93c4a44aad75397fe31aea8')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (279, NULL, NULL, N'how do I redirect the output to another command?', N'how do i redirect the output to another command', N'The operator to pipe output from one command to another is the vertical bar ''|'' character. For example, to pipe the output of ls into the command wc, the command would be: ls | wc', NULL, N'1cd5d12d7a744793ba900fc2544f5a86')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (280, NULL, NULL, N'how do I open dirbuster?', N'how do i open dirbuster', N'Assuming you have dirbuster installed, you can start the tool from your command line terminal by entering the command: dirbuster. You can use dirbuster to rapidly make HTTP GET requests to a server and monitor which requests return valid data. This can help you find files on a server that may have been left available publicly by mistake. You can supply the tool with a dictionary of directories to request, just like you can give a password dictionary to Hydra or Patator.', NULL, N'3704e1486ae44d618469e81e3f8db1b4')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (281, NULL, NULL, N'how do I use dirbuster?', N'how do i use dirbuster', N'Assuming you have dirbuster installed, you can start the tool from your command line terminal by entering the command: dirbuster. You can use dirbuster to rapidly make HTTP GET requests to a server and monitor which requests return valid data. This can help you find files on a server that may have been left available publicly by mistake. You can supply the tool with a dictionary of directories to request, just like you can give a password dictionary to Hydra or Patator.', NULL, N'bfde825e176a475bb98ff240e4cb33f7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (282, NULL, NULL, N'how do I alert on the malware?', N'how do i alert on the malware', N'A good way to detect malware is to create IDS rules to monitor the network for any malicious activity you''ve identified. Reference library resouce "Bro open source IDS" has more information about creating IDS rules.', NULL, N'b55a6a8c90814dfaa8a57c36f94fe940')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (283, NULL, NULL, N'what is elf?', N'what is elf', N'The Executable and Linkable Format (ELF, formerly named Extensible Linking Format), is a common standard file format for executable files, object code, shared libraries, and core dumps.', NULL, N'cacb4e732b01469c8647574b4c618ddd')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (284, NULL, NULL, N'what is a terminal?', N'what is a terminal', N'Technically the term terminal applies to all programs that emulate a screen used to interact with a computer system, but this term is often used interchangeably with the terms shell and CLI (command-line interface) which are exclusively text based interfaces as opposed to GUIs.', NULL, N'c09911193c30414ea82e56171967d454')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (285, NULL, NULL, N'what is elf64?', N'what is elf64', N'The elf64 object format is the 64-bit version of the Executable and Linkable Object Format.', NULL, N'9f27a3a1df654f7294b8a9ff3b37bd07')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (286, NULL, NULL, N'what is an assembler?', N'what is an assembler', N'An assembler is a program for converting instructions written in low-level symbolic code into machine code.', NULL, N'49688aa7b41a40ce8a4616329b85b827')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (287, NULL, NULL, N'what is root?', N'what is root', N'Root (aka the superuser) is a special account on Linux machines. You need to become the root user in order to execute many system commands, which you can do by logging in as root, or prefacing your command with sudo and entering appropriate credentials if prompted to do so. If you''re familiar with managing a Windows server, you know that the Administrator can edit any files on the server and perform any tasks they need to. Linux has a similar administrative account, but it is named root instead of administrator.', NULL, N'c788e939c74a4f25b645725712da1b9f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (288, NULL, NULL, N'how do I use metasploit?', N'how do i use metasploit', N'This is the typical workflow for using metasploit. Open the tool in your terminal with the command: msfconsole. If you want to launch the tool as root, use sudo. Next search for an appropriate module using the search command, e.g. to search for modules relating to drupal enter: search drupal. When you''ve found an exploit to use, choose it with the command: use <path to exploit>. When you''ve chosen your exploit, you can view the options with the command: options. You can set the options with the command: set <option_name> <option_value>. Finally, you can run the exploit with the command: run', NULL, N'609c4edca3c7483f8cc2e5097137227e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (289, NULL, NULL, N'how do I download something in a meterpreter shell?', N'how do i download something in a meterpreter shell', N'When you have a meterpreter shell open to another machine, you can download files from that machine using the download command with a relative or absolute path as an argument. If using a relative path the point of reference is your current working directory on the machine meterpreter is connected to. For example, if there is a directory in your current directory called Users, and there is a file in that directory called example.txt, you could download the file with the following command: download Users/example.txt', NULL, N'f615886a7361487c92439f48999859c5')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (290, NULL, NULL, N'what is metasploit?', N'what is metasploit', N'The Metasploit Project is a computer security project that provides information on computer vulnerabilities and aids in penetration testing and IDS signature development. The Metasploit Framework (MSF) is a popular open source sub-project of Metasploit. MSF is a tool for developing and executing exploits against a remote target machine. Assuming MSF is installed, you can start it with the command: msfconsole', NULL, N'52838d30df6248c1b30f70dfd46e1393')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (291, NULL, NULL, N'what is psexec?', N'what is psexec', N'The PSExec module was written by Sysinternals and integrated into the Metasploit Framework. This module can be used to gain access to a system whose credentials you already have. A useful feature of this module is that you can also set a password hash as the password. This can save you the trouble of having to use another tool to crack the hash value.', NULL, N'9f152fbc0ef14c7aba6563b25945db77')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (292, NULL, NULL, N'what is a buffer?', N'what is a buffer', N'A data buffer is used to temporarily store data that is being moved from one place to another.', NULL, N'1e43d3e6c0d24dcfbdc3611fa526d0cb')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (293, NULL, NULL, N'what is libc?', N'what is libc', N'glibc is both the C standard library (e.g, "standard C functions") and a wrapper for system calls. You cannot issue system calls directly because the compiler doesn''t know how -- glibc contains the "glue" which is necessary to issue system calls, which is written in assembly. (It is possible to reimplement this yourself, but it''s far more trouble than it''s worth.)', NULL, N'fd51d20fd0d34344a4789132883e3b3a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (294, NULL, NULL, N'how do I start a netcat listener?', N'how do i start a netcat listener', N'Netcat is a command-line tool used for reading from or writing to connections using tcp or udp. For example, if you wanted to try to connect to example.com on port 1000, you could use the command: nc example.com 1000. If you wanted to start a listener on your box on port 1000, you could use the command: nc -lvp 1000', NULL, N'af1205059e824b7aaea5e1a575727935')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (295, NULL, NULL, N'what are privileges?', N'what are privileges', N'Privilege can be defined in computing as the delegation of authority over a computer system. Users require the necessary privileges to perform an action, e.g. create a file in a certain directory, delete or read a particular file, access a device, or read or write to a socket for communicating over the internet. The exact details of how privileges work vary between different operating systems.', NULL, N'c0b4813a84e64633a661b34fc218ccd2')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (296, NULL, NULL, N'what is a stack pointer?', N'what is a stack pointer', N'A stack pointer is a small register that stores the address of the last program request in a stack.', NULL, N'd2cf2d68d84a45929373fc4636a30abd')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (297, NULL, NULL, N'what is the metasploit psexec module?', N'what is the metasploit psexec module', N'The PSExec module was written by Sysinternals and integrated into the Metasploit Framework. This module can be used to gain access to a system whose credentials you already have. A useful feature of this module is that you can also set a password hash as the password. This can save you the trouble of having to use another tool to crack the hash value.', NULL, N'2370ec4fd2724c72a3b18072e283e103')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (298, NULL, NULL, N'what is nikto?', N'what is nikto', N'Nikto is an Open Source (GPL) web server scanner which performs comprehensive tests against web servers for multiple items, including over 6400 potentially dangerous files/CGIs, checks for outdated versions of over 1200 servers, and version specific problems on over 270 servers.', NULL, N'9bd345ce83f94a058ecb54af8a652fa3')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (299, NULL, NULL, N'how do I open grep?', N'how do i open grep', N'Grep is a command-line tool that can be used with standard input or from a file.', NULL, N'f4d3fc9b738a4498ae4a54c10f60cbd9')
GO
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (300, NULL, NULL, N'what is a reverse shell?', N'what is a reverse shell', N'A reverse shell, also known as a connect-back, is a type of communication path or backdoor in which the victim machine communicates back to the attacking machine. The attacking machine has a listener port on which it receives the connection, which can be used to execute commands on the victim machine. There are a number of popular shell tools such as Meterpreter, C99 PHP web shell, JSP web shell, Netcat, etc. There are over 150 reverse shells in the Metasploit Framework. As a rule of thumb, always pick a meterpreter, because it currently provides better support of post exploitation Metasploit has to offer.', NULL, N'c7a68046c2cd4925821bdcd4456d2349')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (301, NULL, NULL, N'what is strings?', N'what is strings', N'The Unix command line tool ''strings'' finds and prints text strings embedded in binary files such as executables. For example, you can find and print any strings in the binary example.exe with the following command: strings example.exe', NULL, N'80459b171fb440e2887313477c4c8c99')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (302, NULL, NULL, N'how do I use hydra?', N'how do i use hydra', N'Hydra requires similar information to brute force most protocols. In most cases you will need to supply hydra with the location of the password dictionary you''d like to use, the target server''s IP address, the port the target server is running the service on, and the name of the service you want to attack. The command should follow this pattern for ssh for instance, assuming you''re on a Kali box with metasploit: hydra -l admin -P /usr/share/metasploit-framework/data/wordlists/unix_passwords.txt ssh://<IP of target>:<port numer>', NULL, N'838712dd60ee4f5aa6978aa56ff10940')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (303, NULL, NULL, N'how do I start an exploit handler with metasploit?', N'how do i start an exploit handler with metasploit', N'The exploit handler is a metasploit module used to interact with payloads that were launched outside of the metasploit framework. Once you''ve opened the msfconsole, you can use the exploit handler just like any other module, i.e. by typing ''use'' followed by the path to the module: use exploit/multi/handler. When you see the exploit(handler) prompt you can set your options. The exploit handler is used to interact with payloads launched outside of the metasploit framework, so the options are not hardcoded like they are in some other modules. Typical options you might want to set for this module include PAYLOAD, LPORT, and LHOST. When you''ve set appropriate options, you can run the exploit handler by entering: run', NULL, N'cbe6d1e5562441188f2dff7f5292b829')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (304, NULL, NULL, N'how do I use autoroute?', N'how do i use autoroute', N'The Metasploit script autoroute can be used to add a route to another subnet from an already compromised machine. To add a route to the 172.16.10.0/24 subnet from a meterpreter shell on an appropriate compromised machine, you could use the following command: run autoroute -s 172.16.10.0/24', NULL, N'48b1ddb0750b48fab623ab7eed0d707b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (305, NULL, NULL, N'how do I capture traffic with wireshark?', N'how do i capture traffic with wireshark', N'To capture traffic with Wireshark you can open the tool from the command line, select an interface to capture traffic on, then click Start.', NULL, N'23c7259ec4b04341a2a5d965b9596e3c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (306, NULL, NULL, N'how do I view the man page?', N'how do i view the man page', N'In order to access a command''s manual page, type man <command_name> at the command line. You can also search man page entries by typing a / while viewing a manpage. For example, to search for the string ''example'' simply type /example with the man page open. You can use backspace to refine your search, and delete the / or press esc to end the search. After you press enter to finalize your search, you can jump to the next instance of the search entry on the page by pressing n and the previous search entry by pressing shift+n. Scroll up or down with the up and down arrow keys. Type q (while not searching) to quit the manpage.', NULL, N'72cd007fc14c402b95842a160cdbdb4a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (307, NULL, NULL, N'how do I search the man page?', N'how do i search the man page', N'You can search man page entries by typing a / while viewing a manpage. For example, to search for the string ''example'' simply type /example with the man page open. You can use backspace to refine your search, and delete the / or press esc to end the search. After you press enter to finalize your search, you can jump to the next instance of the search entry on the page by pressing n and the previous search entry by pressing shift+n.', NULL, N'd9e6b04af58d4dbfa1c60ab99ef53dc7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (308, NULL, NULL, N'how do I scroll the man page?', N'how do i scroll the man page', N'Scroll up or down the manual page with the up and down arrow keys.', NULL, N'5f398d1a2d6d4d67983c6a3a4cc5ba6d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (309, NULL, NULL, N'how do I quit the man page', N'how do i quit the man page', N'Type q (while not searching) to quit the manpage.', NULL, N'235ba74b8c724dd5bec88cd6cbb94511')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (310, NULL, NULL, N'how do I view the file?', N'how do i view the file', N'The best way to view the contents of a file on linux is with the cat command. On Windows, the type command can be used similarly to view the contents of a file. As a general rule, user home directories are a good place to look for important files.', NULL, N'db5cfb0852bd424ebd26c828fae3ec4d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (311, NULL, NULL, N'how do I login to sguil?', N'how do i login to sguil', N'You can search for sguil on your client1 box assuming the tool is installed. For the mission network the sguild host should be changed from localhost to 172.16.30.11. Use your credentials to login. At the next dialog box click select all, then click start sguil.', NULL, N'c91c1d68765b47a48d067c1f6dad5b04')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (312, NULL, NULL, N'how do I download a pcap from elsa?', N'how do i download a pcap from elsa', N'To download a pcap from a record in ELSA, you can click the info link beside the record, then click Plugin, then getPcap. Finally log in with your username and password. Once logged in, you can download the pcap by clicking on the blue link in the upper left ending in .pcap. Be aware that you can only download pcaps for TCP related traffic that has a timestamp, src ip, source port, destination ip, and destination port.', NULL, N'9c32f3b118894980b8ca7b5c247b0918')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (313, NULL, NULL, N'what is an IP address?', N'what is an ip address', N'A unique string of numbers that identifies each computer using the Internet Protocol to communicate over a network. IP addresses are separated into octets and can be expressed in binary notation and decimal notation. There are two main versions of IP addresses, IPv4 and IPv6. Each version can be further divided into two types of addresses, local and external. Your local IP address identifies your device to other devices on your network, while your external IP address identifies your device to other devices on the internet.', NULL, N'1373762b624245eeb6f5d2fdc7a75b74')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (314, NULL, NULL, N'how do I open autoruns?', N'how do i open autoruns', N'All the windows boxes you have access to should have the sysinternals tool suite installed. You can navigate to C:\sysinternals in the Windows file explorer and search that folder for autoruns. Use the entry with Application in the Type column and agree to the license agreement if necessary. Sometimes you will need to run this tool as an administrator.', NULL, N'0c24c96c92f147b4b509655283afed93')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (315, NULL, NULL, N'where are password hashes?', N'where are password hashes', N'On Linux and some other Unix-like systems, there is a file called passwd in the /etc directory. The passwd file contains information associated with each user account on the system. Historically, password hashes were kept in the passwd file. For security reasons, in more modern systems the password hashes are kept in a separate file called shadow. Both the shadow and passwd files contain one line for each user on the system, and each line contains data separated by colons (:). In modern systems, the passwd file has a placeholder for the second value on each line which indicates the hash is stored in the shadow file. On Windows, user accounts are typically managed by Security Accounts Manager (SAM) which stores user''s passwords in a database file that utilizes cryptographic measures to keep unauthenticated users from accessing the system. In order to improve the security of SAM, Microsoft introduced the program SYSKEY in 1996. When SYSKEY is enabled, the on-disk copy of the SAM database is partially encrypted such that the password hashes are encrypted a second time using a key, often referred to as the SYSKEY. SYSKEY can be enabled on Windows by running the SYSKEY program.', NULL, N'3f4b0599cd3948ebaef6f550055c3607')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (316, NULL, NULL, N'where are password hashes on Linux?', N'where are password hashes on linux', N'On Linux and some other Unix-like systems, there is a file called passwd in the /etc directory. The passwd file contains information associated with each user account on the system. Historically, password hashes were kept in the passwd file. For security reasons, in more modern systems the password hashes are kept in a separate file called shadow. Both the shadow and passwd files contain one line for each user on the system, and each line contains data separated by colons (:). In modern systems, the passwd file has a placeholder for the second value on each line which indicates the hash is stored in the shadow file.', NULL, N'9d94870337ae4c638bba86577162300d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (317, NULL, NULL, N'where are password hashes on Windows?', N'where are password hashes on windows', N'On Windows, user accounts are typically managed by Security Accounts Manager (SAM) which stores user''s passwords in a database file that utilizes cryptographic measures to keep unauthenticated users from accessing the system. In order to improve the security of SAM, Microsoft introduced the program SYSKEY in 1996. When SYSKEY is enabled, the on-disk copy of the SAM database is partially encrypted such that the password hashes are encrypted a second time using a key, often referred to as the SYSKEY. SYSKEY can be enabled on Windows by running the SYSKEY program.', NULL, N'9d07a21a4b5648e2b855b76f58a7a867')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (318, NULL, NULL, N'how do I kill a process on Linux?', N'how do i kill a process on linux', N'On Linux, processes can be killed from the command line with the following command where <PID> is the process ID: kill <PID>. The command may need to be run as an administrator, and can be passed the -9 flag to kill more forcefully.', NULL, N'47648a30e357443b969122cb9b19c8a8')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (319, NULL, NULL, N'how do I kill a process on Windows?', N'how do i kill a process on windows', N'On Windows, the most reliable ways to kill processes are by using the Taskkill command from the command prompt, or by right clicking, then killing the process in procexp. If you find a suspicious process in TCPView or another tool you can note the PID and use that to find the same process in Procexp.', NULL, N'c3edae5473dc47749ff6c9bc43242bb9')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (320, NULL, NULL, N'why can''t I delete the files?', N'why can''t i delete the files', N'A process may be running that is using the source code file. Make sure all malicious processes have been killed and none of them were restarted by persistence mechanisms, then try again to delete the file(s).', NULL, N'34b0d99c340f4343b37e9d6829774694')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (321, NULL, NULL, N'what is a buffer overflow?', N'what is a buffer overflow', N'A buffer overflow is a software bug where a program, while writing data to a buffer, overruns the buffer''s boundary and overwrites adjacent memory locations.', NULL, N'dc5ee3b781394d5291b80ff0b15eb590')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (322, NULL, NULL, N'what is nx?', N'what is nx', N'In computer security, executable-space protection marks memory regions as non-executable, such that an attempt to execute machine code in these regions will cause an exception. It makes use of hardware features such as the NX bit (no-execute bit), or in some cases software emulation of those features.', NULL, N'd690a6428a99413c87c4e7bdf8a03d49')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (323, NULL, NULL, N'how do I transfer a file?', N'how do i transfer a file', N'Scp stands for secure copy, and it can be used on the Linux and other Unix-like command lines to copy files from one directory on one machine to another directory on another machine on the same network. The general syntax of the scp command is as follows: scp user1@host1:/file/original_location user2@host2:/file/destination. SCP will use the same authentication methods as ssh on any remote machines referenced in the command.', NULL, N'457fcd2d85eb47e9bda3dc5656f38164')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (324, NULL, NULL, N'what is the stack?', N'what is the stack', N'In computer science, a call stack is a stack data structure that stores information about the active subroutines of a computer program. This kind of stack is also known as an execution stack, program stack, control stack, run-time stack, or machine stack, and is often shortened to just ''the stack''.', NULL, N'd93a47f8dc864ab6bd0ed56872f74f5b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (325, NULL, NULL, N'what is ack?', N'what is ack', N'In some digital communication protocols, ACK (short for acknowledgement) is the name of a signal that data has been received successfully. For example, an ACK signal is sent when data is received with an acceptable number of errors.', NULL, N'bb742325189f4490ade4ac13c421e88f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (326, NULL, NULL, N'why do I need so many terminals?', N'why do i need so many terminals', N'Multiple terminals are needed to monitor simultaneous functions.', NULL, N'547c9d363f794aa881eee86ee1687ca6')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (327, NULL, NULL, N'how do I find the source code on Linux?', N'how do i find the source code on linux', N'You can run sudo ls the /proc/<PID>/exe directory (where <PID> is the process ID) to print the command used to start a process, which will sometimes include the absolute path. If the absolute path is not included you can use the find command with name of the file, e.g. to find all files with names starting with example and starting the search in the root directory, I could run the command: sudo find / -name example*', NULL, N'8b4f9c1becf644f9a1417c41730808db')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (328, NULL, NULL, N'how do I find the source code on Windows?', N'how do i find the source code on windows', N'You can usually find source code used to start a process by right clicking, then viewing the properties of a process in Procexp or TCPView - to have access to the properties of every process, the tools should be run as administrator.', NULL, N'bf01e603743f41eea1470595050896ff')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (329, NULL, NULL, N'what is ifconfig?', N'what is ifconfig', N'The Linux command line tool ifconfig (short for interface configuration) is used to view and/or change the configuration of network interfaces on your system.', NULL, N'9e856a6dff32463cb3b7c7e04d0dbbc0')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (330, NULL, NULL, N'how do I use ifconfig?', N'how do i use ifconfig', N'Running ifconfig with no arguments will display information for all the network interfaces currently operating on your system. You can pass the name of a particular interface to view only information for that interface, or you can pass the -a flag to view information for all the interfaces instead of only the ones currently operating. The simplest way to interact with an interface via ifconfig is to enable or disable it with ''sudo ifconfig <interface name> up'' and ''sudo ifconfig <interface name> down'' respectively, but you can also assign to your interfaces static IP addresses, network masks, and broadcast addresses.', NULL, N'26e2996e2c8847c5b797005eba9e2c5b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (331, NULL, NULL, N'what is a network interface?', N'what is a network interface', N'A network interface is the point of interconnection between a computer and a private or public network. Typically this takes the form of a physical card called a NIC (Network Interface Card), but an interface can also be implemented as software.', NULL, N'1d57a2526ad74f51a50e6bf64b19eb75')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (332, NULL, NULL, N'what is a network?', N'what is a network', N'A computer network is a set of computers connected together for the purpose of sharing resources, e.g. the internet. However there are many types of networks that use similar technology, collectively referred to as packet-switched networks. Other examples of packet-switched networks include TV broadcasting and the NASA Deep Space Network.', NULL, N'4bce1f3bab554a0aa77d02ee13678bda')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (333, NULL, NULL, N'why is Rdesktop taking so long?', N'why is rdesktop taking so long', N'Rdesktop may take 2 or 3 minutes to connect - this is perfectly normal, just be patient.', NULL, N'42b479390ed8407597fe09a7e31e15b4')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (334, NULL, NULL, N'what is broadcasting?', N'what is broadcasting', N'In the context of computer networking broadcasting is sending data packets to all possible recipients on your network simultaneously. Broadcasting is one of five major techniques used to route data on a computer network. The other major techniques are unicast, multicast, anycast, and geocast.', NULL, N'90859f9f8b834105a86faf4d13b6d716')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (335, NULL, NULL, N'what is a broadcast address?', N'what is a broadcast address', N'In the context of computer networking broadcasting is sending data packets to all possible recipients on your network simultaneously. This is done by transmitting data to the correct broadcast address, after which the network hardware (e.g. routers or switches) does the work of actually sending the data to all the potential recipients, referred to as the broadcast domain. Broadcasting is one of five major techniques used to route data on a computer network. The other major techniques are unicast, multicast, anycast, and geocast.', NULL, N'2a313f534ac74f2cbc06ecfaf203ce45')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (336, NULL, NULL, N'what is unicast?', N'what is unicast', N'In the context of computer networking unicast refers to the process of sending a data packet to a single recipient. Unicast is one of five major techniques used to route data on a computer network. The other major techniques are broadcast, multicast, anycast, and geocast.', NULL, N'3a2773103cf14cea975c6316d0358897')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (337, NULL, NULL, N'what is multicast?', N'what is multicast', N'In the context of computer networking multicast refers to the process of sending a data packet to a particular set of machines on your network at one time. Multicast is one of five major techniques used to route data on a computer network. The other major techniques are unicast, broadcast, anycast, and geocast.', NULL, N'9ae66a092c544f57bf5a75123e8f9195')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (338, NULL, NULL, N'what is anycast?', N'what is anycast', N'In the context of computer networking anycast refers to the process of sending a data packet to exactly one member of a set of IP addresses on your network, i.e. there is a group of addresses that are appropriate for receiving the data, and the data may be sent to any one of these machines. Anycast is one of five major techniques used to route data on a computer network. The other major techniques are broadcast, multicast, unicast, and geocast.', NULL, N'66dda245f43241b593eaf99f2434b30d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (339, NULL, NULL, N'what is geocast?', N'what is geocast', N'In the context of computer networking a geocast is a specialized form of multicast used by some protocols. In geocast transmissions the group of addresses to deliver the data to is determined by the geographical location of the devices. Geocast is one of the five major techniques used to route data on a computer network and is typically used for mobile ad hoc networks. The other major techniques are broadcast, multicast, unicast, and anycast.', NULL, N'f535db038f884139aa2f24b528537c0a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (340, NULL, NULL, N'what is a mobile ad hoc network?', N'what is a mobile ad hoc network', N'A mobile ad hoc network, a.k.a. MANET, wireless ad hoc network, or ad hoc wireless network, is a network of wirelessly connected mobile devices. MANETs are continuously self-configuring and require no additional associated infrastructure.', NULL, N'929c909283a846bd81c88ff4465d6d2e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (341, NULL, NULL, N'what is CLI?', N'what is cli', N'A Command-line Interface (CLI) refers to a user interface that requires users to interact with the system by issuing commands in the form of successive lines of text, i.e. command lines or simply commands. A program that handles a CLI is called a shell.', NULL, N'e15f98f9d6a2407d8632dc00de7a311e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (342, NULL, NULL, N'what is a GUI?', N'what is a gui', N'A Graphical User Interface (GUI) refers to a user interface that allows users to interact with the system using graphical icons and other visual elements, rather than requiring purely text based input to interact with the system.', NULL, N'9934b26d32934edc87021d00fd9114bf')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (343, NULL, NULL, N'what is a domain', N'what is a domain', N'In a computer networking context the term domain can refer to two things. Firstly, a domain is a particular type of subnet on a Local Area Network consisting of clients and servers all under the control of a central security database, i.e. the domain controller. Secondly, a domain on the internet refers to a particular part of every network address. Website addresses, email addresses, and addresses for other protocols such as FTP all contain a domain. All devices sharing a common part of a URL are said to be in the same domain. In the address www.example.edu/stuff/file.html, example is the domain, stuff is a directory in that domain, and file.html is a file in the directory.', NULL, N'397b062c1d4d4c67bad64a9997bbf42a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (344, NULL, NULL, N'what is python?', N'what is python', N'Python is a relatively simple, powerful, and very popular programming language.', NULL, N'66a8a8ffa46b4cdf96c8e66b3252b5ea')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (345, NULL, NULL, N'how do I use mount?', N'how do i use mount', N'Running the mount command with no arguments will display the currently mounted file systems. The mount and umount commands maintain this list in the /etc/mtab file. A standard use of the mount command looks like this ''mount -t type device dir'' where ''type'' is the type of file system found on ''device'' and ''dir is the directory to which you wish to mount the device filesystem.', NULL, N'17d1823ca4cf4c838077583bf00995c7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (346, NULL, NULL, N'what is mount?', N'what is mount', N'In Unix-like systems the mount command is used to attach the filesystem of a device like a CD-ROM to the OS filesystem. The umount command is used to detach such a filesystem from the OS filesystem. View the man page for more information.', NULL, N'c70a91d5b5c04fb9975986c1a81e5016')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (347, NULL, NULL, N'what is a stream?', N'what is a stream', N'In computer science the term ''stream'' can refer to a number of similar things, but in general a stream is a (potentially infinite) sequence of data elements delivered over time, e.g. characters being typed from a keyboard as input to a shell, or image data from a video camera.', NULL, N'9bd78a91a35443cd8933fbd0398232e4')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (348, NULL, NULL, N'what is stdin?', N'what is stdin', N'Standard input, often abbreviated stdin, is one of the three standard streams in Unix-like operating systems. The others are standard output (stdout), and standard error (stderr). Windows operating systems have similar streams. Stdin provides the input for programs, and is generally expected from the keyboard which started the program, unless the stdout of one program has been redirected to the stdin of another. The file descriptor for stdin is 0.', NULL, N'0bd9b4be8f2d4223821507d124eb68a7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (349, NULL, NULL, N'what is stdout?', N'what is stdout', N'Standard output, often abbreviated stdout, is one of the three standard streams in Unix-like operating systems. The others are standard input (stdin), and standard error (stderr). Windows operating systems have similar streams. Stdout, naturally, serves as the stream where a program writes its output data. Unless redirected to another program, stdout is the terminal that initiated the program. The file descriptor for stdout is 1.', NULL, N'8b18abbe3f614f22a231681226b5074a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (350, NULL, NULL, N'what is stderr?', N'what is stderr', N'Standard error, often abbreviated stderr, is one of the three standard streams in Unix-like operating systems. The others are standard output (stdout), and standard input (stdin). Windows operating systems have similar streams. Stderr is an output stream (separated from stdout) that is used to output errors. As a separate stream, stderr can be redirected to a different location than stdout, but redirecting these streams to the same location is a common practice. The file descriptor for stderr is 2.', NULL, N'1811700d16574c97a528c9710097c43e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (351, NULL, NULL, N'what is a handle?', N'what is a handle', N'In the context of computer programming, a handle is an abstract reference to a resource. A piece of software can use a handle to refer to something (e.g. a block of memory) that is managed by another system, like a database or the operating system.', NULL, N'978999f8c74544b5a66ad66190516d06')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (352, NULL, NULL, N'what is curl?', N'what is curl', N'cURL is a software project that provides a command-line tool (called cURL) and a free library (called libcurl) for client-side URL transfers. The command-line tool utilizes the library, so it can be used to send and receive files using URL syntax over a wide range of internet protocols like http, https, ftp, ldap, and many more. Originally cURL stood for see URL.', NULL, N'0eb53a016abb468185c4ac5326bc6ed8')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (353, NULL, NULL, N'What is OAuth?', N'what is oauth', N'Open Authorization (OAuth) is a protocol that allows an application to authenticate against a server as a user, without requiring passwords or any third party server that acts as an identity provider. It uses a token generated by the server, and provides how the authorization flows most occur, so that a client, such as a mobile application, can tell the server what user is using the service. The recommendation is to use and implement OAuth 1.0a or OAuth 2.0, since the very first version (OAuth1.0) has been found to be vulnerable to session fixation. OAuth 2.0 relies on HTTPS for security and is currently used and implemented by API''s from companies such as Facebook, Google, Twitter and Microsoft. OAuth1.0a is more difficult to use because it requires the use of cryptographic libraries for digital signatures, however does not rely on HTTPS for security and can therefore be more suited for higher risk transactions.', NULL, N'2de6523c47c3460b83eccf15ce41909c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (354, NULL, NULL, N'What is OpenId?', N'what is openid', N'OpenId is an HTTP-based protocol that uses identity providers to validate that a user is who he says he is. It is a very simple protocol which allows a service provider initiated way for single sign-on (SSO). This allows the user to re-use a single identity given to a trusted OpenId identity provider and be the same user in multiple websites, without the need to provide any website the password, except for the OpenId identity provider. Due to its simplicity and that it provides protection of passwords, OpenId has been well adopted. Some of the well known identity providers for OpenId are Stack Exchange, Google, Facebook and Yahoo! For non-enterprise environment, OpenId is considered a secure and often better choice, as long as the identity provider is of trust.', NULL, N'c530be9feac34b95846657487e1a693d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (355, NULL, NULL, N'What is SAML?', N'what is saml', N'Security Assertion Markup Language (SAML) is often considered to compete with OpenId. The most recommended version is 2.0, since it is very feature complete and provides a strong security. Like with OpenId, SAML uses identity providers, but unlike it, it is XML-based and provides more flexibility. SAML is based on browser redirects which send XML data. Unlike SAML, it isn''t only initiated by a service provider, but it can also be initiated from the identity provider. This allows the user to navigate through different portals while still being authenticated without having to do anything, making the process transparent. While OpenId has taken most of the consumer market, SAML is often the choice for enterprise applications. The reason for this is often that there are few OpenId identity providers which are considered of enterprise class (meaning that the way they validate the user identity doesn''t have high standards required for enterprise identity). It is more common to see SAML being used inside of intranet websites, sometimes even using a server from the intranet as the identity provider.', NULL, N'74861ff70fd643fb8179b5ca0b98ef88')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (356, NULL, NULL, N'What are Password Managers?', N'what are password managers', N'Password managers are programs, browser plugins or web services that automate management of large number of different credentials, including memorizing and filling-in, generating random passwords on different sites etc. The web application can help password managers by using standard HTML forms for username and password input, not disabling copy and paste on HTML form fields, allowing very long passwords, not using multi-stage login schemes (username on first screen, then password), not using highly scripted (JavaScript) authentication schemes.', NULL, N'3aaf9e9c0512419187a2faeeb4c0b4c2')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (357, NULL, NULL, N'what is a stack?', N'what is a stack', N'A stack is an abstract datatype often used in computer science. Stacks have several operations defined on them, with the most important being PUSH, which deposits something on top of a stack, and POP, which removes something off the top of a stack. Stacks are also (less commonly) called LIFO queues. LIFO stands for Last In First Out and refers to the order in which things can be removed from the data structure, i.e. the last thing you push "onto" the stack will be the first thing removed the next time you pop something "off" the stack.', NULL, N'71da73aa8408475eb82791e06e3bd4fb')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (358, NULL, NULL, N'what is ip?', N'what is ip', N'The command line tool ip is used to show or manipulate routing, devices, policy routing and tunnels, on certain Linux distributions.', NULL, N'3871f4226f174f80b3e56c3f5b61b17f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (359, NULL, NULL, N'what is beaconing?', N'what is beaconing', N'A beacon, also called a heartbeat, is traffic leaving from inside a network at regular intervals. Beacons can be used for various different reasons such as obtaining new orders from a command and control server, downloading additional malware, or periodically exfiling data.', NULL, N'01ec5275b1f643f7be7348e0b11c3149')
