INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (362, NULL, NULL, N'What is Windows Management Instrumentation?', N'what is windows management instrumentation', N'WMI (Windows Management Instrumentation) is Microsoft''s implementation of Web-Based Enterprise Management (WBEM), an industry standard for accessing management information in an enterprise environment. WMI uses the Common Information Model industry standard to represent systems, applications, networks, devices, and other managed components.', NULL, N'0cfe88ebd2cb4a8cbd2d8fb8f2d85547')
