INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (600, 16, NULL, N'how do I do objective 2?', N'how do i do objective 2', N'Step 1: analyze network traffic to find a machine on your network that has been infected with malware. Step 2: login to that machine and find any malicious process(es) present, then view the process properties to find the source code, and then kill the process', N'm', N'fb8a8238bdc94b46afa2e1e1f3053a1b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (601, 16, 2, N'how do I do step 1?', N'how do i do step 1', N'You should look for unusually large or unusually long lasting connections to the network to find the infected machine. You can query ELSA or you can ssh to the security onion box directly and query the bro logs.', N'm', N'e5fdf902feb84ab3bb871f1cfece054f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (602, 16, 2, N'how do I do step 2?', N'how do i do step 2', N'You can use rdesktop to login to the infected machine, then find and kill any malicious processes with tcpview or procexp. Before killing the processes, be sure to note the location of the source code so it can be deleted later.', N'm', N'dbe5f17904274630be1e5a815c8809e2')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (603, 16, NULL, N'how do I do objective 3?', N'how do i do objective 3', N'Track down and remove all artifacts of the infection. Step 1: find and remove any malicious source code. Step 2: remove any persistence mechanisms. Step 3: kill any malicious processes that may have been restarted before you eliminated the persistence mechanisms.', N'm', N'd06b219df68b4c26a0349f9a9c36717f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (604, 16, 3, N'how do I do step 1?', N'how do i do step 1', N'You should have noted the location of any source code before killing the malicious process in the previous objective. To remove the code, find it in the Windows file explorer, right click it, then click delete, and select yes in the resulting dialog box. You also need to remove the outfiles directory, which you can do by right clicking it in the file explorer and then clicking delete, then clicking yes in the resulting dialog box.', N'm', N'41903d38b5ec47089999f2a862baf03d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (605, 16, 3, N'how do I do step 2?', N'how do i do step 2', N'Common places to look for persistence mechanisms on Windows are the registry entries or the Task Scheduler. You should check for anything suspicious, but there may not be any persistence mechanisms.', N'm', N'064cacf1b2a44fa6836619a35a3cb3e0')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (606, 16, 3, N'how do I do step 3?', N'how do i do step 3', N'Once again you can find any malicious processes by examining the processes in procexp or tcpview.', N'm', N'4d84345b4942467fb3ff8b026c308c1f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (607, 16, NULL, N'how do I do objective 4?', N'how do i do objective 4', N'To earn this objective you need to ensure that all persistence mechanisms have been removed and no malicious processes were restarted by those mechanisms prior to their removal.', N'm', N'130637b65efd4bdca3bcb8dcdf2ee343')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (608, 16, NULL, N'how do i find the exfil directory?', N'how do i find the exfil directory', N'First you need to use Tcpview or Procexp to find a malicious process on the system (there may be a few malicious processes) then track down the exfil directory by using Procexp to view the properties of the malicious process and any subprocesses. You should find a reference to the exfil directory in the command used to start one of the subprocesses.', N'm', N'b4666eab9c5949b4b0e78a3d15aba886')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (609, 17, NULL, N'how do I do objective 1?', N'how do i do objective 1', N'Capture traffic with Wireshark to find the beacon packets, then open the msfconsole as root and use the exploit handler with the windows reverse_tcp payload option to connect to the payload that is beaconing from inside the network.', N'e', N'a13fd5af87f7439f9f5e27b063b5eabd')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (610, 17, NULL, N'how do I capture traffic with wireshark?', N'how do i capture traffic with wireshark', N'To capture traffic with Wireshark you can open the tool from the command line, select an interface to capture traffic on, then click Start. You will need to run Wireshark as root to capture on the eth1 interface, so use the following command and click Ok in the resulting dialog boxes: xhost +; sudo wireshark', N'e', N'bc2e93ab1cbb4e7fa93130600f1fe41c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (611, 17, NULL, N'what should I use for the PAYLOAD option?', N'what should i use for the payload option', N'For this mission, you should use the windows/meterpreter/reverse_tcp payload option with the exploit handler module, so you can start a meterpreter shell to the infected windows machine.', N'e', N'373f8e112306471d86e10203330e12fb')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (612, 17, NULL, N'what should I use for the LPORT option?', N'what should i use for the lport option', N'For this mission, when you use the exploit handler module to connect to the beaconing payload, you should set the LPORT option to one of the ports on which you have observed beaconing. If you do not find the desired documentation you after you connect with meterpreter, then you should background the meterpreter session and run the exploit handler again with a different LPORT option.', N'e', N'6b006ddfc536469d929feb6b119090ca')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (613, 17, NULL, N'what should I use for the LHOST option?', N'what should i use for the lhost option', N'For this mission, you should use your Kali box''s IP address for the LHOST (local host) option.', N'e', N'ff34afcc8cc3427486702f6812680717')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (614, 17, NULL, N'how do I do objective 2?', N'how do i do objective 2', N'Once you''ve connected to the beaconing payload you should cd to the /Users directory and look for the user called controller, then find and download their documentation regarding the PLC. If you do not see that user, background your meterpreter session and connect to a different workstation by altering the LPORT option to a different port found in Wireshark and running the handler again. You may have to connect to several workstations to find the correct user.', N'e', N'53fa6e34707c4f069a958f1593d8f2df')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (615, 17, NULL, N'how do I do objective 3?', N'how do i do objective 3', N'Use meterpreter''s autoroute tool to forward traffic to the Modbus systems on the 172.16.10.0/24 network. Then you may view the pcap file in your home directory on the Kali box with Wireshark to find the appropriate port to use for your next scan. Finally use autoroute again to add a route to the 172.16.40.0/24 network, then background your meterpreter session and run a tcp scan of that network with the auxiliary/scanner/portscan/tcp module.', N'e', N'b49f99a33c094bb0a34004e4c2ac7076')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (616, 17, NULL, N'what should I use for the RHOSTS option?', N'what should i use for the rhosts option', N'Once you''ve established a route to the 172.16.40.0/24 subnet in objective 3, you should use that subnet as the RHOSTS option of the tcp scan.', N'e', N'aebf8369326c492896ced2db4fd89b60')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (617, 17, NULL, N'what should I use for the PORTS option?', N'what should i use for the ports option', N'Once you''ve established a route to the 172.16.40.0/24 subnet in objective 3, you should use the port you found in hmi.traffic.pcap file for the PORTS option of the tcp scan and as the RPORTS option for any subsequent interactions with the PLC through metasploit''s modbus modules.', N'e', N'c79f3daed359426a870a3360d9a63ef9')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (618, 17, NULL, N'how do I do objective 4?', N'how do i do objective 4', N'Use meterpreter''s modbusclient module to read data from the correct PLC register. You should find which register you need to read from by examining the documentation found in previous objectives.', N'e', N'35155f7d2c8c4aecb49d364302abf828')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (619, 17, NULL, N'what should I use for the RHOST option?', N'what should i use for the rhost option', N'When attempting to read or write PLC register values with the modbusclient module, you should set the RHOST option to the IP address of the PLC you identified with the tcp scan in objective 3.', N'e', N'db1dfe271dd7423694fd5fb24e84a30c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (620, 17, NULL, N'how do I do objective 5?', N'how do i do objective 5', N'Use meterpreter''s modbusclient module again, this time to write a value to the correct PLC register which will interupt the Taliban''s water supply. Remember to set the action to WRITE_REGISTER.', N'e', N'031cce795eb347b58b89ed7ea05dabbf')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (621, 17, NULL, N'what should I use for the data_address option?', N'what should i use for the data_address option', N'The documentation acquired in objective 2 highlights one register value as important, and notes that the value should not be set below 300. When reading to or writing from the PLC registers in later objectives, you should use the hexadecimal address of that register as the DATA_ADDRESS option.', N'e', N'1755486f2cf2475985386452a81550b5')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (622, 17, NULL, N'what should I use for the data option?', N'what should i use for the data option', N'The documentation acquired in objective 2 highlights one register value as important, and notes that the value should not be set below 300. Thus, when it comes time to write out a value to this register to disrupt the operations of the plant, just set any value between 0 and 299 for the data option.', N'e', N'4add0b5115a14de88b7c52e590f046ef')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (623, 18, NULL, N'what tools should I use?', N'what tools should i use', N'For this mission you will need to use ssh to move around to other machines on the network, and you will need to use Snort to programatically monitor the network traffic, prevent further infections, and identify a second infected machine. You will need to use a combination of Linux command line tools to eradicate infection from both of the infected machines, including netstat to monitor network activity on the infected machines, rm to remove malicious files, strings to examine binary files, kill to kill malicious processes, and ls with the -la flag to view the long listing of all files in the working directory.', N'e', N'66a5b45653b743efb4c0c3921151e6b0')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (624, 18, NULL, N'what are my credentials?', N'what are my credentials', N'Supplied usernames and passwords will follow the format - username: user<#>, password: P@ssedU<#> (where <#> is the user number appearing in your command line prompt on the first terminal you''re given)', N'e', N'488ec67d82944a6c9e0146dcba660de7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (625, 18, NULL, N'how do I gather credentials?', N'how do i gather credentials', N'You should not need to find a password for this mission.', N'e', N'8402d12defe9416290e5f4cc510e1a85')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (626, 18, NULL, N'how do I find the C2 server?', N'how do i find the c2 server', N'This is primarily a defensive mission, and finding/attacking a C2 server is not one of your objectives.', N'e', N'7eb36e92dfb04dcba7b4b569fa07f32b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (627, 18, NULL, N'how do I find the router IP address?', N'how do i find the router ip address', N'Use the command ''ip route'' to view the routing table and find the default router''s IP address.', N'e', N'069c2a349b5d4c0db735c740c928469b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (628, 18, NULL, N'how do I do objective 1?', N'how do i do objective 1', N'In the mission orders you were given the IP address (192.168.0.13) of a machine on your network that is known to be infected with malware. Step 1: login to that machine and find malware contained somewhere in the home directory of that machine''s primary user, testuser. Step 2: examine the file contents for anything that could be used to create snort rules to block the initial infection vector. Step 3: create snort rules to block the initial infection vector.', N'e', N'0918254198324a20a0c86046369a2894')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (629, 18, 1, N'how do I do step 1?', N'how do i do step 1', N'You should ssh to the infected machine using your credentials. Explore the directory /home/testuser and its subdirectories with the command: ls -al. The ls command lists files and directories in the current directory. You can use the pwd command (print working directory) to check which directory you are currently in.', N'e', N'849acb1148384871b4d27a7b95faed74')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (630, 18, 1, N'how do I do step 2?', N'how do i do step 2', N'When you find the malware source code you can inspect the file''s contents by opening it in a text editor like vi or nano, or you can just print the contents to the terminal with the cat command. Look for references to other files or directories, and any malicious IP addresses to block with snort rules.', N'e', N'631239363c8242a886d1aa5b3acb7193')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (631, 18, 1, N'how do I do step 3?', N'how do i do step 3', N'You can ssh to the security onion box with your credentials (IP address 192.168.0.10). From there you can edit the file /etc/snort/rules/local.rules to add any snort rules needed to block the infection. There are references listed in your mission orders to help you write snort rules.', N'e', N'434b574d9b1a4008a69ec045e5d90751')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (632, 18, NULL, N'how do I do objective 2?', N'how do i do objective 2', N'In the previous objective you should have found a malicious file, examined the contents, and used IP addresses found inside the file to create 2 snort rules. You need to create 2 more snort rules to complete this objective. Step 1: log back into the infected machine and continue to examine the contents of the malicious file until you find a reference to a second file. Step 2: Analyze the second file for something you can use to make more snort rules to prevent further malicious downloads. Step 3: create 2 more snort rules with what you found in the second file, so you can prevent the download of anymore malicious executables.', N'e', N'82552e282f8f42449aab3341c75c068e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (633, 18, 2, N'how do I do step 1?', N'how do i do step 1', N'Once again, ssh to the infected machine using your credentials and examine the malicious file in a text editor or by catting it to the terminal. Find a reference to a second file so you can continue investigating.', N'e', N'e18ab391418c4eaeb5320c2be7bb983d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (634, 18, 2, N'how do I do step 2?', N'how do i do step 2', N'This next file is a binary, so you may find the command line tool ''strings'' useful - the tool can extract printable characters from a file and can be useful in analyzing binaries. Run strings on the file and redirect the output to a new file so you can easily search through said output. One simple way to search the file is to cat it and pipe the output to grep, e.g. to search for the string ''example'' in a file call output.txt you can use this command: cat output.txt | grep example. You should search the malicious binary file for references to domain names.', N'e', N'21d558c532fb410e8a882a3d34d2703c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (635, 18, 2, N'how do I do step 3?', N'how do i do step 3', N'You should have found two suspicious domain names in the binary file (suspicious because they closely resemble legitimate domains). Use the reverse DNS lookup tool (dig) to find the IP addresses associated with these domains, then log back into the IPS server and add new snort rules to drop connections from these IP addresses.', N'e', N'adca0fa74c7f4889a77d5848566c3085')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (636, 18, NULL, N'how do I do objective 3?', N'how do i do objective 3', N'Once you''ve created the necessary snort rules in objectives 1 and 2, you should restart snortd so your changes take effect. After some time, the malware will try something sneaky. Your snort rules should spot this activity and generate an alert, completing objective 3. Snort should raise an alert for a second infected machine on your network, other than the known infected machine (192.168.0.13). You can view the end of the alert file (/var/log/snort/alert) with tail.', N'e', N'a81b0d492e6c4c188fde9182cc16c53b')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (637, 18, NULL, N'how do I restart snortd?', N'how do i restart snortd', N'You can restart the snort process to pick up any rule changes you made by running the following command on your IPS/IDS box: sudo systemctl restart snortd. You can monitor the end of the alert file for any snort alerts with tail.', N'e', N'3bb6dbdd7df24a9aa6ac3d99e3d1e55e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (638, 18, NULL, N'how do I do objective 4?', N'how do i do objective 4', N'You should login to the known infected machine and explore running processes and the filesystem to find and remove suspicious artifacts and kill any malicious processes. Step 1: Use netstat to find a malicious process. Step 2: Find the executable location for that process by listing the correct file in the /proc directory. Step 3: kill the process and remove all malicious source code you''ve found on the machine.', N'e', N'6fba8a3434b34070a72431b241ff1e18')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (639, 18, 4, N'how do I do step 1?', N'how do i do step 1', N'You should run netstat with sudo, and pass flags to show the PID of the process that made the connection (-p), disable name resolution (-n), monitor tcp traffic (-t), and monitor the traffic continuously (-c). You may also want to pipe the output to grep and search for 192.168, the first two octets of your network IP range.', N'e', N'9c311d445ffd458b8246f131af621738')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (640, 18, 4, N'how do I do step 2?', N'how do i do step 2', N'On Linux machines, /proc is a special directory containing information about running processes. To list the executable location of the file used to start a process, use the following command where <PID> is the Process ID of that process: sudo ls -l /proc/<PID>/exe', N'e', N'5bf8952ecd1d43979085c93107b110c7')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (641, 18, 4, N'how do I do step 3?', N'how do i do step 3', N'To remove files on Linux machines, use the rm command. To remove the malicious files you will need to run this command with sudo and pass the -f flag, which stands for force. Remove the -bash and firefox.exe files. Finally, kill the process you identified with netstat.', N'e', N'4ca0bc919b7543b78f884cfe46ad5ad1')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (642, 18, NULL, N'how do I do objective 5?', N'how do i do objective 5', N'You should have identified a second infected machine in objective 3. Login to that machine and again explore running processes and the file system to find and remove suspicious artifacts and kill any malicious processes. The steps for eradicating the malware from the second machine are the same as they were for eradicating the infection from the known infected machine.', N'e', N'9fb6571d156641778fe977d2f22889aa')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (643, 18, NULL, N'how do I do objective 6?', N'how do i do objective 6', N'On each of the infected machines you should go back and verify that no malicious processes were restarted before you removed the persistence mechanisms.', N'e', N'63c4638bec8b464cacaae8e8a94ca1f5')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (644, 18, NULL, N'what tools should I use?', N'what tools should i use', N'For this mission you will need to use tools including but not limited to ELSA and Snort to find a second existing infection and prevent future infections. You will need to use Rdesktop to login to the infected machines, then use the sysinternals tools Procexp and TCPView to find and eradicate the infections.', N'm', N'727fd881fadb4173afa17bcfd04c9f13')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (645, 18, NULL, N'what are my credentials?', N'what are my credentials', N'Supplied usernames and passwords will follow the format - username: user<#>, password: P@ssedU<#> (where <#> is the user number appearing in your command line prompt on the first terminal you''re given)', N'm', N'b655bfd7dbea4c0687bad1aeb9814760')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (646, 18, NULL, N'how do I find the C2 server?', N'how do i find the c2 server', N'This is primarily a defensive mission, and finding/attacking a C2 server is not one of your objectives.', N'm', N'6c6032458b6149f6a8e1450e50286585')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (647, 18, NULL, N'how do I find the router IP address?', N'how do i find the router ip address', N'To find the IP addresses of the other machines in the "sec ops" portion of the network diagram as well as the Router, run the following command with the password P@ssedU<#> where <#> is your user number. The Security Onion machine will have SSH and HTTPS services running, while the client1 machine will have ssh, https, and several more services running. The remaining IP addresses belong to the Router. When you have found the remaining 3 Router IP addresses, you can kill the command with Ctrl+c: sudo nmap 172.16.10,20,30.0-255 -sS -ttl 1', N'm', N'c9a47e5713964d88abd198bb97832c36')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (648, 18, NULL, N'how do I do objective 1?', N'how do i do objective 1', N'You were given the name of a machine on the network known to be infected with malware. Step 1: using the IP address of this machine, query elsa for any files that machine may have downloaded - you should find two such files. Step 2: extract a .pcap file for the download of the first file. Step 3: find a hexadecimal string from that .pcap file that you can use in a snort rule. Step 4: login to the IDS, create a snort rule using the hexadecimal you generated, update your snort rules, and restart the snort sensor.', N'm', N'ed7459dd92c24860b8a9438371130a8e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (649, 18, NULL, N'how do I gather credentials?', N'how do i gather credentials', N'You should not need to find a password for this mission.', N'm', N'1799e962517c4eb3af5f8c32b7a0368d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (650, 18, 1, N'how do I do step 1?', N'how do i do step 1', N'The infected machine is given in the mission orders as wkst1. Find the IP with dig, the reverse dns lookup tool, then login to elsa. Query elsa for any files that may have been downloaded by that machine by specifying the class as BRO_FILES, specifying the rx_hosts as the known infected IP, and grouping by mime_type.', N'm', N'46d527c9ed9141d690ab0694726b575d')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (651, 18, 1, N'how do I do step 2?', N'how do i do step 2', N'To extract a pcap from a record in elsa, click the info link to the left of the record, then click Plugin in the popup, and then choose getPcap, and enter your username and password. After a few minutes this should complete loading a tcptrace of the connection.', N'm', N'5c915ff652014ca5adc856ff5a8fc1fc')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (652, 18, 1, N'how do I do step 3?', N'how do i do step 3', N'Once the tcptrace has loaded you can download the pcap by clicking the blue link in the upper left hand corner that ends with .pcap. Open the file in wireshark and then export the HTTP object from the file and save it to your home directory. Use the command line tool strings to extract printable strings from the binary file, then find a string unique enough to be used in a snort rule and encode the string as hexadecimal.', N'm', N'20ed9e626628479690a8cb38acb81c06')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (653, 18, NULL, N'how do I update my snort rules?', N'how do i update my snort rules', N'You can update your snort rules with this command: sudo rule-update. You can restart your nsm_sensor with this command: sudo nsm_sensor_ps-restart', N'm', N'2e523775732e4205a5fbc6fd3ce08ded')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (654, 18, NULL, N'how do I do objective 2?', N'how do i do objective 2', N'In the previous objective, you should have found two files in elsa, and taken steps to extract a hexadecimal string from one of them to create a snort rule. Do the same thing for the second file, then update your snort rules and restart the snort sensor.', N'm', N'c574549a81d8409ca1d946e25e52cc72')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (655, 18, NULL, N'how do I do objective 3?', N'how do i do objective 3', N'Create a snort rule to alert on connections to the malware C2 server. Step 1: login to the known infected machine and find a connection to the C2 server, then search in elsa for HTTP connections to the server. Step 2: create a snort rule to alert on these connections, update your snort rules and restart the snort sensor.', N'm', N'bee5be716fb44684ab5e3bb0b3f3cdec')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (656, 18, 3, N'how do I do step 1?', N'how do i do step 1', N'Use the rdesktop program installed on the client1 machine to remote desktop to the known infected machine. Using Tcpview, find a process connecting to an external IP address over HTTP, then search for connections in elsa with class=BRO_HTTP and with that IP as the dstip.', N'm', N'27c86a8c378f4ddab2085f12f5923f74')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (657, 18, 3, N'how do I do step 2', N'how do i do step 2', N'Using your credentials, ssh to the IDS at the IP 172.16.30.11 and create your snort rule in the file /etc/nsm/rules/local.rules', N'm', N'bb7c0a858c6b46899ff10c3a1fa8e382')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (658, 18, NULL, N'how do I do objective 4?', N'how do i do objective 4', N'Login to the known infected machine, then explore running processes and the filesystem to find and remove suspicious artifacts and kill any malicious processes. You can use tcpview, procexp, and the Task Manager (among other things) to explore processes.', N'm', N'0c2ad477b37448448ec25cf109e27c18')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (659, 18, NULL, N'how do I do objective 5?', N'how do i do objective 5', N'You need to use SGUIL to find alerts generated by your snort rules. One of these alerts should lead you to a second infected machine on your network. Login to that machine and again explore running processes and the file system to find and remove suspicious artifacts and kill any malicious processes.', N'm', N'f3283d2c31a6431896d9d292e6995602')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (660, 18, NULL, N'how do I do objective 6?', N'how do i do objective 6', N'On each of the infected machines you should go back and verify that no malicious processes were restarted before you removed the persistence mechanisms.', N'm', N'ef4a4c29fc0a41b3857285110372dff3')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (661, 19, NULL, N'what tools should I use?', N'what tools should i use', N'For this mission you will need to use nmap to find the server, netcat to connect to the server, and metasploit to handle the payload you develop. You may also need to use Radare2 or the GDB to aid in developing your exploit, along with the programming language Python to serve as the payload containing your exploit.', N'e', N'ea6f181e08da41578c4d250d5489e1dd')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (662, 19, NULL, N'how do I find the Kali IP address?', N'how do i find the kali ip address', N'On your Kali box, use the command ''ip a''. View the manpage for more information.', N'e', N'a97e6448f1e14c388cb1e5b663b0c138')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (663, 19, NULL, N'how do I find the DNS IP address?', N'how do i find the dns ip address', N'I don''t know. Generally speaking, Nmap is a good tool for finding IP addresses, but you may have to use a different tool.', N'e', N'bcaa67c3a7b34c95a9681ac59f8af262')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (664, 19, NULL, N'what are my credentials?', N'what are my credentials', N'Supplied usernames and passwords will follow the format - username: user<#>, password: P@ssedU<#> (where <#> is the user number appearing in your command line prompt on the first terminal you''re given)', N'e', N'e673eb1ea2c54f00b04f6ccc0f88bd68')
