INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (687, 21, NULL, N'What network monitoring tools can I use for this mission?', N'what network monitoring tools can i use for this mission', N'For this mission you can use Sguil, Snort, Bro, Tcpdump and Wireshark for network monitoring.', N'e', N'42a63224eccb4339a232a99ff721f2ad')
