INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (689, 21, 1, N'What interface do I select to monitor traffic?', N'what interface do i select to monitor traffic', N'Select interfaces connected to the different segments of the network.', N'e', N'9e12a37fe2b84320aa4312c32c4982fa')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (690, 21, 1, N'How do I verify network traffic collection?', N'how do i verify network traffic collection', N'Once you logon to Sguil, select interfaces and start it ensure the real time events tab is populates with events.', N'e', N'5cf1208f22df4b92a997c6a3410b1e61')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (691, 21, 3, N'How do I look at Windows policies?', N'how do i look at windows policies', N'You can use cat to view a copy of it or logon to the server and use Group Policy manager.', N'e', N'97229d22b93a42129dd1eed07e6bd2a8')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (692, 21, 4, N'How do I download and inspect traffic from a suspected compromised system?', N'how do i download and inspect traffic from a suspected compromised system', N'Once you escalate an event in Sguil, go to the escalated events tab and right click over the Alert Id. Select Wireshark a copy of the session selected will be copied to your local /tmp. A pop up will inform you with the file name and location.', N'e', N'564f03521c5c46f2af8f742fe7d86750')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (693, 21, NULL, N'How do I find SGUIL from the command line?', N'how do i find sguil from the command line', N'Use the find or locate to get the full path of sguil.tk.', N'e', N'5151e4a536ab412099bc1f537b8263af')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (694, 21, NULL, N'How can I identify a process associated with a listening port?', N'how can i identify a process associated with a listening port', N'You can run netstat -ano or netstat -anob.', N'e', N'0ab9d435ca514c8bb8ca75fae89255cb')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (695, 21, NULL, N'How do I inspect services via autorun?', N'how do i inspect services via autorun', N'Once ran as an administrator, check the services tab.', N'e', N'407514c9d8d544918518050e08adefda')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (696, 21, NULL, N'How do I find services installed using the windows registry?', N'how do i find services installed using the windows registry', N'You can use option /s to do a recursive search and /f to specify a Pattern to search for. Via reg.exe query (i.e., reg query HKLM /s /f myservice).', N'e', N'8faa05ec5fcb48bea2e85735dec9902f')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (697, 21, NULL, N'How do I delete the malware registry entries?', N'how do i delete the malware registry entries', N'You can navigate to the key of interest via regedt32.exe and right click to delete or by using reg delete HKLM\badkey.', N'e', N'49ebd4a2fbd24532a747dacaa949cbba')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (698, 21, NULL, N'How do I disable a service that is running?', N'how do i disable a service that is running', N'Launch services.msc navigate to the service. Right click. Select properties and under Startup type select disable. If needed via CLI, Stop service and set configuration to disabled (e.g., sc stop badservice then sc config badservice start= disabled).', N'e', N'225f30d9325249ff8efffd0105d37a0c')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (699, 21, NULL, N'Where do I add a column to process explorer?', N'where do i add a column to process explorer', N'Click view then Select Columns from the options. Once options available are displayed select the one of interest.', N'e', N'6a214767fa384f3dbe0bf72565935d04')
