INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (718, 21, NULL, N'How do I do objective 2?', N'how do i do objective 2', N'To complete this objective you need to review the email policies located in the /opt/edge-exchange/edge_config.xml file.', N'e', N'44de11513cff4757865cf81c337969e5')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (719, 21, NULL, N'How do I do objective 3?', N'how do i do objective 3', N'Examine SIRDACON''s domain group and user policies to determine if they should be modified to conform to best practices. You should use rdesktop to login to the domain controller (172.16.10.10) and review the policies, which can be found in the Group Policy Management tool. In the rdesktop command your username should be SIRDACON\\user<#> where <#> is your user number on the client1 box and your password is the same as your SGUIL password.', N'e', N'd031ee3b16a0446ba76b96ffb57d6d1e')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (720, 21, NULL, N'How do I do objective 4?', N'how do i do objective 4', N'You need to monitor traffic on the network and find the domain name where the malware is downloaded from, and enter that name into the TYR site, available through an icon on your desktop.', N'e', N'd47aab270ba94759858ebdbf5c83b801')
INSERT [dbo].[api_questionanswerpair] ([id], [mission_catalog_id_id], [objective_id], [utterance], [normalized_utterance], [answer], [difficulty], [uid]) VALUES (721, 21, NULL, N'How do I do objective 5?', N'how do i do objective 5', N'You should use rdesktop to login to the infected machine and then use Procexp to find and kill a malicious process running there. Before killing the process, you should view the process properties to track down any artifacts of the infection for the next objective.', N'e', N'0a14105c33f04ccfac1d16a1aa8dcab1')
